"5028": {"surat": 55, "ayat": 75, "teks": "لَمۡ يَطۡمِثۡهُنَّ اِنۡسٌ قَبۡلَهُمۡ وَلَا جَآنٌّ‌ۚ‏"},
"5029": {"surat": 55, "ayat": 76, "teks": "فَبِاَىِّ اٰلَاۤءِ رَبِّكُمَا  تُكَذِّبٰنِ‌ۚ‏"},
"5030": {"surat": 55, "ayat": 77, "teks": "مُتَّكِــِٕيۡنَ عَلٰى رَفۡرَفٍ خُضۡرٍ وَّعَبۡقَرِىٍّ حِسَانٍ‌ۚ‏"},
"5031": {"surat": 55, "ayat": 78, "teks": "فَبِاَىِّ  اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ‏"},
"5032": {"surat": 55, "ayat": 79, "teks": "تَبٰـرَكَ اسۡمُ رَبِّكَ ذِى الۡجَـلٰلِ وَالۡاِكۡرَامِ‏"},
"5033": {"surat": 56, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5034": {"surat": 56, "ayat": 2, "teks": "اِذَا وَقَعَتِ الۡوَاقِعَةُ ۙ‏"},
"5035": {"surat": 56, "ayat": 3, "teks": "لَيۡسَ لِـوَقۡعَتِهَا كَاذِبَةٌ‌ ۘ‏"},
"5036": {"surat": 56, "ayat": 4, "teks": "خَافِضَةٌ رَّافِعَةٌ ۙ‏"},
"5037": {"surat": 56, "ayat": 5, "teks": "اِذَا رُجَّتِ الۡاَرۡضُ رَجًّا ۙ‏"},
"5038": {"surat": 56, "ayat": 6, "teks": "وَّبُسَّتِ الۡجِبَالُ بَسًّا ۙ‏"},
"5039": {"surat": 56, "ayat": 7, "teks": "فَكَانَتۡ هَبَآءً مُّنۡۢبَـثًّا ۙ‏"},
"5040": {"surat": 56, "ayat": 8, "teks": "وَّكُنۡـتُمۡ اَزۡوَاجًا ثَلٰـثَـةً ؕ‏"},
"5041": {"surat": 56, "ayat": 9, "teks": "فَاَصۡحٰبُ الۡمَيۡمَنَةِ ۙ مَاۤ اَصۡحٰبُ الۡمَيۡمَنَةِ  ؕ‏"},
"5042": {"surat": 56, "ayat": 10, "teks": "وَاَصۡحٰبُ الۡمَشۡـَٔـمَةِ ۙ مَاۤ اَصۡحٰبُ الۡمَشۡـَٔـمَةِؕ‏"},
"5043": {"surat": 56, "ayat": 11, "teks": "وَالسّٰبِقُوۡنَ السّٰبِقُوۡنَۚ  ۙ‏"},
"5044": {"surat": 56, "ayat": 12, "teks": "اُولٰٓٮِٕكَ الۡمُقَرَّبُوۡنَ‌ۚ‏"},
"5045": {"surat": 56, "ayat": 13, "teks": "فِىۡ جَنّٰتِ  النَّعِيۡمِ‏"},
"5046": {"surat": 56, "ayat": 14, "teks": "ثُلَّةٌ مِّنَ الۡاَوَّلِيۡنَۙ‏"},
"5047": {"surat": 56, "ayat": 15, "teks": "وَقَلِيۡلٌ مِّنَ الۡاٰخِرِيۡنَؕ‏"},
"5048": {"surat": 56, "ayat": 16, "teks": "عَلٰى سُرُرٍ مَّوۡضُوۡنَةٍۙ‏"},
"5049": {"surat": 56, "ayat": 17, "teks": "مُّتَّكِـــِٕيۡنَ عَلَيۡهَا مُتَقٰبِلِيۡنَ‏"},
"5050": {"surat": 56, "ayat": 18, "teks": "يَطُوۡفُ عَلَيۡهِمۡ وِلۡدَانٌ مُّخَلَّدُوۡنَۙ‏"},
"5051": {"surat": 56, "ayat": 19, "teks": "بِاَكۡوَابٍ وَّاَبَارِيۡقَ ۙ وَكَاۡسٍ مِّنۡ مَّعِيۡنٍۙ‏"},
"5052": {"surat": 56, "ayat": 20, "teks": "لَّا يُصَدَّعُوۡنَ عَنۡهَا وَلَا يُنۡزِفُوۡنَۙ‏"},
"5053": {"surat": 56, "ayat": 21, "teks": "وَفَاكِهَةٍ مِّمَّا  يَتَخَيَّرُوۡنَۙ‏"},
"5054": {"surat": 56, "ayat": 22, "teks": "وَلَحۡمِ طَيۡرٍ مِّمَّا يَشۡتَهُوۡنَؕ‏"},
"5055": {"surat": 56, "ayat": 23, "teks": "وَحُوۡرٌ عِيۡنٌۙ‏"},
"5056": {"surat": 56, "ayat": 24, "teks": "كَاَمۡثَالِ  اللُّـؤۡلُـوٴِالۡمَكۡنُوۡنِ‌ۚ‏"},
"5057": {"surat": 56, "ayat": 25, "teks": "جَزَآءًۢ بِمَا كَانُوۡا يَعۡمَلُوۡنَ‏"},
"5058": {"surat": 56, "ayat": 26, "teks": "لَا يَسۡمَعُوۡنَ فِيۡهَا لَغۡوًا وَّلَا تَاۡثِيۡمًا ۙ‏"},
"5059": {"surat": 56, "ayat": 27, "teks": "اِلَّا قِيۡلًا سَلٰمًا سَلٰمًا‏"},
"5060": {"surat": 56, "ayat": 28, "teks": "وَاَصۡحٰبُ الۡيَمِيۡنِ ۙ مَاۤ اَصۡحٰبُ الۡيَمِيۡنِؕ‏"},
"5061": {"surat": 56, "ayat": 29, "teks": "فِىۡ سِدۡرٍ مَّخۡضُوۡدٍۙ‏"},
"5062": {"surat": 56, "ayat": 30, "teks": "وَّطَلۡحٍ مَّنۡضُوۡدٍۙ‏"},
"5063": {"surat": 56, "ayat": 31, "teks": "وَّظِلٍّ مَّمۡدُوۡدٍۙ‏"},
"5064": {"surat": 56, "ayat": 32, "teks": "وَّ مَآءٍ مَّسۡكُوۡبٍۙ‏"},
"5065": {"surat": 56, "ayat": 33, "teks": "وَّفَاكِهَةٍ كَثِيۡرَةٍۙ‏"},
"5066": {"surat": 56, "ayat": 34, "teks": "لَّا مَقۡطُوۡعَةٍ وَّلَا مَمۡنُوۡعَةٍۙ‏"},
"5067": {"surat": 56, "ayat": 35, "teks": "وَّ فُرُشٍ مَّرۡفُوۡعَةٍؕ‏"},
"5068": {"surat": 56, "ayat": 36, "teks": "اِنَّاۤ اَنۡشَاۡنٰهُنَّ اِنۡشَآءًۙ‏"},
"5069": {"surat": 56, "ayat": 37, "teks": "فَجَعَلۡنٰهُنَّ اَبۡكَارًاۙ‏"},
"5070": {"surat": 56, "ayat": 38, "teks": "عُرُبًا اَتۡرَابًاۙ‏"},
"5071": {"surat": 56, "ayat": 39, "teks": "لِّاَصۡحٰبِ الۡيَمِيۡنِؕ‏"},
"5072": {"surat": 56, "ayat": 40, "teks": "ثُلَّةٌ مِّنَ الۡاَوَّلِيۡنَۙ‏"},
"5073": {"surat": 56, "ayat": 41, "teks": "وَثُلَّةٌ مِّنَ الۡاٰخِرِيۡنَؕ‏"},
"5074": {"surat": 56, "ayat": 42, "teks": "وَاَصۡحٰبُ الشِّمَالِ ۙ مَاۤ اَصۡحٰبُ الشِّمَالِؕ‏"},
"5075": {"surat": 56, "ayat": 43, "teks": "فِىۡ سَمُوۡمٍ  وَّحَمِيۡمٍۙ‏"},
"5076": {"surat": 56, "ayat": 44, "teks": "وَّظِلٍّ مِّنۡ يَّحۡمُوۡمٍۙ‏"},
"5077": {"surat": 56, "ayat": 45, "teks": "لَّا بَارِدٍ وَّلَا كَرِيۡمٍ‏"},
"5078": {"surat": 56, "ayat": 46, "teks": "اِنَّهُمۡ كَانُوۡا قَبۡلَ ذٰ لِكَ مُتۡرَفِيۡنَۚ  ۖ‏"},
"5079": {"surat": 56, "ayat": 47, "teks": "وَكَانُوۡا يُصِرُّوۡنَ عَلَى الۡحِنۡثِ الۡعَظِيۡمِ‌ۚ‏"},
"5080": {"surat": 56, "ayat": 48, "teks": "وَكَانُوۡا يَقُوۡلُوۡنَ ۙ اَٮِٕذَا مِتۡنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبۡعُوۡثُوۡنَۙ‏"},
"5081": {"surat": 56, "ayat": 49, "teks": "اَوَاٰبَآؤُنَا  الۡاَوَّلُوۡنَ‏"},
"5082": {"surat": 56, "ayat": 50, "teks": "قُلۡ اِنَّ الۡاَوَّلِيۡنَ وَالۡاٰخِرِيۡنَۙ‏"},
"5083": {"surat": 56, "ayat": 51, "teks": "لَمَجۡمُوۡعُوۡنَ ۙ  اِلٰى مِيۡقَاتِ يَوۡمٍ مَّعۡلُوۡمٍ‏"},
"5084": {"surat": 56, "ayat": 52, "teks": "ثُمَّ اِنَّكُمۡ اَيُّهَا الضَّآلُّوۡنَ الۡمُكَذِّبُوۡنَۙ‏"},
"5085": {"surat": 56, "ayat": 53, "teks": "لَاٰكِلُوۡنَ مِنۡ شَجَرٍ مِّنۡ زَقُّوۡمٍۙ‏"},
"5086": {"surat": 56, "ayat": 54, "teks": "فَمٰلِــُٔوۡنَ مِنۡهَا الۡبُطُوۡنَ‌ۚ‏"},
"5087": {"surat": 56, "ayat": 55, "teks": "فَشٰرِبُوۡنَ عَلَيۡهِ مِنَ الۡحَمِيۡمِ‌ۚ‏"},
"5088": {"surat": 56, "ayat": 56, "teks": "فَشٰرِبُوۡنَ شُرۡبَ الۡهِيۡمِؕ‏"},
"5089": {"surat": 56, "ayat": 57, "teks": "هٰذَا نُزُلُهُمۡ يَوۡمَ الدِّيۡنِؕ‏"},
"5090": {"surat": 56, "ayat": 58, "teks": "نَحۡنُ خَلَقۡنٰكُمۡ فَلَوۡلَا تُصَدِّقُوۡنَ‏"},
"5091": {"surat": 56, "ayat": 59, "teks": "اَفَرَءَيۡتُمۡ مَّا تُمۡنُوۡنَؕ‏"},
"5092": {"surat": 56, "ayat": 60, "teks": "ءَاَنۡتُمۡ تَخۡلُقُوۡنَهٗۤ اَمۡ نَحۡنُ الۡخٰلِقُوۡنَ‏"},
"5093": {"surat": 56, "ayat": 61, "teks": "نَحۡنُ قَدَّرۡنَا بَيۡنَكُمُ الۡمَوۡتَ وَمَا نَحۡنُ بِمَسۡبُوۡقِيۡنَۙ‏"},
"5094": {"surat": 56, "ayat": 62, "teks": "عَلٰٓى اَنۡ نُّبَدِّلَ اَمۡثَالَـكُمۡ وَنُـنۡشِئَكُمۡ فِىۡ مَا لَا تَعۡلَمُوۡنَ‏"},
"5095": {"surat": 56, "ayat": 63, "teks": "وَلَـقَدۡ عَلِمۡتُمُ  النَّشۡاَةَ الۡاُوۡلٰى فَلَوۡلَا تَذَكَّرُوۡنَ‏"},
"5096": {"surat": 56, "ayat": 64, "teks": "اَفَرَءَيۡتُمۡ مَّا تَحۡرُثُوۡنَؕ‏"},
"5097": {"surat": 56, "ayat": 65, "teks": "ءَاَنۡتُمۡ تَزۡرَعُوۡنَهٗۤ اَمۡ نَحۡنُ الزّٰرِعُوۡنَ‏"},
"5098": {"surat": 56, "ayat": 66, "teks": "لَوۡ نَشَآءُ لَجَـعَلۡنٰهُ حُطَامًا  فَظَلۡتُمۡ تَفَكَّهُوۡنَ‏"},
"5099": {"surat": 56, "ayat": 67, "teks": "اِنَّا لَمُغۡرَمُوۡنَۙ‏"},
"5100": {"surat": 56, "ayat": 68, "teks": "بَلۡ نَحۡنُ مَحۡرُوۡمُوۡنَ‏"},
"5101": {"surat": 56, "ayat": 69, "teks": "اَفَرَءَيۡتُمُ الۡمَآءَ الَّذِىۡ تَشۡرَبُوۡنَؕ‏"},
"5102": {"surat": 56, "ayat": 70, "teks": "ءَاَنۡـتُمۡ اَنۡزَلۡـتُمُوۡهُ مِنَ الۡمُزۡنِ اَمۡ نَحۡنُ الۡمُنۡزِلُوۡنَ‏"},
"5103": {"surat": 56, "ayat": 71, "teks": "لَوۡ نَشَآءُ جَعَلۡنٰهُ اُجَاجًا فَلَوۡلَا  تَشۡكُرُوۡنَ‏"},
"5104": {"surat": 56, "ayat": 72, "teks": "اَفَرَءَيۡتُمُ النَّارَ الَّتِىۡ تُوۡرُوۡنَؕ‏"},
"5105": {"surat": 56, "ayat": 73, "teks": "ءَاَنۡتُمۡ  اَنۡشَاۡتُمۡ شَجَرَتَهَاۤ اَمۡ نَحۡنُ الۡمُنۡشِـُٔـوۡنَ‏"},
"5106": {"surat": 56, "ayat": 74, "teks": "نَحۡنُ جَعَلۡنٰهَا تَذۡكِرَةً  وَّمَتَاعًا لِّلۡمُقۡوِيۡنَ‌ۚ‏"},
"5107": {"surat": 56, "ayat": 75, "teks": "فَسَبِّحۡ بِاسۡمِ رَبِّكَ الۡعَظِيۡمِ‏"},
"5108": {"surat": 56, "ayat": 76, "teks": "فَلَاۤ اُقۡسِمُ بِمَوٰقِعِ النُّجُوۡمِۙ‏‏"},
"5109": {"surat": 56, "ayat": 77, "teks": "وَاِنَّهٗ لَقَسَمٌ لَّوۡ تَعۡلَمُوۡنَ عَظِيۡمٌۙ‏"},
"5110": {"surat": 56, "ayat": 78, "teks": "اِنَّهٗ لَـقُرۡاٰنٌ كَرِيۡمٌۙ‏"},
"5111": {"surat": 56, "ayat": 79, "teks": "فِىۡ كِتٰبٍ مَّكۡنُوۡنٍۙ‏"},
"5112": {"surat": 56, "ayat": 80, "teks": "لَّا يَمَسُّهٗۤ اِلَّا الۡمُطَهَّرُوۡنَؕ‏"},
"5113": {"surat": 56, "ayat": 81, "teks": "تَنۡزِيۡلٌ مِّنۡ رَّبِّ الۡعٰلَمِيۡنَ‏"},
"5114": {"surat": 56, "ayat": 82, "teks": "اَفَبِهٰذَا الۡحَـدِيۡثِ اَنۡتُمۡ مُّدۡهِنُوۡنَۙ‏"},
"5115": {"surat": 56, "ayat": 83, "teks": "وَتَجۡعَلُوۡنَ رِزۡقَكُمۡ اَنَّكُمۡ تُكَذِّبُوۡنَ‏"},
"5116": {"surat": 56, "ayat": 84, "teks": "فَلَوۡلَاۤ اِذَا بَلَغَتِ الۡحُـلۡقُوۡمَۙ‏"},
"5117": {"surat": 56, "ayat": 85, "teks": "وَاَنۡتُمۡ حِيۡنَٮِٕذٍ تَـنۡظُرُوۡنَۙ‏"},
"5118": {"surat": 56, "ayat": 86, "teks": "وَنَحۡنُ اَقۡرَبُ اِلَيۡهِ مِنۡكُمۡ وَلٰـكِنۡ لَّا  تُبۡصِرُوۡنَ‏"},
"5119": {"surat": 56, "ayat": 87, "teks": "فَلَوۡلَاۤ اِنۡ كُنۡتُمۡ غَيۡرَ مَدِيۡنِيۡنَۙ‏"},
"5120": {"surat": 56, "ayat": 88, "teks": "تَرۡجِعُوۡنَهَاۤ اِنۡ  كُنۡتُمۡ صٰدِقِيۡنَ‏"},
"5121": {"surat": 56, "ayat": 89, "teks": "فَاَمَّاۤ اِنۡ كَانَ مِنَ الۡمُقَرَّبِيۡنَۙ‏"},
"5122": {"surat": 56, "ayat": 90, "teks": "فَرَوۡحٌ وَّ رَيۡحَانٌ ۙ وَّجَنَّتُ نَعِيۡمٍ‏"},
"5123": {"surat": 56, "ayat": 91, "teks": "وَاَمَّاۤ اِنۡ كَانَ مِنۡ اَصۡحٰبِ الۡيَمِيۡنِۙ‏"},
"5124": {"surat": 56, "ayat": 92, "teks": "فَسَلٰمٌ لَّكَ مِنۡ اَصۡحٰبِ الۡيَمِيۡنِؕ‏"},
"5125": {"surat": 56, "ayat": 93, "teks": "وَاَمَّاۤ اِنۡ كَانَ مِنَ الۡمُكَذِّبِيۡنَ  الضَّآلِّيۡنَۙ‏"},
"5126": {"surat": 56, "ayat": 94, "teks": "فَنُزُلٌ مِّنۡ حَمِيۡمٍۙ‏"},
"5127": {"surat": 56, "ayat": 95, "teks": "وَّتَصۡلِيَةُ جَحِيۡمٍ‏"},
"5128": {"surat": 56, "ayat": 96, "teks": "اِنَّ  هٰذَا لَهُوَ حَقُّ الۡيَـقِيۡنِۚ‏"},
"5129": {"surat": 56, "ayat": 97, "teks": "فَسَبِّحۡ بِاسۡمِ رَبِّكَ الۡعَظِيۡمِ‏"},
"5130": {"surat": 57, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5131": {"surat": 57, "ayat": 2, "teks": "سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالۡاَرۡضِ‌ۚ وَهُوَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5132": {"surat": 57, "ayat": 3, "teks": "لَهٗ مُلۡكُ  السَّمٰوٰتِ وَالۡاَرۡضِ‌ۚ يُحۡىٖ وَيُمِيۡتُ‌ۚ وَهُوَ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرٌ‏"},
"5133": {"surat": 57, "ayat": 4, "teks": "هُوَ الۡاَوَّلُ وَالۡاٰخِرُ وَالظَّاهِرُ وَالۡبَاطِنُ‌ۚ وَهُوَ بِكُلِّ شَىۡءٍ عَلِيۡمٌ‏"},
"5134": {"surat": 57, "ayat": 5, "teks": "هُوَ الَّذِىۡ خَلَقَ السَّمٰوٰتِ وَالۡاَرۡضَ فِىۡ سِتَّةِ اَيَّامٍ ثُمَّ اسۡتَوٰى عَلَى الۡعَرۡشِ‌ؕ يَعۡلَمُ مَا يَلِجُ فِى الۡاَرۡضِ وَمَا يَخۡرُجُ مِنۡهَا وَمَا يَنۡزِلُ مِنَ السَّمَآءِ وَمَا يَعۡرُجُ فِيۡهَاؕ وَهُوَ مَعَكُمۡ اَيۡنَ مَا كُنۡتُمۡ‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ بَصِيۡرٌ‏"},
"5135": {"surat": 57, "ayat": 6, "teks": "لَهٗ مُلۡكُ السَّمٰوٰتِ وَالۡاَرۡضِ‌ؕ وَاِلَى اللّٰهِ تُرۡجَعُ  الۡاُمُوۡرُ‏"},
"5136": {"surat": 57, "ayat": 7, "teks": "يُوۡلِجُ الَّيۡلَ فِى النَّهَارِ وَيُوۡلِجُ النَّهَارَ فِى الَّيۡلِ‌ؕ وَهُوَ عَلِيۡمٌۢ بِذَاتِ الصُّدُوۡرِ‏"},
"5137": {"surat": 57, "ayat": 8, "teks": "اٰمِنُوۡا بِاللّٰهِ وَرَسُوۡلِهٖ وَاَنۡفِقُوۡا مِمَّا  جَعَلَـكُمۡ مُّسۡتَخۡلَفِيۡنَ فِيۡهِ‌ؕ فَالَّذِيۡنَ اٰمَنُوۡا مِنۡكُمۡ وَاَنۡفَقُوۡا  لَهُمۡ اَجۡرٌ كَبِيۡرٌ‏"},
"5138": {"surat": 57, "ayat": 9, "teks": "وَمَا لَـكُمۡ لَا تُؤۡمِنُوۡنَ بِاللّٰهِ‌ۚ وَالرَّسُوۡلُ يَدۡعُوۡكُمۡ  لِتُؤۡمِنُوۡا بِرَبِّكُمۡ وَقَدۡ اَخَذَ مِيۡثَاقَكُمۡ اِنۡ كُنۡتُمۡ مُّؤۡمِنِيۡنَ‏"},
"5139": {"surat": 57, "ayat": 10, "teks": "هُوَ الَّذِىۡ يُنَزِّلُ عَلٰى عَبۡدِهٖۤ اٰيٰتٍۭ بَيِّنٰتٍ لِّيُخۡرِجَكُمۡ مِّنَ الظُّلُمٰتِ اِلَى النُّوۡرِ‌ؕ وَاِنَّ اللّٰهَ بِكُمۡ لَرَءُوۡفٌ رَّحِيۡمٌ‏"},
"5140": {"surat": 57, "ayat": 11, "teks": "وَ مَا لَـكُمۡ اَلَّا تُنۡفِقُوۡا فِىۡ سَبِيۡلِ اللّٰهِ وَلِلّٰهِ مِيۡـرَاثُ السَّمٰوٰتِ وَ الۡاَرۡضِ‌ؕ لَا يَسۡتَوِىۡ مِنۡكُمۡ مَّنۡ اَنۡفَقَ مِنۡ قَبۡلِ الۡفَتۡحِ وَقَاتَلَ‌  ؕ اُولٰٓٮِٕكَ اَعۡظَمُ دَرَجَةً مِّنَ الَّذِيۡنَ اَنۡفَقُوۡا مِنۡۢ بَعۡدُ وَقَاتَلُوۡا‌  ؕ وَكُلًّا وَّعَدَ اللّٰهُ الۡحُسۡنٰى‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ خَبِيۡرٌ‏"},
"5141": {"surat": 57, "ayat": 12, "teks": "مَنۡ ذَا الَّذِىۡ يُقۡرِضُ اللّٰهَ قَرۡضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗ وَلَهٗۤ اَجۡرٌ كَرِيۡمٌ ۚ‏"},
"5142": {"surat": 57, "ayat": 13, "teks": "يَوۡمَ تَرَى الۡمُؤۡمِنِيۡنَ وَالۡمُؤۡمِنٰتِ يَسۡعٰى نُوۡرُهُمۡ  بَيۡنَ اَيۡدِيۡهِمۡ وَبِاَيۡمَانِهِمۡ بُشۡرٰٮكُمُ الۡيَوۡمَ جَنّٰتٌ تَجۡرِىۡ مِنۡ تَحۡتِهَا  الۡاَنۡهٰرُ خٰلِدِيۡنَ فِيۡهَا‌ؕ ذٰلِكَ هُوَ الۡفَوۡزُ الۡعَظِيۡمُ‌ۚ‏"},
"5143": {"surat": 57, "ayat": 14, "teks": "يَوۡمَ يَقُوۡلُ الۡمُنٰفِقُوۡنَ وَالۡمُنٰفِقٰتُ لِلَّذِيۡنَ اٰمَنُوا انْظُرُوۡنَا نَقۡتَبِسۡ مِنۡ نُّوۡرِكُمۡ‌ۚ قِيۡلَ ارۡجِعُوۡا وَرَآءَكُمۡ فَالۡتَمِسُوۡا نُوۡرًاؕ فَضُرِبَ بَيۡنَهُمۡ بِسُوۡرٍ لَّهٗ بَابٌؕ بَاطِنُهٗ فِيۡهِ الرَّحۡمَةُ وَظَاهِرُهٗ مِنۡ قِبَلِهِ الۡعَذَابُؕ‏"},
"5144": {"surat": 57, "ayat": 15, "teks": "يُنَادُوۡنَهُمۡ اَلَمۡ نَكُنۡ مَّعَكُمۡ‌ؕ قَالُوۡا بَلٰى وَلٰـكِنَّكُمۡ فَتَنۡتُمۡ اَنۡفُسَكُمۡ وَ تَرَبَّصۡتُمۡ وَارۡتَبۡتُمۡ وَغَرَّتۡكُمُ الۡاَمَانِىُّ حَتّٰى جَآءَ اَمۡرُ اللّٰهِ وَ غَرَّكُمۡ بِاللّٰهِ الۡغَرُوۡرُ‏"},
"5145": {"surat": 57, "ayat": 16, "teks": "فَالۡيَوۡمَ لَا يُؤۡخَذُ مِنۡكُمۡ فِدۡيَةٌ وَّلَا مِنَ  الَّذِيۡنَ كَفَرُوۡا‌ؕ مَاۡوٰٮكُمُ النَّارُ‌ؕ هِىَ مَوۡلٰٮكُمۡ‌ؕ وَبِئۡسَ الۡمَصِيۡرُ‏"},
"5146": {"surat": 57, "ayat": 17, "teks": "اَلَمۡ يَاۡنِ لِلَّذِيۡنَ اٰمَنُوۡۤا اَنۡ تَخۡشَعَ قُلُوۡبُهُمۡ لِذِكۡرِ اللّٰهِ وَمَا نَزَلَ مِنَ الۡحَـقِّۙ وَلَا يَكُوۡنُوۡا كَالَّذِيۡنَ اُوۡتُوا الۡكِتٰبَ مِنۡ قَبۡلُ فَطَالَ عَلَيۡهِمُ الۡاَمَدُ فَقَسَتۡ قُلُوۡبُهُمۡ‌ؕ وَكَثِيۡرٌ مِّنۡهُمۡ فٰسِقُوۡنَ‏"},
"5147": {"surat": 57, "ayat": 18, "teks": "اِعۡلَمُوۡۤا اَنَّ  اللّٰهَ يُحۡىِ الۡاَرۡضَ بَعۡدَ مَوۡتِهَا‌ؕ قَدۡ بَيَّنَّا لَكُمُ الۡاٰيٰتِ لَعَلَّكُمۡ تَعۡقِلُوۡنَ‏"},
"5148": {"surat": 57, "ayat": 19, "teks": "اِنَّ الۡمُصَّدِّقِيۡنَ وَالۡمُصَّدِّقٰتِ وَاَقۡرَضُوا اللّٰهَ قَرۡضًا حَسَنًا يُّضٰعَفُ لَهُمۡ وَلَهُمۡ اَجۡرٌ كَرِيۡمٌ‏"},
"5149": {"surat": 57, "ayat": 20, "teks": "وَالَّذِيۡنَ اٰمَنُوۡا بِاللّٰهِ وَرُسُلِهٖۤ اُولٰٓٮِٕكَ هُمُ  الصِّدِّيۡقُوۡنَۖ وَالشُّهَدَآءُ عِنۡدَ رَبِّهِمۡؕ لَهُمۡ اَجۡرُهُمۡ وَنُوۡرُهُمۡ‌ؕ وَ الَّذِيۡنَ كَفَرُوۡا وَكَذَّبُوۡا بِاٰيٰتِنَاۤ اُولٰٓٮِٕكَ اَصۡحٰبُ الۡجَحِيۡمِ‏"},
"5150": {"surat": 57, "ayat": 21, "teks": "اِعۡلَمُوۡۤا اَنَّمَا الۡحَيٰوةُ الدُّنۡيَا لَعِبٌ وَّلَهۡوٌ وَّزِيۡنَةٌ وَّتَفَاخُرٌۢ بَيۡنَكُمۡ وَتَكَاثُرٌ فِى الۡاَمۡوَالِ وَالۡاَوۡلَادِ‌ؕ كَمَثَلِ غَيۡثٍ اَعۡجَبَ الۡكُفَّارَ نَبَاتُهٗ ثُمَّ يَهِيۡجُ فَتَرٰٮهُ مُصۡفَرًّا ثُمَّ يَكُوۡنُ حُطٰمًا‌ؕ وَفِى الۡاٰخِرَةِ عَذَابٌ شَدِيۡدٌ ۙ وَّمَغۡفِرَةٌ مِّنَ اللّٰهِ وَرِضۡوَانٌ‌ؕ وَمَا الۡحَيٰوةُ الدُّنۡيَاۤ اِلَّا مَتَاعُ الۡغُرُوۡرِ‏"},
"5151": {"surat": 57, "ayat": 22, "teks": "سَابِقُوۡۤا اِلٰى مَغۡفِرَةٍ مِّنۡ رَّبِّكُمۡ وَجَنَّةٍ عَرۡضُهَا كَعَرۡضِ السَّمَآءِ وَ الۡاَرۡضِۙ اُعِدَّتۡ لِلَّذِيۡنَ اٰمَنُوۡا بِاللّٰهِ وَرُسُلِهٖ‌ؕ ذٰلِكَ فَضۡلُ اللّٰهِ يُؤۡتِيۡهِ مَنۡ يَّشَآءُ‌ؕ وَاللّٰهُ ذُو الۡفَضۡلِ الۡعَظِيۡمِ‏"},
"5152": {"surat": 57, "ayat": 23, "teks": "مَاۤ اَصَابَ مِنۡ مُّصِيۡبَةٍ فِى الۡاَرۡضِ وَلَا فِىۡۤ اَنۡفُسِكُمۡ اِلَّا فِىۡ كِتٰبٍ مِّنۡ قَبۡلِ اَنۡ نَّبۡـرَاَهَا  ؕ اِنَّ ذٰ لِكَ عَلَى اللّٰهِ يَسِيۡرٌۚ  ۖ‏"},
"5153": {"surat": 57, "ayat": 24, "teks": "لِّـكَيۡلَا تَاۡسَوۡا عَلٰى مَا فَاتَكُمۡ وَلَا تَفۡرَحُوۡا بِمَاۤ اٰتٰٮكُمۡ‌ؕ وَاللّٰهُ لَا يُحِبُّ كُلَّ مُخۡتَالٍ فَخُوۡرِۙ‏"},
"5154": {"surat": 57, "ayat": 25, "teks": "اۨلَّذِيۡنَ يَبۡخَلُوۡنَ وَيَاۡمُرُوۡنَ  النَّاسَ بِالۡبُخۡلِ‌ؕ وَمَنۡ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الۡغَنِىُّ الۡحَمِيۡدُ‏"},
"5155": {"surat": 57, "ayat": 26, "teks": "لَـقَدۡ اَرۡسَلۡنَا رُسُلَنَا بِالۡبَيِّنٰتِ وَاَنۡزَلۡنَا مَعَهُمُ الۡكِتٰبَ  وَالۡمِيۡزَانَ لِيَقُوۡمَ النَّاسُ بِالۡقِسۡطِ‌ۚ وَاَنۡزَلۡنَا الۡحَـدِيۡدَ فِيۡهِ  بَاۡسٌ شَدِيۡدٌ وَّمَنَافِعُ لِلنَّاسِ وَلِيَـعۡلَمَ اللّٰهُ مَنۡ يَّنۡصُرُهٗ وَ رُسُلَهٗ بِالۡغَيۡبِ‌ؕ اِنَّ اللّٰهَ قَوِىٌّ عَزِيۡزٌ‏"},
"5156": {"surat": 57, "ayat": 27, "teks": "وَلَقَدۡ اَرۡسَلۡنَا نُوۡحًا وَّ اِبۡرٰهِيۡمَ وَجَعَلۡنَا فِىۡ ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالۡـكِتٰبَ‌ فَمِنۡهُمۡ مُّهۡتَدٍ‌ۚ وَكَثِيۡرٌ مِّنۡهُمۡ فٰسِقُوۡنَ‏"},
"5157": {"surat": 57, "ayat": 28, "teks": "ثُمَّ قَفَّيۡنَا عَلٰٓى اٰثَارِهِمۡ بِرُسُلِنَا وَقَفَّيۡنَا بِعِيۡسَى ابۡنِ مَرۡيَمَ وَاٰتَيۡنٰهُ الۡاِنۡجِيۡلَ ۙ وَجَعَلۡنَا فِىۡ قُلُوۡبِ الَّذِيۡنَ اتَّبَعُوۡهُ رَاۡفَةً وَّرَحۡمَةً  ؕ وَرَهۡبَانِيَّةَ  اۨبۡتَدَعُوۡهَا مَا كَتَبۡنٰهَا عَلَيۡهِمۡ اِلَّا ابۡتِغَآءَ رِضۡوَانِ اللّٰهِ فَمَا رَعَوۡهَا حَقَّ رِعَايَتِهَا‌  ۚ فَاٰتَيۡنَا الَّذِيۡنَ اٰمَنُوۡا مِنۡهُمۡ اَجۡرَهُمۡ‌ۚ وَكَثِيۡرٌ مِّنۡهُمۡ فٰسِقُوۡنَ‏"},
"5158": {"surat": 57, "ayat": 29, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَاٰمِنُوۡا بِرَسُوۡلِهٖ يُؤۡتِكُمۡ كِفۡلَيۡنِ مِنۡ رَّحۡمَتِهٖ وَيَجۡعَلْ لَّـكُمۡ نُوۡرًا تَمۡشُوۡنَ بِهٖ وَيَغۡفِرۡ لَـكُمۡ‌ؕ وَاللّٰهُ غَفُوۡرٌ رَّحِيۡمٌ   ۙۚ‏"},
"5159": {"surat": 57, "ayat": 30, "teks": "لِّـئَلَّا يَعۡلَمَ اَهۡلُ الۡكِتٰبِ اَلَّا يَقۡدِرُوۡنَ عَلٰى شَىۡءٍ مِّنۡ فَضۡلِ اللّٰهِ‌ وَاَنَّ الۡفَضۡلَ بِيَدِ اللّٰهِ يُؤۡتِيۡهِ مَنۡ يَّشَآءُ‌  ؕ وَاللّٰهُ ذُوۡ الۡفَضۡلِ الۡعَظِيۡمِ‏"},
"5160": {"surat": 58, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5161": {"surat": 58, "ayat": 2, "teks": "قَدۡ سَمِعَ اللّٰهُ قَوۡلَ الَّتِىۡ تُجَادِلُكَ فِىۡ زَوۡجِهَا وَ تَشۡتَكِىۡۤ اِلَى اللّٰهِ‌ۖ وَاللّٰهُ يَسۡمَعُ تَحَاوُرَكُمَا‌ؕ اِنَّ اللّٰهَ سَمِيۡعٌۢ بَصِيۡرٌ‏"},
"5162": {"surat": 58, "ayat": 3, "teks": "اَلَّذِيۡنَ يُظٰهِرُوۡنَ مِنۡكُمۡ مِّنۡ نِّسَآٮِٕهِمۡ مَّا هُنَّ اُمَّهٰتِهِمۡ‌ؕ اِنۡ اُمَّهٰتُهُمۡ  اِلَّا الّٰٓـىِٔۡ وَلَدۡنَهُمۡ‌ؕ وَاِنَّهُمۡ لَيَقُوۡلُوۡنَ مُنۡكَرًا مِّنَ الۡقَوۡلِ وَزُوۡرًا‌ؕ وَ اِنَّ اللّٰهَ لَعَفُوٌّ غَفُوۡرٌ‏"},
"5163": {"surat": 58, "ayat": 4, "teks": "وَالَّذِيۡنَ يُظٰهِرُوۡنَ مِنۡ نِّسَآٮِٕهِمۡ ثُمَّ يَعُوۡدُوۡنَ لِمَا قَالُوۡا فَتَحۡرِيۡرُ رَقَبَةٍ مِّنۡ قَبۡلِ اَنۡ يَّتَمَآسَّا‌ ؕ ذٰ لِكُمۡ تُوۡعَظُوۡنَ بِهٖ‌  ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ خَبِيۡرٌ‏"},
"5164": {"surat": 58, "ayat": 5, "teks": "فَمَنۡ لَّمۡ يَجِدۡ فَصِيَامُ  شَهۡرَيۡنِ مُتَتَابِعَيۡنِ مِنۡ قَبۡلِ اَنۡ يَّتَمَآسَّاؕ فَمَنۡ لَّمۡ يَسۡتَطِعۡ فَاِطۡعَامُ  سِتِّيۡنَ مِسۡكِيۡنًا‌ؕ ذٰلِكَ لِتُؤۡمِنُوۡا بِاللّٰهِ وَرَسُوۡلِهٖ‌ؕ وَتِلۡكَ حُدُوۡدُ اللّٰهِ‌ؕ  وَلِلۡكٰفِرِيۡنَ عَذَابٌ اَلِیْمٌ‏"},
"5165": {"surat": 58, "ayat": 6, "teks": "اِنَّ الَّذِيۡنَ يُحَآدُّوۡنَ اللّٰهَ وَرَسُوۡلَهٗ كُبِتُوۡا كَمَا كُبِتَ الَّذِيۡنَ مِنۡ قَبۡلِهِمۡ‌ وَقَدۡ اَنۡزَلۡنَاۤ اٰيٰتٍۢ بَيِّنٰتٍ‌  ؕ وَ لِلۡكٰفِرِيۡنَ عَذَابٌ مُّهِيۡنٌ‌ ۚ‏"},
"5166": {"surat": 58, "ayat": 7, "teks": "يَوۡمَ يَبۡعَثُهُمُ اللّٰهُ جَمِيۡعًا فَيُنَبِّئُهُمۡ بِمَا عَمِلُوۡا‌  ؕ اَحۡصٰٮهُ اللّٰهُ وَنَسُوۡهُ‌  ؕ وَاللّٰهُ عَلٰى كُلِّ شَىۡءٍ شَهِيۡدٌ‌‏"},
"5167": {"surat": 58, "ayat": 8, "teks": "اَلَمۡ تَرَ اَنَّ اللّٰهَ يَعۡلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الۡاَرۡضِ‌ؕ مَا يَكُوۡنُ مِنۡ نَّجۡوٰى ثَلٰثَةٍ اِلَّا هُوَ رَابِعُهُمۡ وَلَا خَمۡسَةٍ اِلَّا هُوَ سَادِسُهُمۡ وَلَاۤ اَدۡنٰى مِنۡ ذٰ لِكَ وَلَاۤ اَكۡثَرَ اِلَّا هُوَ مَعَهُمۡ اَيۡنَ مَا كَانُوۡا‌ۚ ثُمَّ يُنَبِّئُهُمۡ بِمَا عَمِلُوۡا يَوۡمَ الۡقِيٰمَةِ‌  ؕ اِنَّ اللّٰهَ بِكُلِّ شَىۡءٍ عَلِيۡمٌ‏"},
"5168": {"surat": 58, "ayat": 9, "teks": "اَلَمۡ تَرَ اِلَى الَّذِيۡنَ نُهُوۡا عَنِ  النَّجۡوٰى ثُمَّ يَعُوۡدُوۡنَ لِمَا نُهُوۡا عَنۡهُ وَيَتَنٰجَوۡنَ بِالۡاِثۡمِ وَالۡعُدۡوَانِ  وَمَعۡصِيَتِ الرَّسُوۡلِ وَاِذَا جَآءُوۡكَ حَيَّوۡكَ بِمَا لَمۡ يُحَيِّكَ بِهِ اللّٰهُۙ  وَيَقُوۡلُوۡنَ فِىۡۤ اَنۡفُسِهِمۡ لَوۡلَا يُعَذِّبُنَا اللّٰهُ بِمَا نَقُوۡلُ‌ؕ حَسۡبُهُمۡ جَهَنَّمُ‌ۚ  يَصۡلَوۡنَهَا‌ۚ فَبِئۡسَ الۡمَصِيۡرُ‏"},
"5169": {"surat": 58, "ayat": 10, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡۤا اِذَا تَنَاجَيۡتُمۡ فَلَا تَـتَـنَاجَوۡا بِالۡاِثۡمِ وَالۡعُدۡوَانِ وَمَعۡصِيَتِ الرَّسُوۡلِ وَتَنَاجَوۡا بِالۡبِرِّ وَالتَّقۡوٰى‌ؕ وَاتَّقُوا اللّٰهَ الَّذِىۡۤ اِلَيۡهِ تُحۡشَرُوۡنَ‏"},
"5170": {"surat": 58, "ayat": 11, "teks": "اِنَّمَا النَّجۡوٰى  مِنَ الشَّيۡطٰنِ لِيَحۡزُنَ الَّذِيۡنَ اٰمَنُوۡا وَلَيۡسَ بِضَآرِّهِمۡ شَيۡـًٔـا اِلَّا  بِاِذۡنِ اللّٰهِ‌ؕ وَعَلَى اللّٰهِ فَلۡيَتَوَكَّلِ الۡمُؤۡمِنُوۡنَ‏"},
"5171": {"surat": 58, "ayat": 12, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡۤا اِذَا قِيۡلَ لَـكُمۡ تَفَسَّحُوۡا فِى الۡمَجٰلِسِ فَافۡسَحُوۡا يَفۡسَحِ اللّٰهُ لَـكُمۡ‌  ۚ وَاِذَا قِيۡلَ انْشُزُوۡا فَانْشُزُوۡا يَرۡفَعِ اللّٰهُ الَّذِيۡنَ اٰمَنُوۡا مِنۡكُمۡ ۙ وَالَّذِيۡنَ اُوۡتُوا الۡعِلۡمَ دَرَجٰتٍ  ‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ خَبِيۡرٌ‏"},
"5172": {"surat": 58, "ayat": 13, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡۤا اِذَا نَاجَيۡتُمُ الرَّسُوۡلَ فَقَدِّمُوۡا بَيۡنَ يَدَىۡ نَجۡوٰٮكُمۡ صَدَقَةً  ‌ؕ ذٰ لِكَ خَيۡرٌ لَّكُمۡ وَاَطۡهَرُ  ‌ؕ فَاِنۡ لَّمۡ تَجِدُوۡا فَاِنَّ اللّٰهَ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5173": {"surat": 58, "ayat": 14, "teks": "ءَاَشۡفَقۡتُمۡ اَنۡ تُقَدِّمُوۡا بَيۡنَ يَدَىۡ نَجۡوٰٮكُمۡ صَدَقٰتٍ‌  ؕ فَاِذۡ لَمۡ تَفۡعَلُوۡا وَتَابَ اللّٰهُ عَلَيۡكُمۡ فَاَقِيۡمُوا الصَّلٰوةَ وَ اٰتُوا الزَّكٰوةَ وَاَطِيۡعُوا اللّٰهَ وَرَسُوۡلَهٗ‌  ؕ وَاللّٰهُ خَبِيۡرٌۢ بِمَا تَعۡمَلُوۡنَ‏"},
"5174": {"surat": 58, "ayat": 15, "teks": "اَلَمۡ تَرَ اِلَى الَّذِيۡنَ تَوَلَّوۡا قَوۡمًا غَضِبَ اللّٰهُ عَلَيۡهِمؕۡ مَّا هُمۡ مِّنۡكُمۡ وَلَا  مِنۡهُمۡۙ وَيَحۡلِفُوۡنَ عَلَى الۡكَذِبِ وَهُمۡ يَعۡلَمُوۡنَ‏"},
"5175": {"surat": 58, "ayat": 16, "teks": "اَعَدَّ اللّٰهُ لَهُمۡ عَذَابًا شَدِيۡدًا‌  ؕ اِنَّهُمۡ سَآءَ مَا كَانُوۡا يَعۡمَلُوۡنَ‏"},
"5176": {"surat": 58, "ayat": 17, "teks": "اِتَّخَذُوۡۤا اَيۡمَانَهُمۡ جُنَّةً  فَصَدُّوۡا عَنۡ سَبِيۡلِ اللّٰهِ فَلَهُمۡ عَذَابٌ مُّهِيۡنٌ‏"},
"5177": {"surat": 58, "ayat": 18, "teks": "لَنۡ تُغۡنِىَ عَنۡهُمۡ اَمۡوَالُهُمۡ وَلَاۤ اَوۡلَادُهُمۡ مِّنَ اللّٰهِ شَيۡــًٔـا‌  ؕ اُولٰٓٮِٕكَ اَصۡحٰبُ النَّارِ‌  ؕ هُمۡ فِيۡهَا خٰلِدُوۡنَ‏"},
"5178": {"surat": 58, "ayat": 19, "teks": "يَوۡمَ يَبۡعَثُهُمُ اللّٰهُ جَمِيۡعًا فَيَحۡلِفُوۡنَ لَهٗ كَمَا يَحۡلِفُوۡنَ لَـكُمۡ‌ وَيَحۡسَبُوۡنَ اَنَّهُمۡ عَلٰى شَىۡءٍ‌  ؕ اَلَاۤ اِنَّهُمۡ هُمُ الۡكٰذِبُوۡنَ‏"},
"5179": {"surat": 58, "ayat": 20, "teks": "اِسۡتَحۡوَذَ عَلَيۡهِمُ الشَّيۡطٰنُ فَاَنۡسٰٮهُمۡ ذِكۡرَ اللّٰهِ‌ؕ  اُولٰٓٮِٕكَ حِزۡبُ الشَّيۡطٰنِ‌ؕ اَلَاۤ اِنَّ حِزۡبَ الشَّيۡطٰنِ هُمُ الۡخٰسِرُوۡنَ‏"},
"5180": {"surat": 58, "ayat": 21, "teks": "اِنَّ الَّذِيۡنَ يُحَآدُّوۡنَ اللّٰهَ وَرَسُوۡلَهٗۤ اُولٰٓٮِٕكَ فِى الۡاَذَلِّيۡنَ‏"},
"5181": {"surat": 58, "ayat": 22, "teks": "كَتَبَ اللّٰهُ لَاَغۡلِبَنَّ اَنَا وَرُسُلِىۡ‌ؕ اِنَّ اللّٰهَ قَوِىٌّ عَزِيۡزٌ‏"},
"5182": {"surat": 58, "ayat": 23, "teks": "لَا تَجِدُ قَوۡمًا يُّؤۡمِنُوۡنَ بِاللّٰهِ وَالۡيَوۡمِ الۡاٰخِرِ يُوَآدُّوۡنَ مَنۡ حَآدَّ اللّٰهَ وَرَسُوۡلَهٗ وَلَوۡ كَانُوۡۤا اٰبَآءَهُمۡ اَوۡ اَبۡنَآءَهُمۡ اَوۡ اِخۡوَانَهُمۡ اَوۡ عَشِيۡرَتَهُمۡ‌ؕ اُولٰٓٮِٕكَ كَتَبَ فِىۡ قُلُوۡبِهِمُ الۡاِيۡمَانَ وَاَيَّدَهُمۡ بِرُوۡحٍ مِّنۡهُ‌  ؕ وَيُدۡخِلُهُمۡ جَنّٰتٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ خٰلِدِيۡنَ فِيۡهَا‌  ؕ رَضِىَ اللّٰهُ عَنۡهُمۡ وَرَضُوۡا عَنۡهُ‌  ؕ اُولٰٓٮِٕكَ حِزۡبُ اللّٰهِ‌  ؕ اَلَاۤ اِنَّ حِزۡبَ اللّٰهِ هُمُ الۡمُفۡلِحُوۡنَ‏"},
"5183": {"surat": 59, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5184": {"surat": 59, "ayat": 2, "teks": "سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الۡاَرۡضِۚ وَهُوَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5185": {"surat": 59, "ayat": 3, "teks": "هُوَ الَّذِىۡۤ اَخۡرَجَ الَّذِيۡنَ كَفَرُوۡا مِنۡ اَهۡلِ الۡكِتٰبِ مِنۡ دِيَارِهِمۡ لِاَوَّلِ الۡحَشۡرِ‌ؔؕ مَا ظَنَنۡـتُمۡ اَنۡ يَّخۡرُجُوۡا‌ وَظَنُّوۡۤا اَنَّهُمۡ مَّانِعَتُهُمۡ حُصُوۡنُهُمۡ مِّنَ اللّٰهِ فَاَتٰٮهُمُ اللّٰهُ مِنۡ حَيۡثُ لَمۡ يَحۡتَسِبُوۡا وَقَذَفَ فِىۡ قُلُوۡبِهِمُ الرُّعۡبَ يُخۡرِبُوۡنَ بُيُوۡتَهُمۡ بِاَيۡدِيۡهِمۡ وَاَيۡدِى الۡمُؤۡمِنِيۡنَ فَاعۡتَبِـرُوۡا يٰۤاُولِى الۡاَبۡصَارِ‌‏"},
"5186": {"surat": 59, "ayat": 4, "teks": "وَلَوۡلَاۤ اَنۡ كَتَبَ اللّٰهُ عَلَيۡهِمُ الۡجَـلَاۤءَ لَعَذَّبَهُمۡ فِى الدُّنۡيَا‌ؕ وَلَهُمۡ فِى الۡاٰخِرَةِ عَذَابُ النَّارِ‏"},
"5187": {"surat": 59, "ayat": 5, "teks": "ذٰ لِكَ بِاَنَّهُمۡ شَآقُّوا اللّٰهَ وَرَسُوۡلَهٗ‌  ۚ وَمَنۡ يُّشَآقِّ اللّٰهَ فَاِنَّ اللّٰهَ شَدِيۡدُ الۡعِقَابِ‏"},
"5188": {"surat": 59, "ayat": 6, "teks": "مَا قَطَعۡتُمۡ مِّنۡ لِّيۡنَةٍ اَوۡ تَرَكۡتُمُوۡهَا قَآٮِٕمَةً عَلٰٓى  اُصُوۡلِهَا فَبِاِذۡنِ اللّٰهِ وَلِيُخۡزِىَ الۡفٰسِقِيۡنَ‏"},
"5189": {"surat": 59, "ayat": 7, "teks": "وَمَاۤ اَفَآءَ اللّٰهُ عَلٰى  رَسُوۡلِهٖ مِنۡهُمۡ فَمَاۤ اَوۡجَفۡتُمۡ عَلَيۡهِ مِنۡ خَيۡلٍ وَّلَا رِكَابٍ وَّلٰڪِنَّ  اللّٰهَ يُسَلِّطُ رُسُلَهٗ عَلٰى مَنۡ يَّشَآءُ‌ؕ وَاللّٰهُ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرٌ‏"},
"5190": {"surat": 59, "ayat": 8, "teks": "مَاۤ اَفَآءَ اللّٰهُ عَلٰى رَسُوۡلِهٖ مِنۡ اَهۡلِ الۡقُرٰى فَلِلّٰهِ وَلِلرَّسُوۡلِ وَلِذِى الۡقُرۡبٰى وَالۡيَتٰمٰى وَالۡمَسٰكِيۡنِ وَابۡنِ السَّبِيۡلِۙ كَىۡ لَا يَكُوۡنَ دُوۡلَةًۢ بَيۡنَ الۡاَغۡنِيَآءِ مِنۡكُمۡ‌ ؕ وَمَاۤ اٰتٰٮكُمُ الرَّسُوۡلُ فَخُذُوْهُ وَ مَا َنَهٰٮكُمۡ عَنۡهُ فَانْتَهُوۡا‌ ۚ وَاتَّقُوا اللّٰهَ ‌ؕ اِنَّ اللّٰهَ شَدِيۡدُ الۡعِقَابِ‌ۘ‏"},
"5191": {"surat": 59, "ayat": 9, "teks": "لِلۡفُقَرَآءِ الۡمُهٰجِرِيۡنَ الَّذِيۡنَ اُخۡرِجُوۡا مِنۡ دِيَارِهِمۡ وَاَمۡوَالِهِمۡ يَبۡتَغُوۡنَ فَضۡلًا مِّنَ اللّٰهِ وَرِضۡوَانًا وَّيَنۡصُرُوۡنَ اللّٰهَ وَرَسُوۡلَهٗ‌ؕ اُولٰٓٮِٕكَ هُمُ الصّٰدِقُوۡنَ‌ۚ‏"},
"5192": {"surat": 59, "ayat": 10, "teks": "وَالَّذِيۡنَ تَبَوَّؤُ الدَّارَ وَالۡاِيۡمَانَ مِنۡ قَبۡلِهِمۡ يُحِبُّوۡنَ مَنۡ هَاجَرَ اِلَيۡهِمۡ وَلَا يَجِدُوۡنَ فِىۡ صُدُوۡرِهِمۡ حَاجَةً مِّمَّاۤ اُوۡتُوۡا وَيُـؤۡثِرُوۡنَ عَلٰٓى اَنۡفُسِهِمۡ وَلَوۡ كَانَ بِهِمۡ خَصَاصَةٌ   ؕ وَمَنۡ يُّوۡقَ شُحَّ نَـفۡسِهٖ فَاُولٰٓٮِٕكَ هُمُ الۡمُفۡلِحُوۡنَ‌ۚ‏"},
"5193": {"surat": 59, "ayat": 11, "teks": "وَالَّذِيۡنَ جَآءُوۡ مِنۡۢ بَعۡدِهِمۡ يَقُوۡلُوۡنَ رَبَّنَا اغۡفِرۡ لَـنَا وَلِاِخۡوَانِنَا الَّذِيۡنَ سَبَقُوۡنَا بِالۡاِيۡمَانِ وَلَا تَجۡعَلۡ فِىۡ قُلُوۡبِنَا غِلًّا لِّلَّذِيۡنَ اٰمَنُوۡا رَبَّنَاۤ اِنَّكَ رَءُوۡفٌ رَّحِيۡمٌ‏"},
"5194": {"surat": 59, "ayat": 12, "teks": "اَلَمۡ تَرَ اِلَى الَّذِيۡنَ نَافَقُوۡا يَقُوۡلُوۡنَ لِاِخۡوَانِهِمُ الَّذِيۡنَ كَفَرُوۡا مِنۡ اَهۡلِ الۡكِتٰبِ لَٮِٕنۡ اُخۡرِجۡتُمۡ لَنَخۡرُجَنَّ مَعَكُمۡ وَلَا نُطِيۡعُ فِيۡكُمۡ اَحَدًا اَبَدًاۙ وَّاِنۡ قُوۡتِلۡتُمۡ لَـنَـنۡصُرَنَّكُمۡ ؕ وَاللّٰهُ يَشۡهَدُ اِنَّهُمۡ لَـكٰذِبُوۡنَ‏"},
"5195": {"surat": 59, "ayat": 13, "teks": "لَٮِٕنۡ اُخۡرِجُوۡا  لَا يَخۡرُجُوۡنَ مَعَهُمۡ‌ۚ وَلَٮِٕنۡ قُوۡتِلُوۡا لَا يَنۡصُرُوۡنَهُمۡ‌ۚ وَلَٮِٕنۡ نَّصَرُوۡهُمۡ  لَيُوَلُّنَّ الۡاَدۡبَارَ ثُمَّ لَا يُنۡصَرُوۡنَ‏"},
"5196": {"surat": 59, "ayat": 14, "teks": "لَاَنۡتُمۡ اَشَدُّ رَهۡبَةً  فِىۡ صُدُوۡرِهِمۡ مِّنَ اللّٰهِ‌ؕ ذٰلِكَ بِاَنَّهُمۡ قَوۡمٌ لَّا يَفۡقَهُوۡنَ‏"},
"5197": {"surat": 59, "ayat": 15, "teks": "لَا يُقَاتِلُوۡنَكُمۡ جَمِيۡعًا اِلَّا فِىۡ قُرًى مُّحَصَّنَةٍ اَوۡ مِنۡ وَّرَآءِ  جُدُرٍؕ بَاۡسُهُمۡ بَيۡنَهُمۡ شَدِيۡدٌ‌ؕ تَحۡسَبُهُمۡ جَمِيۡعًا وَّقُلُوۡبُهُمۡ  شَتّٰى‌ؕ ذٰلِكَ بِاَنَّهُمۡ قَوۡمٌ لَّا يَعۡقِلُوۡنَ‌ۚ‏"},
"5198": {"surat": 59, "ayat": 16, "teks": "كَمَثَلِ الَّذِيۡنَ  مِنۡ قَبۡلِهِمۡ قَرِيۡبًا‌ ذَاقُوۡا وَبَالَ اَمۡرِهِمۡ‌ۚ وَلَهُمۡ عَذَابٌ  اَلِيۡمٌ‌ۚ‏"},
"5199": {"surat": 59, "ayat": 17, "teks": "كَمَثَلِ الشَّيۡطٰنِ اِذۡ قَالَ لِلۡاِنۡسَانِ اكۡفُرۡ‌ۚ فَلَمَّا كَفَرَ  قَالَ اِنِّىۡ بَرِىۡٓءٌ مِّنۡكَ اِنِّىۡۤ اَخَافُ اللّٰهَ رَبَّ الۡعٰلَمِيۡنَ‏"},
"5200": {"surat": 59, "ayat": 18, "teks": "فَكَانَ عَاقِبَتَهُمَاۤ اَنَّهُمَا فِى النَّارِ خَالِدَيۡنِ فِيۡهَا‌  ؕ وَذٰ لِكَ جَزٰٓؤُا الظّٰلِمِيۡن‏"},
"5201": {"surat": 59, "ayat": 19, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا اتَّقُوا اللّٰهَ وَلۡتَـنۡظُرۡ نَـفۡسٌ مَّا قَدَّمَتۡ لِغَدٍ‌ ۚ وَاتَّقُوا اللّٰهَ‌ؕ اِنَّ اللّٰهَ خَبِيۡرٌۢ بِمَا تَعۡمَلُوۡنَ‏"},
"5202": {"surat": 59, "ayat": 20, "teks": "وَلَا تَكُوۡنُوۡا كَالَّذِيۡنَ نَسُوا اللّٰهَ فَاَنۡسٰٮهُمۡ اَنۡفُسَهُمۡ‌ؕ اُولٰٓٮِٕكَ هُمُ الۡفٰسِقُوۡنَ‏"},
"5203": {"surat": 59, "ayat": 21, "teks": "لَا يَسۡتَوِىۡۤ اَصۡحٰبُ  النَّارِ وَاَصۡحٰبُ الۡجَـنَّةِ‌ؕ اَصۡحٰبُ الۡجَـنَّةِ هُمُ الۡفَآٮِٕزُوۡنَ‏"},
"5204": {"surat": 59, "ayat": 22, "teks": "لَوۡ اَنۡزَلۡنَا هٰذَا الۡقُرۡاٰنَ عَلٰى جَبَلٍ لَّرَاَيۡتَهٗ خَاشِعًا  مُّتَصَدِّعًا مِّنۡ خَشۡيَةِ اللّٰهِ‌ؕ وَتِلۡكَ الۡاَمۡثَالُ نَضۡرِبُهَا  لِلنَّاسِ لَعَلَّهُمۡ يَتَفَكَّرُوۡنَ‏"},
"5205": {"surat": 59, "ayat": 23, "teks": "هُوَ اللّٰهُ الَّذِىۡ لَاۤ اِلٰهَ اِلَّا هُوَ‌  ۚ عٰلِمُ الۡغَيۡبِ وَالشَّهَادَةِ‌   ۚ هُوَ الرَّحۡمٰنُ الرَّحِيۡمُ‏"},
"5206": {"surat": 59, "ayat": 24, "teks": "هُوَ اللّٰهُ الَّذِىۡ لَاۤ اِلٰهَ اِلَّا هُوَ‌ۚ اَلۡمَلِكُ الۡقُدُّوۡسُ السَّلٰمُ  الۡمُؤۡمِنُ الۡمُهَيۡمِنُ الۡعَزِيۡزُ الۡجَـبَّارُ الۡمُتَكَبِّرُ‌ؕ سُبۡحٰنَ اللّٰهِ  عَمَّا يُشۡرِكُوۡنَ‏"},
"5207": {"surat": 59, "ayat": 25, "teks": "هُوَ اللّٰهُ الۡخَـالِـقُ الۡبَارِئُ الۡمُصَوِّرُ‌ لَـهُ الۡاَسۡمَآءُ الۡحُسۡنٰى‌ؕ يُسَبِّحُ لَهٗ مَا فِى السَّمٰوٰتِ وَالۡاَرۡضِ‌ۚ وَهُوَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5208": {"surat": 60, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5209": {"surat": 60, "ayat": 2, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا لَا تَتَّخِذُوۡا عَدُوِّىۡ وَعَدُوَّكُمۡ اَوۡلِيَآءَ تُلۡقُوۡنَ اِلَيۡهِمۡ بِالۡمَوَدَّةِ وَقَدۡ كَفَرُوۡا بِمَا جَآءَكُمۡ مِّنَ الۡحَـقِّ‌  ۚ يُخۡرِجُوۡنَ الرَّسُوۡلَ وَاِيَّاكُمۡ‌ اَنۡ تُؤۡمِنُوۡا بِاللّٰهِ رَبِّكُمۡ  ؕ اِنۡ كُنۡـتُمۡ خَرَجۡتُمۡ جِهَادًا فِىۡ سَبِيۡلِىۡ وَ ابۡتِغَآءَ مَرۡضَاتِىۡ   ‌ۖ    تُسِرُّوۡنَ اِلَيۡهِمۡ بِالۡمَوَدَّةِ   ‌ۖ   وَاَنَا اَعۡلَمُ بِمَاۤ اَخۡفَيۡتُمۡ وَمَاۤ اَعۡلَنۡتُمۡ‌ؕ وَمَنۡ يَّفۡعَلۡهُ مِنۡكُمۡ فَقَدۡ ضَلَّ سَوَآءَ السَّبِيۡلِ‏"},
"5210": {"surat": 60, "ayat": 3, "teks": "اِنۡ  يَّثۡقَفُوۡكُمۡ يَكُوۡنُوۡا لَـكُمۡ اَعۡدَآءً وَّيَبۡسُطُوۡۤا اِلَيۡكُمۡ اَيۡدِيَهُمۡ  وَاَلۡسِنَتَهُمۡ  بِالسُّوۡٓءِ وَوَدُّوۡا لَوۡ تَكۡفُرُوۡنَؕ‏"},
"5211": {"surat": 60, "ayat": 4, "teks": "لَنۡ تَـنۡفَعَكُمۡ اَرۡحَامُكُمۡ وَلَاۤ اَوۡلَادُكُمۡ  ۛۚ يَوۡمَ الۡقِيٰمَةِ   ۛۚ يَفۡصِلُ بَيۡنَكُمۡ‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ بَصِيۡرٌ‏"},
"5212": {"surat": 60, "ayat": 5, "teks": "قَدۡ كَانَتۡ لَـكُمۡ اُسۡوَةٌ حَسَنَةٌ فِىۡۤ اِبۡرٰهِيۡمَ وَالَّذِيۡنَ مَعَهٗ‌ۚ اِذۡ قَالُوۡا لِقَوۡمِهِمۡ اِنَّا بُرَءٰٓؤُا مِنۡكُمۡ وَمِمَّا تَعۡبُدُوۡنَ مِنۡ دُوۡنِ اللّٰهِ كَفَرۡنَا بِكُمۡ وَبَدَا بَيۡنَنَا وَبَيۡنَكُمُ الۡعَدَاوَةُ وَالۡبَغۡضَآءُ اَبَدًا حَتّٰى تُؤۡمِنُوۡا بِاللّٰهِ وَحۡدَهٗۤ اِلَّا قَوۡلَ اِبۡرٰهِيۡمَ لِاَبِيۡهِ لَاَسۡتَغۡفِرَنَّ لَـكَ وَمَاۤ اَمۡلِكُ لَـكَ مِنَ اللّٰهِ مِنۡ شَىۡءٍ  ‌ؕ رَبَّنَا عَلَيۡكَ تَوَكَّلۡنَا وَاِلَيۡكَ اَنَـبۡنَا وَاِلَيۡكَ الۡمَصِيۡرُ‏"},
"5213": {"surat": 60, "ayat": 6, "teks": "رَبَّنَا لَا تَجۡعَلۡنَا فِتۡنَةً لِّلَّذِيۡنَ كَفَرُوۡا وَاغۡفِرۡ لَـنَا رَبَّنَا‌  ۚ اِنَّكَ اَنۡتَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5214": {"surat": 60, "ayat": 7, "teks": "لَقَدۡ كَانَ لَـكُمۡ فِيۡهِمۡ اُسۡوَةٌ حَسَنَةٌ لِّمَنۡ كَانَ يَرۡجُوا اللّٰهَ وَالۡيَوۡمَ الۡاٰخِرَ‌ ؕ وَمَنۡ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الۡغَنِىُّ الۡحَمِيۡدُ‏"},
"5215": {"surat": 60, "ayat": 8, "teks": "عَسَى اللّٰهُ اَنۡ يَّجۡعَلَ بَيۡنَكُمۡ وَبَيۡنَ الَّذِيۡنَ عَادَيۡتُمۡ مِّنۡهُمۡ مَّوَدَّةًؕ  وَاللّٰهُ قَدِيۡرٌ‌ؕ وَاللّٰهُ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5216": {"surat": 60, "ayat": 9, "teks": "لَا يَنۡهٰٮكُمُ اللّٰهُ عَنِ الَّذِيۡنَ لَمۡ يُقَاتِلُوۡكُمۡ فِى الدِّيۡنِ وَلَمۡ يُخۡرِجُوۡكُمۡ مِّنۡ دِيَارِكُمۡ اَنۡ تَبَرُّوۡهُمۡ وَ تُقۡسِطُوۡۤا اِلَيۡهِمۡ‌ؕ اِنَّ اللّٰهَ يُحِبُّ الۡمُقۡسِطِيۡنَ‏"},
"5217": {"surat": 60, "ayat": 10, "teks": "اِنَّمَا يَنۡهٰٮكُمُ اللّٰهُ عَنِ الَّذِيۡنَ قَاتَلُوۡكُمۡ فِى الدِّيۡنِ وَاَخۡرَجُوۡكُمۡ مِّنۡ دِيَارِكُمۡ وَظَاهَرُوۡا عَلٰٓى اِخۡرَاجِكُمۡ اَنۡ تَوَلَّوۡهُمۡ‌ۚ وَمَنۡ يَّتَوَلَّهُمۡ فَاُولٰٓٮِٕكَ هُمُ الظّٰلِمُوۡنَ‏"},
"5218": {"surat": 60, "ayat": 11, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡۤا اِذَا جَآءَكُمُ الۡمُؤۡمِنٰتُ مُهٰجِرٰتٍ فَامۡتَحِنُوۡهُنَّ‌  ؕ اَللّٰهُ اَعۡلَمُ بِاِيۡمَانِهِنَّ‌  ۚ فَاِنۡ عَلِمۡتُمُوۡهُنَّ مُؤۡمِنٰتٍ فَلَا تَرۡجِعُوۡهُنَّ اِلَى الۡكُفَّارِ‌  ؕ لَا هُنَّ حِلٌّ لَّهُمۡ وَلَا هُمۡ يَحِلُّوۡنَ لَهُنَّ‌  ۚ وَاٰ تُوۡهُمۡ مَّاۤ اَنۡفَقُوۡا‌  ؕ وَلَا جُنَاحَ عَلَيۡكُمۡ اَنۡ تَنۡكِحُوۡهُنَّ اِذَاۤ اٰ تَيۡتُمُوۡهُنَّ اُجُوۡرَهُنَّ‌  ؕ وَلَا تُمۡسِكُوۡا بِعِصَمِ الۡكَوَافِرِ وَسۡــَٔـلُوۡا مَاۤ اَنۡفَقۡتُمۡ وَلۡيَسۡــَٔـلُوۡا مَاۤ اَنۡفَقُوۡا‌  ؕ ذٰ لِكُمۡ حُكۡمُ اللّٰهِ‌  ؕ يَحۡكُمُ بَيۡنَكُمۡ‌  ؕ وَاللّٰهُ عَلِيۡمٌ حَكِيۡمٌ‏"},
"5219": {"surat": 60, "ayat": 12, "teks": "وَاِنۡ فَاتَكُمۡ شَىۡءٌ مِّنۡ اَزۡوَاجِكُمۡ اِلَى الۡكُفَّارِ فَعَاقَبۡتُمۡ فَاٰ تُوا الَّذِيۡنَ ذَهَبَتۡ اَزۡوَاجُهُمۡ مِّثۡلَ مَاۤ اَنۡفَقُوۡا‌  ؕ وَاتَّقُوا اللّٰهَ الَّذِىۡۤ اَنۡـتُمۡ بِهٖ مُؤۡمِنُوۡنَ‏"},
"5220": {"surat": 60, "ayat": 13, "teks": "يٰۤاَيُّهَا النَّبِىُّ اِذَا جَآءَكَ الۡمُؤۡمِنٰتُ يُبَايِعۡنَكَ  عَلٰٓى اَنۡ لَّا يُشۡرِكۡنَ بِاللّٰهِ شَيۡــًٔا وَّلَا يَسۡرِقۡنَ وَلَا يَزۡنِيۡنَ وَلَا  يَقۡتُلۡنَ اَوۡلَادَهُنَّ وَلَا يَاۡتِيۡنَ بِبُهۡتَانٍ يَّفۡتَرِيۡنَهٗ بَيۡنَ  اَيۡدِيۡهِنَّ وَاَرۡجُلِهِنَّ وَلَا يَعۡصِيۡنَكَ فِىۡ مَعۡرُوۡفٍ‌ فَبَايِعۡهُنَّ  وَاسۡتَغۡفِرۡ لَهُنَّ اللّٰهَ‌ؕ اِنَّ اللّٰهَ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5221": {"surat": 60, "ayat": 14, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا لَا تَتَوَلَّوۡا قَوۡمًا غَضِبَ اللّٰهُ عَلَيۡهِمۡ قَدۡ يَــٮِٕـسُوۡا مِنَ الۡاٰخِرَةِ كَمَا يَــٮِٕـسَ الۡكُفَّارُ مِنۡ اَصۡحٰبِ الۡقُبُوۡرِ‏"},
"5222": {"surat": 61, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5223": {"surat": 61, "ayat": 2, "teks": "سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الۡاَرۡضِ‌ۚ وَهُوَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5224": {"surat": 61, "ayat": 3, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا لِمَ تَقُوۡلُوۡنَ مَا لَا تَفۡعَلُوۡنَ‏"},
"5225": {"surat": 61, "ayat": 4, "teks": "كَبُرَ مَقۡتًا  عِنۡدَ اللّٰهِ اَنۡ تَقُوۡلُوۡا مَا لَا تَفۡعَلُوۡنَ‏"},
"5226": {"surat": 61, "ayat": 5, "teks": "اِنَّ اللّٰهَ يُحِبُّ الَّذِيۡنَ  يُقَاتِلُوۡنَ فِىۡ سَبِيۡلِهٖ صَفًّا كَاَنَّهُمۡ بُنۡيَانٌ مَّرۡصُوۡصٌ‏"},
"5227": {"surat": 61, "ayat": 6, "teks": "وَاِذۡ قَالَ مُوۡسٰى لِقَوۡمِهٖ يٰقَوۡمِ لِمَ تُؤۡذُوۡنَنِىۡ وَقَد تَّعۡلَمُوۡنَ اَنِّىۡ رَسُوۡلُ اللّٰهِ اِلَيۡكُمۡؕ فَلَمَّا زَاغُوۡۤا اَزَاغَ اللّٰهُ قُلُوۡبَهُمۡ‌ؕ وَاللّٰهُ لَا يَهۡدِى الۡقَوۡمَ الۡفٰسِقِيۡنَ‏"},
"5228": {"surat": 61, "ayat": 7, "teks": "وَاِذۡ قَالَ عِيۡسَى ابۡنُ مَرۡيَمَ يٰبَنِىۡۤ اِسۡرَآءِيۡلَ اِنِّىۡ رَسُوۡلُ اللّٰهِ اِلَيۡكُمۡ مُّصَدِّقًا لِّمَا بَيۡنَ يَدَىَّ مِنَ التَّوۡرٰٮةِ وَمُبَشِّرًۢا بِرَسُوۡلٍ يَّاۡتِىۡ مِنۡۢ بَعۡدِى اسۡمُهٗۤ اَحۡمَدُ‌ؕ فَلَمَّا جَآءَهُمۡ بِالۡبَيِّنٰتِ قَالُوۡا هٰذَا سِحۡرٌ مُّبِيۡنٌ‏"},
"5229": {"surat": 61, "ayat": 8, "teks": "وَمَنۡ اَظۡلَمُ مِمَّنِ افۡتَـرٰى عَلَى اللّٰهِ الۡكَذِبَ وَهُوَ يُدۡعٰٓى اِلَى الۡاِسۡلَامِ‌  ؕ وَاللّٰهُ لَا يَهۡدِى الۡقَوۡمَ الظّٰلِمِيۡنَ‏"},
"5230": {"surat": 61, "ayat": 9, "teks": "يُرِيۡدُوۡنَ لِيُطۡفِــُٔـوۡا نُوۡرَ اللّٰهِ بِاَ فۡوَاهِهِمْ وَاللّٰهُ مُتِمُّ نُوۡرِهٖ وَلَوۡ كَرِهَ الۡكٰفِرُوۡنَ‏"},
"5231": {"surat": 61, "ayat": 10, "teks": "هُوَ الَّذِىۡۤ اَرۡسَلَ رَسُوۡلَهٗ بِالۡهُدٰى وَدِيۡنِ الۡحَـقِّ لِيُظۡهِرَهٗ عَلَى الدِّيۡنِ  كُلِّهٖ وَلَوۡ كَرِهَ الۡمُشۡرِكُوۡنَ‏"},
"5232": {"surat": 61, "ayat": 11, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا هَلۡ اَدُلُّكُمۡ عَلٰى تِجَارَةٍ  تُنۡجِيۡكُمۡ مِّنۡ عَذَابٍ اَلِيۡمٍ‏"},
"5233": {"surat": 61, "ayat": 12, "teks": "تُؤۡمِنُوۡنَ بِاللّٰهِ وَرَسُوۡلِهٖ وَتُجَاهِدُوۡنَ  فِىۡ سَبِيۡلِ اللّٰهِ بِاَمۡوَالِكُمۡ وَاَنۡفُسِكُمۡ‌ؕ ذٰلِكُمۡ خَيۡرٌ لَّـكُمۡ اِنۡ كُنۡتُمۡ  تَعۡلَمُوۡنَۙ‏"},
"5234": {"surat": 61, "ayat": 13, "teks": "يَغۡفِرۡ لَـكُمۡ ذُنُوۡبَكُمۡ وَيُدۡخِلۡكُمۡ جَنّٰتٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا  الۡاَنۡهٰرُ وَمَسٰكِنَ طَيِّبَةً فِىۡ جَنّٰتِ عَدۡنٍ‌ؕ ذٰلِكَ الۡفَوۡزُ الۡعَظِيۡمُۙ‏"},
"5235": {"surat": 61, "ayat": 14, "teks": "وَاُخۡرٰى تُحِبُّوۡنَهَا‌  ؕ نَصۡرٌ مِّنَ اللّٰهِ وَفَـتۡحٌ قَرِيۡبٌ‌ ؕ وَبَشِّرِ الۡمُؤۡمِنِيۡنَ‏"},
"5236": {"surat": 61, "ayat": 15, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا كُوۡنُوۡۤا اَنۡصَارَ اللّٰهِ كَمَا قَالَ عِيۡسَى ابۡنُ مَرۡيَمَ لِلۡحَوٰارِيّٖنَ مَنۡ اَنۡصَارِىۡۤ اِلَى اللّٰهِ‌ؕ قَالَ الۡحَـوٰرِيُّوۡنَ نَحۡنُ اَنۡصَارُ اللّٰهِ‌ فَاٰمَنَتۡ طَّآٮِٕفَةٌ مِّنۡۢ بَنِىۡۤ اِسۡرَآءِيۡلَ وَكَفَرَتۡ طَّآٮِٕفَةٌ   ۚ فَاَيَّدۡنَا الَّذِيۡنَ اٰمَنُوۡا عَلٰى عَدُوِّهِمۡ فَاَصۡبَحُوۡا ظٰهِرِيۡنَ‏"},
"5237": {"surat": 62, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5238": {"surat": 62, "ayat": 2, "teks": "يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الۡاَرۡضِ الۡمَلِكِ الۡقُدُّوۡسِ الۡعَزِيۡزِ الۡحَكِيۡمِ‏"},
"5239": {"surat": 62, "ayat": 3, "teks": "هُوَ الَّذِىۡ بَعَثَ فِى الۡاُمِّيّٖنَ رَسُوۡلًا مِّنۡهُمۡ يَتۡلُوۡا عَلَيۡهِمۡ اٰيٰتِهٖ وَيُزَكِّيۡهِمۡ وَيُعَلِّمُهُمُ الۡكِتٰبَ وَالۡحِكۡمَةَ وَاِنۡ كَانُوۡا مِنۡ قَبۡلُ لَفِىۡ ضَلٰلٍ مُّبِيۡنٍۙ‏"},
"5240": {"surat": 62, "ayat": 4, "teks": "وَّاٰخَرِيۡنَ مِنۡهُمۡ لَمَّا يَلۡحَقُوۡا بِهِمۡ‌ؕ وَهُوَ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5241": {"surat": 62, "ayat": 5, "teks": "ذٰ لِكَ فَضۡلُ اللّٰهِ يُؤۡتِيۡهِ مَنۡ يَّشَآءُ‌  ؕ وَاللّٰهُ ذُو الۡفَضۡلِ الۡعَظِيۡمِ‏"},
"5242": {"surat": 62, "ayat": 6, "teks": "مَثَلُ الَّذِيۡنَ حُمِّلُوا التَّوۡرٰٮةَ ثُمَّ لَمۡ يَحۡمِلُوۡهَا كَمَثَلِ الۡحِمَارِ يَحۡمِلُ اَسۡفَارًا‌ ؕ بِئۡسَ مَثَلُ الۡقَوۡمِ الَّذِيۡنَ كَذَّبُوۡا بِاٰيٰتِ اللّٰهِ ‌ؕ وَاللّٰهُ لَا يَهۡدِى الۡقَوۡمَ الظّٰلِمِيۡنَ‏"},
"5243": {"surat": 62, "ayat": 7, "teks": "قُلۡ يٰۤاَيُّهَا الَّذِيۡنَ هَادُوۡۤا اِنۡ زَعَمۡتُمۡ اَنَّكُمۡ اَوۡلِيَآءُ لِلّٰهِ  مِنۡ دُوۡنِ النَّاسِ فَتَمَنَّوُا الۡمَوۡتَ اِنۡ كُنۡتُمۡ صٰدِقِيۡنَ‏"},
"5244": {"surat": 62, "ayat": 8, "teks": "وَلَا يَتَمَنَّوۡنَهٗۤ اَبَدًۢا بِمَا قَدَّمَتۡ اَيۡدِيۡهِمۡ‌ؕ وَاللّٰهُ عَلِيۡمٌۢ بِالظّٰلِمِيۡنَ‏"},
"5245": {"surat": 62, "ayat": 9, "teks": "قُلۡ اِنَّ الۡمَوۡتَ الَّذِىۡ تَفِرُّوۡنَ مِنۡهُ فَاِنَّهٗ مُلٰقِيۡكُمۡ‌ ثُمَّ تُرَدُّوۡنَ  اِلٰى عٰلِمِ الۡغَيۡبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمۡ بِمَا كُنۡتُمۡ تَعۡمَلُوۡنَ‏"},
"5246": {"surat": 62, "ayat": 10, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡۤا اِذَا نُوۡدِىَ لِلصَّلٰوةِ مِنۡ يَّوۡمِ الۡجُمُعَةِ فَاسۡعَوۡا اِلٰى ذِكۡرِ اللّٰهِ وَذَرُوا الۡبَيۡعَ‌  ؕ ذٰ لِكُمۡ خَيۡرٌ لَّـكُمۡ اِنۡ كُنۡتُمۡ تَعۡلَمُوۡنَ‏"},
"5247": {"surat": 62, "ayat": 11, "teks": "فَاِذَا قُضِيَتِ الصَّلٰوةُ فَانْتَشِرُوۡا فِى الۡاَرۡضِ وَابۡتَغُوۡا مِنۡ فَضۡلِ اللّٰهِ وَاذۡكُرُوا اللّٰهَ كَثِيۡرًا لَّعَلَّكُمۡ تُفۡلِحُوۡنَ‏"},
"5248": {"surat": 62, "ayat": 12, "teks": "وَاِذَا رَاَوۡا تِجَارَةً اَوۡ لَهۡوَا۟ اۨنْفَضُّوۡۤا اِلَيۡهَا وَتَرَكُوۡكَ قَآٮِٕمًا‌  ؕ قُلۡ مَا عِنۡدَ اللّٰهِ خَيۡرٌ مِّنَ اللَّهۡوِ وَمِنَ التِّجَارَةِ‌  ؕ وَاللّٰهُ خَيۡرُ الرّٰزِقِيۡنَ‏"},
"5249": {"surat": 63, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5250": {"surat": 63, "ayat": 2, "teks": "اِذَا جَآءَكَ الۡمُنٰفِقُوۡنَ قَالُوۡا نَشۡهَدُ اِنَّكَ لَرَسُوۡلُ اللّٰهِ  ‌ۘ وَاللّٰهُ يَعۡلَمُ اِنَّكَ لَرَسُوۡلُهٗ ؕ وَاللّٰهُ يَشۡهَدُ اِنَّ الۡمُنٰفِقِيۡنَ لَـكٰذِبُوۡنَ‌ ۚ‏"},
"5251": {"surat": 63, "ayat": 3, "teks": "اِتَّخَذُوۡۤا  اَيۡمَانَهُمۡ جُنَّةً فَصَدُّوۡا عَنۡ سَبِيۡلِ اللّٰهِ‌ؕ اِنَّهُمۡ سَآءَ مَا كَانُوۡا يَعۡمَلُوۡنَ‏"},
"5252": {"surat": 63, "ayat": 4, "teks": "ذٰلِكَ بِاَنَّهُمۡ اٰمَنُوۡا ثُمَّ كَفَرُوۡا فَطُبِعَ عَلٰى قُلُوۡبِهِمۡ فَهُمۡ لَا يَفۡقَهُوۡنَ‏"},
"5253": {"surat": 63, "ayat": 5, "teks": "وَاِذَا رَاَيۡتَهُمۡ تُعۡجِبُكَ اَجۡسَامُهُمۡ‌ ؕ وَاِنۡ يَّقُوۡلُوۡا تَسۡمَعۡ لِقَوۡلِهِمۡ‌ ؕ كَاَنَّهُمۡ خُشُبٌ مُّسَنَّدَةٌ    ‌ؕ يَحۡسَبُوۡنَ كُلَّ صَيۡحَةٍ عَلَيۡهِمۡ‌ ؕ هُمُ الۡعَدُوُّ فَاحۡذَرۡهُمۡ‌  ؕ قَاتَلَهُمُ اللّٰهُ‌ اَنّٰى يُـؤۡفَكُوۡنَ‏"},
"5254": {"surat": 63, "ayat": 6, "teks": "وَاِذَا قِيۡلَ لَهُمۡ تَعَالَوۡا يَسۡتَغۡفِرۡ لَـكُمۡ رَسُوۡلُ  اللّٰهِ لَـوَّوۡا رُءُوۡسَهُمۡ وَرَاَيۡتَهُمۡ يَصُدُّوۡنَ وَهُمۡ مُّسۡتَكۡبِرُوۡنَ‏"},
"5255": {"surat": 63, "ayat": 7, "teks": "سَوَآءٌ عَلَيۡهِمۡ اَسۡتَغۡفَرۡتَ لَهُمۡ اَمۡ لَمۡ تَسۡتَغۡفِرۡ لَهُمۡؕ لَنۡ يَّغۡفِرَ اللّٰهُ لَهُمۡ‌ؕ اِنَّ اللّٰهَ لَا يَهۡدِى الۡقَوۡمَ الۡفٰسِقِيۡنَ‏"},
"5256": {"surat": 63, "ayat": 8, "teks": "هُمُ الَّذِيۡنَ يَقُوۡلُوۡنَ لَا تُنۡفِقُوۡا  عَلٰى مَنۡ عِنۡدَ رَسُوۡلِ اللّٰهِ حَتّٰى يَنۡفَضُّوۡا‌ؕ وَلِلّٰهِ خَزَآٮِٕنُ السَّمٰوٰتِ  وَالۡاَرۡضِ وَلٰـكِنَّ الۡمُنٰفِقِيۡنَ لَا يَفۡقَهُوۡنَ‏"},
"5257": {"surat": 63, "ayat": 9, "teks": "يَقُوۡلُوۡنَ لَٮِٕنۡ رَّجَعۡنَاۤ  اِلَى الۡمَدِيۡنَةِ لَيُخۡرِجَنَّ الۡاَعَزُّ مِنۡهَا الۡاَذَلَّ‌ؕ وَلِلّٰهِ الۡعِزَّةُ وَلِرَسُوۡلِهٖ  وَلِلۡمُؤۡمِنِيۡنَ وَلٰـكِنَّ الۡمُنٰفِقِيۡنَ لَا يَعۡلَمُوۡنَ‏"},
"5258": {"surat": 63, "ayat": 10, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا  لَا تُلۡهِكُمۡ اَمۡوَالُكُمۡ وَلَاۤ اَوۡلَادُكُمۡ عَنۡ ذِكۡرِ اللّٰهِ‌ۚ وَمَنۡ يَّفۡعَلۡ ذٰلِكَ  فَاُولٰٓٮِٕكَ هُمُ الۡخٰسِرُوۡنَ‏"},
"5259": {"surat": 63, "ayat": 11, "teks": "وَاَنۡفِقُوۡا مِنۡ مَّا رَزَقۡنٰكُمۡ مِّنۡ قَبۡلِ اَنۡ  يَّاۡتِىَ اَحَدَكُمُ الۡمَوۡتُ فَيَقُوۡلَ رَبِّ لَوۡلَاۤ اَخَّرۡتَنِىۡۤ اِلٰٓى اَجَلٍ  قَرِيۡبٍۙ فَاَصَّدَّقَ وَاَكُنۡ مِّنَ الصّٰلِحِيۡنَ‏"},
"5260": {"surat": 63, "ayat": 12, "teks": "وَلَنۡ يُّؤَخِّرَ اللّٰهُ  نَفۡسًا اِذَا جَآءَ اَجَلُهَا‌ؕ وَاللّٰهُ خَبِيۡرٌۢ بِمَا تَعۡمَلُوۡنَ‏"},
"5261": {"surat": 64, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5262": {"surat": 64, "ayat": 2, "teks": "يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الۡاَرۡضِ‌ۚ لَهُ الۡمُلۡكُ وَلَهُ الۡحَمۡدُ‌ وَهُوَ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرٌ‏"},
"5263": {"surat": 64, "ayat": 3, "teks": "هُوَ الَّذِىۡ خَلَقَكُمۡ  فَمِنۡكُمۡ كَافِرٌ وَّمِنۡكُمۡ مُّؤۡمِنٌ‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ بَصِيۡرٌ‏"},
"5264": {"surat": 64, "ayat": 4, "teks": "خَلَقَ السَّمٰوٰتِ وَالۡاَرۡضَ بِالۡحَـقِّ وَصَوَّرَكُمۡ‌ فَاَحۡسَنَ صُوَرَكُمۡۚ‌ وَاِلَيۡهِ الۡمَصِيۡرُ‏"},
"5265": {"surat": 64, "ayat": 5, "teks": "يَعۡلَمُ مَا فِى السَّمٰوٰتِ وَالۡاَرۡضِ وَيَعۡلَمُ مَا تُسِرُّوۡنَ وَمَا تُعۡلِنُوۡنَ‌ؕ وَاللّٰهُ عَلِيۡمٌۢ بِذَاتِ الصُّدُوۡرِ‏"},
"5266": {"surat": 64, "ayat": 6, "teks": "اَلَمۡ يَاۡتِكُمۡ نَبَـؤُا الَّذِيۡنَ كَفَرُوۡا مِنۡ قَبۡلُ فَذَاقُوۡا وَبَالَ اَمۡرِهِمۡ  وَلَهُمۡ عَذَابٌ اَلِيۡمٌ‏"},
"5267": {"surat": 64, "ayat": 7, "teks": "ذٰ لِكَ بِاَنَّهٗ كَانَتۡ تَّاۡتِيۡهِمۡ رُسُلُهُمۡ بِالۡبَيِّنٰتِ فَقَالُوۡۤا اَبَشَرٌ يَّهۡدُوۡنَـنَا فَكَفَرُوۡا وَتَوَلَّوْا‌ وَّاسۡتَغۡنَى اللّٰهُ‌  ؕ وَاللّٰهُ غَنِىٌّ حَمِيۡدٌ‏"},
"5268": {"surat": 64, "ayat": 8, "teks": "زَعَمَ الَّذِيۡنَ كَفَرُوۡۤا اَنۡ لَّنۡ يُّبۡـعَـثُـوۡا‌  ؕ قُلۡ بَلٰى وَرَبِّىۡ لَـتُبۡـعَـثُـنَّ ثُمَّ لَـتُنَـبَّـؤُنَّ بِمَا عَمِلۡـتُمۡ‌ؕ وَذٰ لِكَ عَلَى اللّٰهِ يَسِيۡرٌ‏"},
"5269": {"surat": 64, "ayat": 9, "teks": "فَاٰمِنُوۡا بِاللّٰهِ وَرَسُوۡلِهٖ  وَالنُّوۡرِ الَّذِىۡۤ اَنۡزَلۡنَا‌ؕ وَاللّٰهُ بِمَا تَعۡمَلُوۡنَ خَبِيۡرٌ‏"},
"5270": {"surat": 64, "ayat": 10, "teks": "يَوۡمَ يَجۡمَعُكُمۡ لِيَوۡمِ الۡجَمۡعِ‌ ذٰ لِكَ يَوۡمُ التَّغَابُنِ‌  ؕ وَمَنۡ يُّؤۡمِنۡۢ بِاللّٰهِ وَيَعۡمَلۡ صَالِحًـا يُّكَفِّرۡ عَنۡهُ سَيِّاٰتِهٖ وَيُدۡخِلۡهُ جَنّٰتٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ خٰلِدِيۡنَ فِيۡهَاۤ اَبَدًا‌  ؕ ذٰ لِكَ الۡفَوۡزُ الۡعَظِیْمُ‏"},
"5271": {"surat": 64, "ayat": 11, "teks": "وَالَّذِيۡنَ كَفَرُوۡا وَكَذَّبُوۡا بِاٰيٰتِنَاۤ اُولٰٓٮِٕكَ اَصۡحٰبُ النَّارِ خٰلِدِيۡنَ فِيۡهَا‌  ؕ وَبِئۡسَ الۡمَصِيۡرُ‏"},
"5272": {"surat": 64, "ayat": 12, "teks": "مَاۤ اَصَابَ مِنۡ مُّصِيۡبَةٍ اِلَّا بِاِذۡنِ اللّٰهِ‌ؕ وَمَنۡ  يُّؤۡمِنۡۢ بِاللّٰهِ يَهۡدِ قَلۡبَهٗ‌ؕ وَاللّٰهُ بِكُلِّ شَىۡءٍ عَلِيۡمٌ‏"},
"5273": {"surat": 64, "ayat": 13, "teks": "وَاَطِيۡعُوا اللّٰهَ وَاَطِيۡعُوا الرَّسُوۡلَ‌ۚ فَاِنۡ تَوَلَّيۡتُمۡ فَاِنَّمَا عَلٰى رَسُوۡلِنَا الۡبَلٰغُ الۡمُبِيۡنُ‏"},
"5274": {"surat": 64, "ayat": 14, "teks": "اَللّٰهُ لَاۤ اِلٰهَ اِلَّا هُوَ‌ؕ وَعَلَى اللّٰهِ فَلۡيَتَوَكَّلِ الۡمُؤۡمِنُوۡنَ‏"},
"5275": {"surat": 64, "ayat": 15, "teks": "‌يٰۤاَيُّهَا الَّذِيۡنَ  اٰمَنُوۡۤا اِنَّ مِنۡ اَزۡوَاجِكُمۡ وَاَوۡلَادِكُمۡ عَدُوًّا لَّكُمۡ فَاحۡذَرُوۡهُمۡ‌ۚ  وَاِنۡ تَعۡفُوۡا وَتَصۡفَحُوۡا وَتَغۡفِرُوۡا فَاِنَّ اللّٰهَ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5276": {"surat": 64, "ayat": 16, "teks": "اِنَّمَاۤ  اَمۡوَالُـكُمۡ وَاَوۡلَادُكُمۡ فِتۡنَةٌ‌ؕ وَاللّٰهُ عِنۡدَهٗۤ اَجۡرٌ عَظِيۡمٌ‏"},
"5277": {"surat": 64, "ayat": 17, "teks": "فَاتَّقُوا  اللّٰهَ مَا اسۡتَطَعۡتُمۡ وَاسۡمَعُوۡا وَاَطِيۡعُوۡا وَاَنۡفِقُوۡا خَيۡرًا لِّاَنۡفُسِكُمۡ‌ؕ  وَمَنۡ يُّوۡقَ شُحَّ نَفۡسِهٖ فَاُولٰٓٮِٕكَ هُمُ الۡمُفۡلِحُوۡنَ‏"},
"5278": {"surat": 64, "ayat": 18, "teks": "اِنۡ  تُقۡرِضُوا اللّٰهَ قَرۡضًا حَسَنًا يُّضٰعِفۡهُ لَـكُمۡ وَيَغۡفِرۡ لَـكُمۡ‌ؕ وَاللّٰهُ  شَكُوۡرٌ حَلِيۡمٌۙ‏"},
"5279": {"surat": 64, "ayat": 19, "teks": "عٰلِمُ الۡغَيۡبِ وَالشَّهَادَةِ الۡعَزِيۡزُ الۡحَكِيۡمُ‏"},
"5280": {"surat": 65, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5281": {"surat": 65, "ayat": 2, "teks": "يٰۤاَيُّهَا النَّبِىُّ اِذَا طَلَّقۡتُمُ النِّسَآءَ فَطَلِّقُوۡهُنَّ لِعِدَّتِهِنَّ وَاَحۡصُوا الۡعِدَّةَ  ‌ ۚ وَاتَّقُوا اللّٰهَ رَبَّكُمۡ‌ ۚ لَا تُخۡرِجُوۡهُنَّ مِنۡۢ بُيُوۡتِهِنَّ وَلَا يَخۡرُجۡنَ اِلَّاۤ اَنۡ يَّاۡتِيۡنَ بِفَاحِشَةٍ مُّبَيِّنَةٍ‌  ؕ وَتِلۡكَ حُدُوۡدُ اللّٰهِ‌ ؕ وَمَنۡ يَّتَعَدَّ حُدُوۡدَ اللّٰهِ فَقَدۡ ظَلَمَ نَفۡسَهٗ‌  ؕ لَا تَدۡرِىۡ لَعَلَّ اللّٰهَ يُحۡدِثُ بَعۡدَ ذٰ لِكَ اَمۡرًا‏"},
"5282": {"surat": 65, "ayat": 3, "teks": "فَاِذَا بَلَغۡنَ اَجَلَهُنَّ فَاَمۡسِكُوۡهُنَّ بِمَعۡرُوۡفٍ اَوۡ فَارِقُوۡهُنَّ بِمَعۡرُوۡفٍ وَّاَشۡهِدُوۡا ذَوَىۡ عَدۡلٍ مِّنۡكُمۡ وَاَقِيۡمُوا الشَّهَادَةَ لِلّٰهِ‌ ؕ ذٰ لِكُمۡ يُوۡعَظُ بِهٖ مَنۡ كَانَ يُؤۡمِنُ بِاللّٰهِ وَالۡيَوۡمِ الۡاٰخِرِ‌ ۙ وَمَنۡ يَّـتَّـقِ اللّٰهَ يَجۡعَلْ لَّهٗ مَخۡرَجًا ۙ‏"},
"5283": {"surat": 65, "ayat": 4, "teks": "وَّيَرۡزُقۡهُ مِنۡ حَيۡثُ لَا يَحۡتَسِبُ‌  ؕ وَمَنۡ يَّتَوَكَّلۡ عَلَى اللّٰهِ فَهُوَ حَسۡبُهٗ  ؕ اِنَّ اللّٰهَ بَالِغُ اَمۡرِهٖ‌  ؕ قَدۡ جَعَلَ اللّٰهُ لِكُلِّ شَىۡءٍ قَدۡرًا‏"},
"5284": {"surat": 65, "ayat": 5, "teks": "وَالّٰٓـىٴِۡ يَٮِٕسۡنَ مِنَ الۡمَحِيۡضِ مِنۡ نِّسَآٮِٕكُمۡ اِنِ ارۡتَبۡتُمۡ فَعِدَّتُهُنَّ ثَلٰثَةُ اَشۡهُرٍ وَّالّٰٓـىٴِۡ لَمۡ يَحِضۡنَ‌  ؕ وَاُولَاتُ الۡاَحۡمَالِ اَجَلُهُنَّ اَنۡ يَّضَعۡنَ حَمۡلَهُنَّ  ‌ؕ وَمَنۡ يَّـتَّـقِ اللّٰهَ يَجۡعَلْ لَّهٗ مِنۡ اَمۡرِهٖ یُسْرًا‏"},
"5285": {"surat": 65, "ayat": 6, "teks": "ذٰ لِكَ اَمۡرُ اللّٰهِ اَنۡزَلَهٗۤ اِلَيۡكُمۡ‌  ؕ وَمَنۡ يَّـتَّـقِ اللّٰهَ يُكَفِّرۡ عَنۡهُ سَيِّاٰتِهٖ وَيُعۡظِمۡ لَهٗۤ اَجۡرًا‏"},
"5286": {"surat": 65, "ayat": 7, "teks": "اَسۡكِنُوۡهُنَّ مِنۡ حَيۡثُ سَكَنۡـتُمۡ مِّنۡ وُّجۡدِكُمۡ وَلَا تُضَآرُّوۡهُنَّ لِتُضَيِّقُوۡا عَلَيۡهِنَّ‌  ؕ وَاِنۡ كُنَّ اُولَاتِ حَمۡلٍ فَاَنۡفِقُوا عَلَيۡهِنَّ حَتّٰى يَضَعۡنَ حَمۡلَهُنَّ‌‌  ۚ فَاِنۡ اَرۡضَعۡنَ لَـكُمۡ فَاٰ تُوۡهُنَّ اُجُوۡرَهُنَّ‌  ۚ وَاۡتَمِرُوۡا بَيۡنَكُمۡ بِمَعۡرُوۡفٍ‌ۚ وَاِنۡ تَعَاسَرۡتُمۡ فَسَتُرۡضِعُ لَهٗۤ اُخۡرٰى ؕ‏"},
"5287": {"surat": 65, "ayat": 8, "teks": "لِيُنۡفِقۡ ذُوۡ سَعَةٍ مِّنۡ سَعَتِهٖ‌ؕ وَمَنۡ  قُدِرَ عَلَيۡهِ رِزۡقُهٗ فَلۡيُنۡفِقۡ مِمَّاۤ اٰتٰٮهُ اللّٰهُ‌ؕ لَا يُكَلِّفُ اللّٰهُ نَفۡسًا اِلَّا مَاۤ اٰتٰٮهَا‌ؕ  سَيَجۡعَلُ اللّٰهُ بَعۡدَ عُسۡرٍ يُّسۡرًا‏"},
"5288": {"surat": 65, "ayat": 9, "teks": "وَكَاَيِّنۡ مِّنۡ قَرۡيَةٍ عَتَتۡ عَنۡ اَمۡرِ  رَبِّهَا وَرُسُلِهٖ فَحَاسَبۡنٰهَا حِسَابًا شَدِيۡدًاۙ وَّعَذَّبۡنٰهَا عَذَابًا نُّكۡرًا‏"},
"5289": {"surat": 65, "ayat": 10, "teks": "فَذَاقَتۡ وَبَالَ اَمۡرِهَا وَكَانَ عَاقِبَةُ اَمۡرِهَا خُسۡرًا‏"},
"5290": {"surat": 65, "ayat": 11, "teks": "اَعَدَّ اللّٰهُ لَهُمۡ عَذَابًا شَدِيۡدًا‌  ۙ فَاتَّقُوا اللّٰهَ يٰۤاُولِى الۡاَ لۡبَابِ   ۖۚ ۛ الَّذِيۡنَ اٰمَنُوۡا  ۛؕ قَدۡ اَنۡزَلَ اللّٰهُ اِلَيۡكُمۡ ذِكۡرًا ۙ‏"},
"5291": {"surat": 65, "ayat": 12, "teks": "رَّسُوۡلًا يَّتۡلُوۡا عَلَيۡكُمۡ اٰيٰتِ اللّٰهِ مُبَيِّنٰتٍ لِّيُخۡرِجَ الَّذِيۡنَ اٰمَنُوۡا وَعَمِلُوا الصّٰلِحٰتِ مِنَ الظُّلُمٰتِ اِلَى النُّوۡرِ‌ؕ وَمَنۡ يُّؤۡمِنۡۢ بِاللّٰهِ وَيَعۡمَلۡ صَالِحًـا يُّدۡخِلۡهُ جَنّٰتٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ خٰلِدِيۡنَ فِيۡهَاۤ اَبَدًا‌ؕ قَدۡ اَحۡسَنَ اللّٰهُ لَهٗ رِزۡقًا‏"},
"5292": {"surat": 65, "ayat": 13, "teks": "اَللّٰهُ الَّذِىۡ خَلَقَ سَبۡعَ سَمٰوٰتٍ وَّمِنَ الۡاَرۡضِ مِثۡلَهُنَّ  ؕ يَتَنَزَّلُ الۡاَمۡرُ بَيۡنَهُنَّ لِتَعۡلَمُوۡۤا اَنَّ اللّٰهَ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرٌ ۙ وَّاَنَّ اللّٰهَ قَدۡ اَحَاطَ بِكُلِّ شَىۡءٍ عِلۡمًا‏"},
"5293": {"surat": 66, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5294": {"surat": 66, "ayat": 2, "teks": "يٰۤاَيُّهَا النَّبِىُّ لِمَ تُحَرِّمُ مَاۤ اَحَلَّ اللّٰهُ لَـكَ‌ۚ تَبۡتَغِىۡ مَرۡضَاتَ  اَزۡوَاجِكَ‌ؕ وَاللّٰهُ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5295": {"surat": 66, "ayat": 3, "teks": "قَدۡ فَرَضَ اللّٰهُ لَـكُمۡ تَحِلَّةَ  اَيۡمَانِكُمۡ‌ؕ وَاللّٰهُ مَوۡلٰٮكُمۡ‌ۚ وَهُوَ الۡعَلِيۡمُ الۡحَكِيۡمُ‏"},
"5296": {"surat": 66, "ayat": 4, "teks": "وَاِذۡ اَسَرَّ  النَّبِىُّ اِلٰى بَعۡضِ اَزۡوَاجِهٖ حَدِيۡثًا‌ۚ فَلَمَّا نَـبَّاَتۡ بِهٖ وَاَظۡهَرَهُ  اللّٰهُ عَلَيۡهِ عَرَّفَ بَعۡضَهٗ وَاَعۡرَضَ عَنۡۢ بَعۡضٍ‌ۚ فَلَمَّا نَـبَّاَهَا بِهٖ  قَالَتۡ مَنۡ اَنۡۢبَاَكَ هٰذَا‌ؕ قَالَ نَـبَّاَنِىَ الۡعَلِيۡمُ الۡخَبِیْرُ‏"},
"5297": {"surat": 66, "ayat": 5, "teks": "اِنۡ تَتُوۡبَاۤ  اِلَى اللّٰهِ فَقَدۡ صَغَتۡ قُلُوۡبُكُمَا‌ۚ وَاِنۡ تَظٰهَرَا عَلَيۡهِ فَاِنَّ اللّٰهَ  هُوَ مَوۡلٰٮهُ وَجِبۡرِيۡلُ وَصَالِحُ الۡمُؤۡمِنِيۡنَ‌ۚ وَالۡمَلٰٓٮِٕكَةُ بَعۡدَ ذٰلِكَ  ظَهِيۡرٌ‏"},
"5298": {"surat": 66, "ayat": 6, "teks": "عَسٰى رَبُّهٗۤ اِنۡ طَلَّقَكُنَّ اَنۡ يُّبۡدِلَهٗۤ اَزۡوَاجًا خَيۡرًا  مِّنۡكُنَّ مُسۡلِمٰتٍ مُّؤۡمِنٰتٍ قٰنِتٰتٍ تٰٓٮِٕبٰتٍ عٰبِدٰتٍ سٰٓٮِٕحٰتٍ  ثَيِّبٰتٍ وَّاَبۡكَارًا‏"},
"5299": {"surat": 66, "ayat": 7, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا قُوۡۤا اَنۡفُسَكُمۡ وَاَهۡلِيۡكُمۡ  نَارًا وَّقُوۡدُهَا النَّاسُ وَالۡحِجَارَةُ عَلَيۡهَا مَلٰٓٮِٕكَةٌ غِلَاظٌ شِدَادٌ  لَّا يَعۡصُوۡنَ اللّٰهَ مَاۤ اَمَرَهُمۡ وَيَفۡعَلُوۡنَ مَا يُؤۡمَرُوۡنَ‏"},
"5300": {"surat": 66, "ayat": 8, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ كَفَرُوۡا لَا تَعۡتَذِرُوا الۡيَوۡمَ‌ؕ اِنَّمَا تُجۡزَوۡنَ مَا كُنۡتُمۡ تَعۡمَلُوۡنَ‏"},
"5301": {"surat": 66, "ayat": 9, "teks": "يٰۤاَيُّهَا الَّذِيۡنَ اٰمَنُوۡا تُوۡبُوۡۤا اِلَى اللّٰهِ تَوۡبَةً نَّصُوۡحًا  ؕ عَسٰى رَبُّكُمۡ اَنۡ يُّكَفِّرَ عَنۡكُمۡ سَيِّاٰتِكُمۡ وَيُدۡخِلَـكُمۡ جَنّٰتٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ ۙ يَوۡمَ لَا يُخۡزِى اللّٰهُ النَّبِىَّ وَالَّذِيۡنَ اٰمَنُوۡا مَعَهٗ‌  ۚ نُوۡرُهُمۡ يَسۡعٰى بَيۡنَ اَيۡدِيۡهِمۡ وَبِاَيۡمَانِهِمۡ يَقُوۡلُوۡنَ رَبَّنَاۤ اَ تۡمِمۡ لَـنَا نُوۡرَنَا وَاغۡفِرۡ لَـنَا‌  ۚ اِنَّكَ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرٌ‏"},
"5302": {"surat": 66, "ayat": 10, "teks": "يٰۤاَيُّهَا النَّبِىُّ جَاهِدِ  الۡكُفَّارَ وَالۡمُنٰفِقِيۡنَ وَاغۡلُظۡ عَلَيۡهِمۡ‌ؕ وَمَاۡوٰٮهُمۡ جَهَنَّمُ‌ؕ وَبِئۡسَ  الۡمَصِيۡرُ‏"},
"5303": {"surat": 66, "ayat": 11, "teks": "ضَرَبَ اللّٰهُ مَثَلًا لِّـلَّذِيۡنَ كَفَرُوا امۡرَاَتَ نُوۡحٍ وَّ امۡرَاَتَ لُوۡطٍ‌  ؕ كَانَـتَا تَحۡتَ عَبۡدَيۡنِ مِنۡ عِبَادِنَا صَالِحَـيۡنِ فَخَانَتٰهُمَا فَلَمۡ يُغۡنِيَا عَنۡهُمَا مِنَ اللّٰهِ شَيۡــًٔا وَّقِيۡلَ ادۡخُلَا النَّارَ مَعَ الدّٰخِلِيۡنَ‏"},
"5304": {"surat": 66, "ayat": 12, "teks": "وَضَرَبَ اللّٰهُ مَثَلًا لِّـلَّذِيۡنَ اٰمَنُوۡا امۡرَاَتَ فِرۡعَوۡنَ‌ۘ اِذۡ قَالَتۡ رَبِّ ابۡنِ لِىۡ عِنۡدَكَ بَيۡتًا فِى الۡجَـنَّةِ وَنَجِّنِىۡ مِنۡ فِرۡعَوۡنَ وَعَمَلِهٖ وَنَجِّنِىۡ مِنَ الۡقَوۡمِ الظّٰلِمِيۡنَۙ‏"},
"5305": {"surat": 66, "ayat": 13, "teks": "وَمَرۡيَمَ  ابۡنَتَ عِمۡرٰنَ الَّتِىۡۤ اَحۡصَنَتۡ فَرۡجَهَا فَنَفَخۡنَا فِيۡهِ مِنۡ رُّوۡحِنَا  وَصَدَّقَتۡ بِكَلِمٰتِ رَبِّهَا وَكُتُبِهٖ وَكَانَتۡ مِنَ الۡقٰنِتِيۡنَ‏"},
"5306": {"surat": 67, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5307": {"surat": 67, "ayat": 2, "teks": "تَبٰرَكَ الَّذِىۡ بِيَدِهِ الۡمُلۡكُ وَهُوَ عَلٰى كُلِّ شَىۡءٍ قَدِيۡرُۙ‏"},
"5308": {"surat": 67, "ayat": 3, "teks": "اۨلَّذِىۡ خَلَقَ الۡمَوۡتَ وَالۡحَيٰوةَ لِيَبۡلُوَكُمۡ اَيُّكُمۡ اَحۡسَنُ  عَمَلًاؕ وَهُوَ الۡعَزِيۡزُ الۡغَفُوۡرُۙ‏"},
"5309": {"surat": 67, "ayat": 4, "teks": "الَّذِىۡ خَلَقَ سَبۡعَ سَمٰوٰتٍ طِبَاقًا‌ ؕ مَا تَرٰى فِىۡ خَلۡقِ الرَّحۡمٰنِ مِنۡ تَفٰوُتٍ‌ ؕ فَارۡجِعِ الۡبَصَرَۙ هَلۡ تَرٰى مِنۡ فُطُوۡرٍ‏"},
"5310": {"surat": 67, "ayat": 5, "teks": "ثُمَّ ارۡجِعِ الۡبَصَرَ كَرَّتَيۡنِ يَنۡقَلِبۡ اِلَيۡكَ  الۡبَصَرُ خَاسِئًا وَّهُوَ حَسِيۡرٌ‏"},
"5311": {"surat": 67, "ayat": 6, "teks": "وَلَـقَدۡ زَيَّـنَّا السَّمَآءَ الدُّنۡيَا  بِمَصَابِيۡحَ وَجَعَلۡنٰهَا رُجُوۡمًا لِّلشَّيٰطِيۡنِ‌ وَاَعۡتَدۡنَا لَهُمۡ  عَذَابَ السَّعِيۡرِ‏"},
"5312": {"surat": 67, "ayat": 7, "teks": "وَلِلَّذِيۡنَ كَفَرُوۡا بِرَبِّهِمۡ عَذَابُ جَهَنَّمَ‌ؕ  وَبِئۡسَ الۡمَصِيۡرُ‏"},
"5313": {"surat": 67, "ayat": 8, "teks": "اِذَاۤ اُلۡقُوۡا فِيۡهَا سَمِعُوۡا لَهَا شَهِيۡقًا وَّهِىَ  تَفُوۡرُۙ‏"},
"5314": {"surat": 67, "ayat": 9, "teks": "تَكَادُ تَمَيَّزُ مِنَ الۡغَيۡظِ‌ؕ كُلَّمَاۤ اُلۡقِىَ فِيۡهَا فَوۡجٌ سَاَلَهُمۡ  خَزَنَـتُهَاۤ اَلَمۡ يَاۡتِكُمۡ نَذِيۡرٌ‏"},
"5315": {"surat": 67, "ayat": 10, "teks": "قَالُوۡا بَلٰى قَدۡ جَآءَنَا نَذِيۡرٌ  ۙ فَكَذَّبۡنَا وَقُلۡنَا مَا نَزَّلَ اللّٰهُ مِنۡ شَىۡءٍ   ۖۚ اِنۡ اَنۡتُمۡ اِلَّا فِىۡ ضَلٰلٍ كَبِيۡرٍ‏"},
"5316": {"surat": 67, "ayat": 11, "teks": "وَقَالُوۡا لَوۡ كُنَّا نَسۡمَعُ اَوۡ نَعۡقِلُ مَا كُنَّا فِىۡۤ اَصۡحٰبِ السَّعِيۡرِ‏"},
"5317": {"surat": 67, "ayat": 12, "teks": "فَاعۡتَرَفُوۡا بِذَنۡۢبِهِمۡ‌ۚ فَسُحۡقًا لِّاَصۡحٰبِ السَّعِيۡرِ‏"},
"5318": {"surat": 67, "ayat": 13, "teks": "اِنَّ الَّذِيۡنَ  يَخۡشَوۡنَ رَبَّهُمۡ بِالۡغَيۡبِ لَهُمۡ مَّغۡفِرَةٌ وَّاَجۡرٌ كَبِيۡرٌ‏"},
"5319": {"surat": 67, "ayat": 14, "teks": "وَاَسِرُّوۡا  قَوۡلَـكُمۡ اَوِ اجۡهَرُوۡا بِهٖؕ اِنَّهٗ عَلِيۡمٌۢ بِذَاتِ الصُّدُوۡرِ‏"},
"5320": {"surat": 67, "ayat": 15, "teks": "اَلَا يَعۡلَمُ  مَنۡ خَلَقَؕ وَهُوَ اللَّطِيۡفُ الۡخَبِيۡرُ‏"},
"5321": {"surat": 67, "ayat": 16, "teks": "هُوَ الَّذِىۡ جَعَلَ لَـكُمُ الۡاَرۡضَ ذَلُوۡلًا فَامۡشُوۡا فِىۡ مَنَاكِبِهَا وَكُلُوۡا مِنۡ رِّزۡقِهٖ‌ؕ وَاِلَيۡهِ النُّشُوۡرُ‏"},
"5322": {"surat": 67, "ayat": 17, "teks": "ءَاَمِنۡتُمۡ مَّنۡ فِىۡ السَّمَآءِ اَنۡ يَّخۡسِفَ بِكُمُ  الۡاَرۡضَ فَاِذَا هِىَ تَمُوۡرُۙ‏"},
"5323": {"surat": 67, "ayat": 18, "teks": "اَمۡ اَمِنۡتُمۡ مَّنۡ فِى السَّمَآءِ اَنۡ يُّرۡسِلَ عَلَيۡكُمۡ حَاصِبًا‌ ؕ فَسَتَعۡلَمُوۡنَ كَيۡفَ نَذِيۡرِ‏"},
"5324": {"surat": 67, "ayat": 19, "teks": "وَلَـقَدۡ  كَذَّبَ الَّذِيۡنَ مِنۡ قَبۡلِهِمۡ فَكَيۡفَ كَانَ نَكِيۡرِ‏"},
"5325": {"surat": 67, "ayat": 20, "teks": "اَوَلَمۡ يَرَوۡا  اِلَى الطَّيۡرِ فَوۡقَهُمۡ صٰٓفّٰتٍ وَّيَقۡبِضۡنَؕؔۘ مَا يُمۡسِكُهُنَّ اِلَّا الرَّحۡمٰنُ‌ؕ  اِنَّهٗ بِكُلِّ شَىۡءٍۢ بَصِيۡرٌ‏"},
"5326": {"surat": 67, "ayat": 21, "teks": "اَمَّنۡ هٰذَا الَّذِىۡ هُوَ جُنۡدٌ لَّكُمۡ  يَنۡصُرُكُمۡ مِّنۡ دُوۡنِ الرَّحۡمٰنِ‌ؕ اِنِ الۡكٰفِرُوۡنَ اِلَّا فِىۡ غُرُوۡرٍ‌ۚ‏"},
"5327": {"surat": 67, "ayat": 22, "teks": "اَمَّنۡ هٰذَا الَّذِىۡ يَرۡزُقُكُمۡ اِنۡ اَمۡسَكَ رِزۡقَهٗ‌ ۚ بَلۡ لَّجُّوۡا فِىۡ عُتُوٍّ وَّنُفُوۡرٍ‏"},
"5328": {"surat": 67, "ayat": 23, "teks": "اَفَمَنۡ يَّمۡشِىۡ مُكِبًّا عَلٰى وَجۡهِهٖۤ اَهۡدٰٓى اَمَّنۡ يَّمۡشِىۡ سَوِيًّا عَلٰى صِرَاطٍ مُّسۡتَقِيۡمٍ‏"},
"5329": {"surat": 67, "ayat": 24, "teks": "قُلۡ هُوَ الَّذِىۡۤ اَنۡشَاَكُمۡ وَجَعَلَ لَـكُمُ السَّمۡعَ وَالۡاَبۡصَارَ وَ الۡاَفۡـــِٕدَةَ  ‌ ؕ قَلِيۡلًا مَّا تَشۡكُرُوۡنَ‏"},
"5330": {"surat": 67, "ayat": 25, "teks": "قُلۡ هُوَ الَّذِىۡ ذَرَاَكُمۡ فِى الۡاَرۡضِ وَاِلَيۡهِ تُحۡشَرُوۡنَ‏"},
"5331": {"surat": 67, "ayat": 26, "teks": "وَيَقُوۡلُوۡنَ مَتٰى هٰذَا الۡوَعۡدُ  اِنۡ كُنۡتُمۡ صٰدِقِيۡنَ‏"},
"5332": {"surat": 67, "ayat": 27, "teks": "قُلۡ اِنَّمَا الۡعِلۡمُ عِنۡدَ اللّٰهِ وَاِنَّمَاۤ اَنَا نَذِيۡرٌ مُّبِيۡنٌ‏"},
"5333": {"surat": 67, "ayat": 28, "teks": "فَلَمَّا رَاَوۡهُ زُلۡفَةً سِیْٓــَٔتۡ وُجُوۡهُ الَّذِيۡنَ  كَفَرُوۡا وَقِيۡلَ هٰذَا الَّذِىۡ كُنۡتُمۡ بِهٖ تَدَّعُوۡنَ‏"},
"5334": {"surat": 67, "ayat": 29, "teks": "قُلۡ اَرَءَيۡتُمۡ  اِنۡ اَهۡلَـكَنِىَ اللّٰهُ وَمَنۡ مَّعِىَ اَوۡ رَحِمَنَاۙ فَمَنۡ يُّجِيۡرُ الۡكٰفِرِيۡنَ  مِنۡ عَذَابٍ اَلِيۡمٍ‏"},
"5335": {"surat": 67, "ayat": 30, "teks": "قُلۡ هُوَ الرَّحۡمٰنُ اٰمَنَّا بِهٖ وَعَلَيۡهِ  تَوَكَّلۡنَا‌ۚ فَسَتَعۡلَمُوۡنَ مَنۡ هُوَ فِىۡ ضَلٰلٍ مُّبِيۡنٍ‏"},
"5336": {"surat": 67, "ayat": 31, "teks": "قُلۡ  اَرَءَيۡتُمۡ اِنۡ اَصۡبَحَ مَآؤُكُمۡ غَوۡرًا فَمَنۡ يَّاۡتِيۡكُمۡ بِمَآءٍ مَّعِيۡنٍ‏"},
"5337": {"surat": 68, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5338": {"surat": 68, "ayat": 2, "teks": "نٓ‌ وَالۡقَلَمِ وَمَا يَسۡطُرُوۡنَۙ‏"},
"5339": {"surat": 68, "ayat": 3, "teks": "مَاۤ اَنۡتَ بِـنِعۡمَةِ رَبِّكَ  بِمَجۡنُوۡنٍ‌ۚ‏"},
"5340": {"surat": 68, "ayat": 4, "teks": "وَاِنَّ لَڪَ لَاَجۡرًا غَيۡرَ مَمۡنُوۡنٍ‌ۚ‏"},
"5341": {"surat": 68, "ayat": 5, "teks": "وَاِنَّكَ  لَعَلٰى خُلُقٍ عَظِيۡمٍ‏"},
"5342": {"surat": 68, "ayat": 6, "teks": "فَسَتُبۡصِرُ وَيُبۡصِرُوۡنَۙ‏"},
"5343": {"surat": 68, "ayat": 7, "teks": "بِاَيِّٮكُمُ الۡمَفۡتُوۡنُ‏"},
"5344": {"surat": 68, "ayat": 8, "teks": "اِنَّ رَبَّكَ هُوَ اَعۡلَمُ بِمَنۡ ضَلَّ عَنۡ سَبِيۡلِهٖ وَهُوَ اَعۡلَمُ  بِالۡمُهۡتَدِيۡنَ‏"},
"5345": {"surat": 68, "ayat": 9, "teks": "فَلَا تُطِعِ الۡمُكَذِّبِيۡنَ‏"},
"5346": {"surat": 68, "ayat": 10, "teks": "وَدُّوۡا لَوۡ تُدۡهِنُ  فَيُدۡهِنُوۡنَ‏"},
"5347": {"surat": 68, "ayat": 11, "teks": "وَلَا تُطِعۡ كُلَّ حَلَّافٍ مَّهِيۡنٍۙ‏"},
"5348": {"surat": 68, "ayat": 12, "teks": "هَمَّازٍ مَّشَّآءٍۢ  بِنَمِيۡمٍۙ‏"},
"5349": {"surat": 68, "ayat": 13, "teks": "مَّنَّاعٍ لِّلۡخَيۡرِ مُعۡتَدٍ اَثِيۡمٍۙ‏"},
"5350": {"surat": 68, "ayat": 14, "teks": "عُتُلٍّ ۢ بَعۡدَ ذٰلِكَ زَنِيۡمٍۙ‏"},
"5351": {"surat": 68, "ayat": 15, "teks": "اَنۡ كَانَ ذَا مَالٍ وَّبَنِيۡنَؕ‏"},
"5352": {"surat": 68, "ayat": 16, "teks": "اِذَا تُتۡلٰى عَلَيۡهِ اٰيٰتُنَا قَالَ  اَسَاطِيۡرُ الۡاَوَّلِيۡنَ‏"},
"5353": {"surat": 68, "ayat": 17, "teks": "سَنَسِمُهٗ عَلَى الۡخُـرۡطُوۡمِ‏"},
"5354": {"surat": 68, "ayat": 18, "teks": "اِنَّا بَلَوۡنٰهُمۡ كَمَا بَلَوۡنَاۤ اَصۡحٰبَ الۡجَـنَّةِ‌ ۚ اِذۡ اَقۡسَمُوۡا لَيَصۡرِمُنَّهَا مُصۡبِحِيۡنَۙ‏"},
"5355": {"surat": 68, "ayat": 19, "teks": "وَلَا يَسۡتَثۡنُوۡنَ‏"},
"5356": {"surat": 68, "ayat": 20, "teks": "فَطَافَ عَلَيۡهَا طَآٮِٕفٌ مِّنۡ رَّبِّكَ وَهُمۡ نَآٮِٕمُوۡنَ‏"},
"5357": {"surat": 68, "ayat": 21, "teks": "فَاَصۡبَحَتۡ كَالصَّرِيۡمِۙ‏"},
"5358": {"surat": 68, "ayat": 22, "teks": "فَتَـنَادَوۡا مُصۡبِحِيۡنَۙ‏"},
"5359": {"surat": 68, "ayat": 23, "teks": "اَنِ اغۡدُوۡا عَلٰى  حَرۡثِكُمۡ اِنۡ كُنۡتُمۡ صٰرِمِيۡنَ‏"},
"5360": {"surat": 68, "ayat": 24, "teks": "فَانۡطَلَقُوۡا وَهُمۡ يَتَخَافَتُوۡنَۙ‏"},
"5361": {"surat": 68, "ayat": 25, "teks": "اَنۡ لَّا يَدۡخُلَنَّهَا الۡيَوۡمَ عَلَيۡكُمۡ مِّسۡكِيۡنٌۙ‏"},
"5362": {"surat": 68, "ayat": 26, "teks": "وَّغَدَوۡا عَلٰى حَرۡدٍ قٰدِرِيۡنَ‏"},
"5363": {"surat": 68, "ayat": 27, "teks": "فَلَمَّا رَاَوۡهَا قَالُوۡۤا اِنَّا لَـضَآلُّوۡنَۙ‏"},
"5364": {"surat": 68, "ayat": 28, "teks": "بَلۡ نَحۡنُ  مَحۡرُوۡمُوۡنَ‏"},
"5365": {"surat": 68, "ayat": 29, "teks": "قَالَ اَوۡسَطُهُمۡ اَلَمۡ اَقُلۡ لَّكُمۡ لَوۡلَا تُسَبِّحُوۡنَ‏"},
"5366": {"surat": 68, "ayat": 30, "teks": "قَالُوۡا سُبۡحٰنَ رَبِّنَاۤ اِنَّا كُنَّا ظٰلِمِيۡنَ‏"},
"5367": {"surat": 68, "ayat": 31, "teks": "فَاَقۡبَلَ بَعۡضُهُمۡ عَلٰى  بَعۡضٍ يَّتَلَاوَمُوۡنَ‏"},
"5368": {"surat": 68, "ayat": 32, "teks": "قَالُوۡا يٰوَيۡلَنَاۤ اِنَّا كُنَّا طٰغِيۡنَ‏"},
"5369": {"surat": 68, "ayat": 33, "teks": "عَسٰى رَبُّنَاۤ اَنۡ يُّبۡدِلَـنَا خَيۡرًا مِّنۡهَاۤ اِنَّاۤ اِلٰى رَبِّنَا رٰغِبُوۡنَ‏"},
"5370": {"surat": 68, "ayat": 34, "teks": "كَذٰلِكَ الۡعَذَابُ‌ؕ وَلَعَذَابُ الۡاٰخِرَةِ اَكۡبَرُ ‌ۘ لَوۡ كَانُوۡا يَعۡلَمُوۡنَ‏"},
"5371": {"surat": 68, "ayat": 35, "teks": "اِنَّ لِلۡمُتَّقِيۡنَ عِنۡدَ رَبِّهِمۡ جَنّٰتِ النَّعِيۡمِ‏"},
"5372": {"surat": 68, "ayat": 36, "teks": "اَفَنَجۡعَلُ الۡمُسۡلِمِيۡنَ كَالۡمُجۡرِمِيۡنَؕ‏"},
"5373": {"surat": 68, "ayat": 37, "teks": "مَا لَـكُمۡ كَيۡفَ تَحۡكُمُوۡنَ‌ۚ‏"},
"5374": {"surat": 68, "ayat": 38, "teks": "اَمۡ لَـكُمۡ كِتٰبٌ فِيۡهِ تَدۡرُسُوۡنَۙ‏"},
"5375": {"surat": 68, "ayat": 39, "teks": "اِنَّ لَـكُمۡ فِيۡهِ لَمَا تَخَيَّرُوۡنَ‌ۚ‏"},
"5376": {"surat": 68, "ayat": 40, "teks": "اَمۡ لَـكُمۡ اَيۡمَانٌ عَلَيۡنَا بَالِغَةٌ اِلٰى يَوۡمِ الۡقِيٰمَةِ‌ ۙ اِنَّ لَـكُمۡ لَمَا تَحۡكُمُوۡنَ‌ۚ‏"},
"5377": {"surat": 68, "ayat": 41, "teks": "سَلۡهُمۡ اَيُّهُمۡ بِذٰلِكَ زَعِيۡمٌ ۛۚ‏"},
"5378": {"surat": 68, "ayat": 42, "teks": "اَمۡ لَهُمۡ شُرَكَآءُۛۚ فَلۡيَاۡتُوۡا بِشُرَكَآٮِٕهِمۡ اِنۡ كَانُوۡا صٰدِقِيۡنَ‏"},
"5379": {"surat": 68, "ayat": 43, "teks": "يَوۡمَ يُكۡشَفُ  عَنۡ سَاقٍ وَّيُدۡعَوۡنَ اِلَى السُّجُوۡدِ فَلَا يَسۡتَطِيۡعُوۡنَۙ‏"},
"5380": {"surat": 68, "ayat": 44, "teks": "خَاشِعَةً اَبۡصَارُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٌ   ؕ وَقَدۡ كَانُوۡا يُدۡعَوۡنَ اِلَى السُّجُوۡدِ وَهُمۡ سٰلِمُوۡنَ‏"},
"5381": {"surat": 68, "ayat": 45, "teks": "فَذَرۡنِىۡ وَمَنۡ يُّكَذِّبُ بِهٰذَا  الۡحَـدِيۡثِ‌ؕ سَنَسۡتَدۡرِجُهُمۡ مِّنۡ حَيۡثُ لَا يَعۡلَمُوۡنَۙ‏"},
"5382": {"surat": 68, "ayat": 46, "teks": "وَاُمۡلِىۡ  لَهُمۡ‌ؕ اِنَّ كَيۡدِىۡ مَتِيۡنٌ‏"},
"5383": {"surat": 68, "ayat": 47, "teks": "اَمۡ تَسۡـَٔـلُهُمۡ اَجۡرًا فَهُمۡ مِّنۡ مَّغۡرَمٍ  مُّثۡقَلُوۡنَ‌ۚ‏"},
"5384": {"surat": 68, "ayat": 48, "teks": "اَمۡ عِنۡدَهُمُ الۡغَيۡبُ فَهُمۡ يَكۡتُبُوۡنَ‏"},
"5385": {"surat": 68, "ayat": 49, "teks": "فَاصۡبِرۡ لِحُكۡمِ  رَبِّكَ وَلَا تَكُنۡ كَصَاحِبِ الۡحُوۡتِ‌ۘ اِذۡ نَادٰى وَهُوَ مَكۡظُوۡمٌؕ‏"},
"5386": {"surat": 68, "ayat": 50, "teks": "لَوۡلَاۤ اَنۡ تَدٰرَكَهٗ نِعۡمَةٌ مِّنۡ رَّبِّهٖ لَنُبِذَ بِالۡعَرَآءِ وَهُوَ مَذۡمُوۡمٌ‏"},
"5387": {"surat": 68, "ayat": 51, "teks": "فَاجۡتَبٰهُ رَبُّهٗ فَجَعَلَهٗ مِنَ الصّٰلِحِيۡنَ‏"},
"5388": {"surat": 68, "ayat": 52, "teks": "وَاِنۡ يَّكَادُ الَّذِيۡنَ كَفَرُوۡا لَيُزۡلِقُوۡنَكَ بِاَبۡصَارِهِمۡ لَمَّا سَمِعُوا الذِّكۡرَ وَيَقُوۡلُوۡنَ اِنَّهٗ لَمَجۡنُوۡنٌ‌ۘ‏"},
"5389": {"surat": 68, "ayat": 53, "teks": "وَمَا هُوَ اِلَّا ذِكۡرٌ لِّلۡعٰلَمِيۡنَ‏"},
"5390": {"surat": 69, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5391": {"surat": 69, "ayat": 2, "teks": "اَلۡحَـآقَّةُ  ۙ‏"},
"5392": {"surat": 69, "ayat": 3, "teks": "مَا الۡحَـآقَّةُ‌  ۚ‏"},
"5393": {"surat": 69, "ayat": 4, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا الۡحَــآقَّةُ  ؕ‏"},
"5394": {"surat": 69, "ayat": 5, "teks": "كَذَّبَتۡ ثَمُوۡدُ وَعَادٌۢ بِالۡقَارِعَةِ‏"},
"5395": {"surat": 69, "ayat": 6, "teks": "فَاَمَّا ثَمُوۡدُ فَاُهۡلِكُوۡا  بِالطَّاغِيَةِ‏"},
"5396": {"surat": 69, "ayat": 7, "teks": "وَاَمَّا عَادٌ فَاُهۡلِكُوۡا بِرِيۡحٍ صَرۡصَرٍ عَاتِيَةٍۙ‏"},
"5397": {"surat": 69, "ayat": 8, "teks": "سَخَّرَهَا عَلَيۡهِمۡ سَبۡعَ لَيَالٍ وَّثَمٰنِيَةَ اَيَّامٍۙ حُسُوۡمًا ۙ فَتَرَى الۡقَوۡمَ فِيۡهَا صَرۡعٰىۙ كَاَنَّهُمۡ اَعۡجَازُ نَخۡلٍ خَاوِيَةٍ‌ ۚ‏"},
"5398": {"surat": 69, "ayat": 9, "teks": "فَهَلۡ تَرٰى لَهُمۡ مِّنۡۢ بَاقِيَةٍ‏"},
"5399": {"surat": 69, "ayat": 10, "teks": "وَجَآءَ فِرۡعَوۡنُ وَمَنۡ قَبۡلَهٗ  وَالۡمُؤۡتَفِكٰتُ بِالۡخَـاطِئَةِ‌ۚ‏"},
"5400": {"surat": 69, "ayat": 11, "teks": "فَعَصَوۡا رَسُوۡلَ رَبِّهِمۡ  فَاَخَذَهُمۡ اَخۡذَةً رَّابِيَةً‏"},
"5401": {"surat": 69, "ayat": 12, "teks": "اِنَّا لَمَّا طَغَا الۡمَآءُ حَمَلۡنٰكُمۡ فِى الۡجَارِيَةِ  ۙ‏"},
"5402": {"surat": 69, "ayat": 13, "teks": "لِنَجۡعَلَهَا لَـكُمۡ تَذۡكِرَةً وَّتَعِيَهَاۤ اُذُنٌ وَّاعِيَةٌ‏"},
"5403": {"surat": 69, "ayat": 14, "teks": "فَاِذَا نُفِخَ فِى الصُّوۡرِ نَفۡخَةٌ وَّاحِدَةٌ ۙ‏"},
"5404": {"surat": 69, "ayat": 15, "teks": "وَحُمِلَتِ الۡاَرۡضُ وَ الۡجِبَالُ فَدُكَّتَا دَكَّةً وَّاحِدَةً ۙ‏"},
"5405": {"surat": 69, "ayat": 16, "teks": "فَيَوۡمَٮِٕذٍ وَّقَعَتِ الۡوَاقِعَةُ  ۙ‏"},
"5406": {"surat": 69, "ayat": 17, "teks": "وَانْشَقَّتِ السَّمَآءُ فَهِىَ يَوۡمَٮِٕذٍ وَّاهِيَةٌ  ۙ‏"},
"5407": {"surat": 69, "ayat": 18, "teks": "وَّالۡمَلَكُ عَلٰٓى اَرۡجَآٮِٕهَا ‌ؕ وَيَحۡمِلُ عَرۡشَ رَبِّكَ فَوۡقَهُمۡ يَوۡمَٮِٕذٍ ثَمٰنِيَةٌ ؕ‏"},
"5408": {"surat": 69, "ayat": 19, "teks": "يَوۡمَٮِٕذٍ  تُعۡرَضُوۡنَ لَا تَخۡفٰى مِنۡكُمۡ خَافِيَةٌ‏"},
"5409": {"surat": 69, "ayat": 20, "teks": "فَاَمَّا مَنۡ اُوۡتِىَ  كِتٰبَهٗ بِيَمِيۡنِهٖۙ فَيَقُوۡلُ هَآؤُمُ اقۡرَءُوۡا كِتٰبِيَهۡ‌ۚ‏"},
"5410": {"surat": 69, "ayat": 21, "teks": "اِنِّىۡ  ظَنَنۡتُ اَنِّىۡ مُلٰقٍ حِسَابِيَهۡ‌ۚ‏"},
"5411": {"surat": 69, "ayat": 22, "teks": "فَهُوَ فِىۡ عِيۡشَةٍ رَّاضِيَةٍۙ‏"},
"5412": {"surat": 69, "ayat": 23, "teks": "فِىۡ جَنَّةٍ عَالِيَةٍۙ‏"},
"5413": {"surat": 69, "ayat": 24, "teks": "قُطُوۡفُهَا دَانِيَةٌ‏"},
"5414": {"surat": 69, "ayat": 25, "teks": "كُلُوۡا وَاشۡرَبُوۡا هَنِيۡٓـــًٔا ۢ بِمَاۤ اَسۡلَفۡتُمۡ فِى الۡاَيَّامِ الۡخَـالِيَةِ‏"},
"5415": {"surat": 69, "ayat": 26, "teks": "وَاَمَّا مَنۡ اُوۡتِىَ كِتٰبَهٗ بِشِمَالِهٖ  ۙ فَيَقُوۡلُ يٰلَيۡتَنِىۡ لَمۡ اُوۡتَ كِتٰبِيَهۡ‌ۚ‏"},
"5416": {"surat": 69, "ayat": 27, "teks": "وَلَمۡ اَدۡرِ مَا حِسَابِيَهۡ‌ۚ‏"},
"5417": {"surat": 69, "ayat": 28, "teks": "يٰلَيۡتَهَا كَانَتِ الۡقَاضِيَةَ‌  ۚ‏"},
"5418": {"surat": 69, "ayat": 29, "teks": "مَاۤ  اَغۡنٰى عَنِّىۡ مَالِيَهۡۚ‏"},
"5419": {"surat": 69, "ayat": 30, "teks": "هَلَكَ عَنِّىۡ سُلۡطٰنِيَهۡ‌ۚ‏"},
"5420": {"surat": 69, "ayat": 31, "teks": "خُذُوۡهُ فَغُلُّوۡهُ  ۙ‏"},
"5421": {"surat": 69, "ayat": 32, "teks": "ثُمَّ الۡجَحِيۡمَ صَلُّوۡهُ ۙ‏"},
"5422": {"surat": 69, "ayat": 33, "teks": "ثُمَّ فِىۡ سِلۡسِلَةٍ ذَرۡعُهَا سَبۡعُوۡنَ ذِرَاعًا فَاسۡلُكُوۡهُ ؕ‏"},
"5423": {"surat": 69, "ayat": 34, "teks": "اِنَّهٗ كَانَ لَا يُؤۡمِنُ  بِاللّٰهِ الۡعَظِيۡمِۙ‏"},
"5424": {"surat": 69, "ayat": 35, "teks": "وَلَا يَحُضُّ عَلٰى طَعَامِ الۡمِسۡكِيۡنِؕ‏"},
"5425": {"surat": 69, "ayat": 36, "teks": "فَلَيۡسَ لَـهُ الۡيَوۡمَ هٰهُنَا حَمِيۡمٌۙ‏"},
"5426": {"surat": 69, "ayat": 37, "teks": "وَّلَا طَعَامٌ اِلَّا مِنۡ  غِسۡلِيۡنٍۙ‏"},
"5427": {"surat": 69, "ayat": 38, "teks": "لَّا يَاۡكُلُهٗۤ اِلَّا الۡخٰطِئُوْنَ‏"},
"5428": {"surat": 69, "ayat": 39, "teks": "فَلَاۤ اُقۡسِمُ بِمَا  تُبۡصِرُوۡنَۙ‏"},
"5429": {"surat": 69, "ayat": 40, "teks": "وَمَا لَا تُبۡصِرُوۡنَۙ‏"},
"5430": {"surat": 69, "ayat": 41, "teks": "اِنَّهٗ لَقَوۡلُ رَسُوۡلٍ  كَرِيۡمٍۚ ۙ‏"},
"5431": {"surat": 69, "ayat": 42, "teks": "وَّمَا هُوَ بِقَوۡلِ شَاعِرٍ‌ؕ قَلِيۡلًا مَّا تُؤۡمِنُوۡنَۙ‏"},
"5432": {"surat": 69, "ayat": 43, "teks": "وَلَا بِقَوۡلِ كَاهِنٍ‌ؕ قَلِيۡلًا مَّا تَذَكَّرُوۡنَؕ‏"},
"5433": {"surat": 69, "ayat": 44, "teks": "تَنۡزِيۡلٌ مِّنۡ  رَّبِّ الۡعٰلَمِيۡنَ‏"},
"5434": {"surat": 69, "ayat": 45, "teks": "وَلَوۡ تَقَوَّلَ عَلَيۡنَا بَعۡضَ الۡاَقَاوِيۡلِۙ‏"},
"5435": {"surat": 69, "ayat": 46, "teks": "لَاَخَذۡنَا مِنۡهُ بِالۡيَمِيۡنِۙ‏"},
"5436": {"surat": 69, "ayat": 47, "teks": "ثُمَّ لَقَطَعۡنَا مِنۡهُ الۡوَتِيۡنَ  ۖ‏"},
"5437": {"surat": 69, "ayat": 48, "teks": "فَمَا  مِنۡكُمۡ مِّنۡ اَحَدٍ عَنۡهُ حَاجِزِيۡنَ‏"},
"5438": {"surat": 69, "ayat": 49, "teks": "وَاِنَّهٗ لَتَذۡكِرَةٌ لِّلۡمُتَّقِيۡنَ‏"},
"5439": {"surat": 69, "ayat": 50, "teks": "وَاِنَّا لَنَعۡلَمُ اَنَّ مِنۡكُمۡ مُّكَذِّبِيۡنَ‏"},
"5440": {"surat": 69, "ayat": 51, "teks": "وَاِنَّهٗ لَحَسۡرَةٌ عَلَى الۡكٰفِرِيۡنَ‏"},
"5441": {"surat": 69, "ayat": 52, "teks": " وَاِنَّهٗ لَحَـقُّ الۡيَقِيۡنِ‏"},
"5442": {"surat": 69, "ayat": 53, "teks": "فَسَبِّحۡ بِاسۡمِ رَبِّكَ الۡعَظِيۡمِ‏"},
"5443": {"surat": 70, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5444": {"surat": 70, "ayat": 2, "teks": "سَاَلَ سَآٮِٕلٌ ۢ بِعَذَابٍ وَّاقِعٍۙ‏"},
"5445": {"surat": 70, "ayat": 3, "teks": "لِّلۡكٰفِرِيۡنَ لَيۡسَ لَهٗ دَافِعٌ ۙ‏"},
"5446": {"surat": 70, "ayat": 4, "teks": "مِّنَ اللّٰهِ ذِى الۡمَعَارِجِؕ‏"},
"5447": {"surat": 70, "ayat": 5, "teks": "تَعۡرُجُ الۡمَلٰٓٮِٕكَةُ وَ الرُّوۡحُ اِلَيۡهِ فِىۡ يَوۡمٍ كَانَ مِقۡدَارُهٗ خَمۡسِيۡنَ اَلۡفَ سَنَةٍ‌ۚ‏"},
"5448": {"surat": 70, "ayat": 6, "teks": "فَاصۡبِرۡ صَبۡرًا جَمِيۡلًا‏"},
"5449": {"surat": 70, "ayat": 7, "teks": "اِنَّهُمۡ يَرَوۡنَهٗ بَعِيۡدًا ۙ‏"},
"5450": {"surat": 70, "ayat": 8, "teks": "وَّنَرٰٮهُ قَرِيۡبًا ؕ‏"},
"5451": {"surat": 70, "ayat": 9, "teks": "يَوۡمَ تَكُوۡنُ السَّمَآءُ كَالۡمُهۡلِۙ‏"},
"5452": {"surat": 70, "ayat": 10, "teks": "وَتَكُوۡنُ الۡجِبَالُ كَالۡعِهۡنِۙ‏"},
"5453": {"surat": 70, "ayat": 11, "teks": "وَلَا يَسۡـَٔـلُ حَمِيۡمٌ حَمِيۡمًا ۖۚ‏"},
"5454": {"surat": 70, "ayat": 12, "teks": "يُّبَصَّرُوۡنَهُمۡ‌ؕ يَوَدُّ الۡمُجۡرِمُ لَوۡ يَفۡتَدِىۡ مِنۡ عَذَابِ يَوۡمِٮِٕذٍۢ بِبَنِيۡهِۙ‏"},
"5455": {"surat": 70, "ayat": 13, "teks": "وَ صَاحِبَتِهٖ وَاَخِيۡهِۙ‏"},
"5456": {"surat": 70, "ayat": 14, "teks": "وَفَصِيۡلَتِهِ الَّتِىۡ تُــْٔوِيۡهِۙ‏"},
"5457": {"surat": 70, "ayat": 15, "teks": "وَمَنۡ فِى الۡاَرۡضِ جَمِيۡعًا  ۙ ثُمَّ يُنۡجِيۡهِۙ‏"},
"5458": {"surat": 70, "ayat": 16, "teks": "كَلَّاؕ اِنَّهَا لَظٰىۙ‏"},
"5459": {"surat": 70, "ayat": 17, "teks": "نَزَّاعَةً  لِّلشَّوٰى‌ۖ‌ۚ‏"},
"5460": {"surat": 70, "ayat": 18, "teks": "تَدۡعُوۡا مَنۡ اَدۡبَرَ وَتَوَلّٰىۙ‏"},
"5461": {"surat": 70, "ayat": 19, "teks": "وَجَمَعَ فَاَوۡعٰى‏"},
"5462": {"surat": 70, "ayat": 20, "teks": "اِنَّ الۡاِنۡسَانَ خُلِقَ هَلُوۡعًا ۙ‏"},
"5463": {"surat": 70, "ayat": 21, "teks": "اِذَا مَسَّهُ الشَّرُّ جَزُوۡعًا ۙ‏"},
"5464": {"surat": 70, "ayat": 22, "teks": "وَاِذَا مَسَّهُ الۡخَيۡرُ مَنُوۡعًا ۙ‏"},
"5465": {"surat": 70, "ayat": 23, "teks": "اِلَّا الۡمُصَلِّيۡنَۙ‏"},
"5466": {"surat": 70, "ayat": 24, "teks": "الَّذِيۡنَ هُمۡ عَلٰى صَلَاتِهِمۡ  دَآٮِٕمُوۡنَۙ‏"},
"5467": {"surat": 70, "ayat": 25, "teks": "وَالَّذِيۡنَ فِىۡۤ اَمۡوَالِهِمۡ حَقٌّ مَّعۡلُوۡمٌۙ‏"},
"5468": {"surat": 70, "ayat": 26, "teks": "لِّلسَّآٮِٕلِ وَالۡمَحۡرُوۡمِۙ‏"},
"5469": {"surat": 70, "ayat": 27, "teks": "وَالَّذِيۡنَ يُصَدِّقُوۡنَ بِيَوۡمِ الدِّيۡنِۙ‏"},
"5470": {"surat": 70, "ayat": 28, "teks": "وَالَّذِيۡنَ هُمۡ مِّنۡ عَذَابِ رَبِّهِمۡ مُّشۡفِقُوۡنَ‌ۚ‏"},
"5471": {"surat": 70, "ayat": 29, "teks": "اِنَّ عَذَابَ  رَبِّهِمۡ غَيۡرُ مَاۡمُوۡنٍ‏"},
"5472": {"surat": 70, "ayat": 30, "teks": "وَالَّذِيۡنَ هُمۡ لِفُرُوۡجِهِمۡ حٰفِظُوۡنَۙ‏"},
"5473": {"surat": 70, "ayat": 31, "teks": "اِلَّا عَلٰٓى اَزۡوَاجِهِمۡ اَوۡ مَا مَلَـكَتۡ اَيۡمَانُهُمۡ فَاِنَّهُمۡ غَيۡرُ  مَلُوۡمِيۡنَ‌ۚ‏"},
"5474": {"surat": 70, "ayat": 32, "teks": "فَمَنِ ابۡتَغٰى وَرَآءَ ذٰلِكَ فَاُولٰٓٮِٕكَ هُمُ الۡعٰدُوۡنَ‌ۚ‏"},
"5475": {"surat": 70, "ayat": 33, "teks": "وَالَّذِيۡنَ هُمۡ لِاَمٰنٰتِهِمۡ وَعَهۡدِهِمۡ رٰعُوۡنَ ۙ‏"},
"5476": {"surat": 70, "ayat": 34, "teks": "وَالَّذِيۡنَ هُمۡ  بِشَهٰدٰتِهِمۡ قَآٮِٕمُوۡنَۙ‏"},
"5477": {"surat": 70, "ayat": 35, "teks": "وَالَّذِيۡنَ هُمۡ عَلٰى صَلَاتِهِمۡ  يُحَافِظُوۡنَؕ‏"},
"5478": {"surat": 70, "ayat": 36, "teks": "اُولٰٓٮِٕكَ فِىۡ جَنّٰتٍ مُّكۡرَمُوۡنَؕ‏"},
"5479": {"surat": 70, "ayat": 37, "teks": "فَمَالِ الَّذِيۡنَ  كَفَرُوۡا قِبَلَكَ مُهۡطِعِيۡنَۙ‏"},
"5480": {"surat": 70, "ayat": 38, "teks": "عَنِ الۡيَمِيۡنِ وَعَنِ الشِّمَالِ  عِزِيۡنَ‏"},
"5481": {"surat": 70, "ayat": 39, "teks": "اَيَطۡمَعُ كُلُّ امۡرِىءٍ مِّنۡهُمۡ اَنۡ يُّدۡخَلَ جَنَّةَ نَعِيۡمٍۙ‏"},
"5482": {"surat": 70, "ayat": 40, "teks": "كَلَّا ؕ اِنَّا خَلَقۡنٰهُمۡ مِّمَّا يَعۡلَمُوۡنَ‏"},
"5483": {"surat": 70, "ayat": 41, "teks": "فَلَاۤ اُقۡسِمُ  بِرَبِّ الۡمَشٰرِقِ وَالۡمَغٰرِبِ اِنَّا لَقٰدِرُوۡنَۙ‏"},
"5484": {"surat": 70, "ayat": 42, "teks": "عَلٰٓى اَنۡ نُّبَدِّلَ  خَيۡرًا مِّنۡهُمۡۙ وَمَا نَحۡنُ بِمَسۡبُوۡقِيۡنَ‏"},
"5485": {"surat": 70, "ayat": 43, "teks": "فَذَرۡهُمۡ يَخُوۡضُوۡا  وَيَلۡعَبُوۡا حَتّٰى يُلٰقُوۡا يَوۡمَهُمُ الَّذِىۡ يُوۡعَدُوۡنَۙ‏"},
"5486": {"surat": 70, "ayat": 44, "teks": "يَوۡمَ يَخۡرُجُوۡنَ  مِنَ الۡاَجۡدَاثِ سِرَاعًا كَاَنَّهُمۡ اِلٰى نُصُبٍ يُّوۡفِضُوۡنَۙ‏"},
"5487": {"surat": 70, "ayat": 45, "teks": "خَاشِعَةً  اَبۡصَارُهُمۡ تَرۡهَقُهُمۡ ذِلَّةٌؕ ذٰلِكَ الۡيَوۡمُ الَّذِىۡ كَانُوۡا يُوۡعَدُوۡنَ‏"},
"5488": {"surat": 71, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5489": {"surat": 71, "ayat": 2, "teks": "اِنَّاۤ اَرۡسَلۡنَا نُوۡحًا اِلٰى قَوۡمِهٖۤ اَنۡ اَنۡذِرۡ قَوۡمَكَ مِنۡ قَبۡلِ اَنۡ  يَّاۡتِيَهُمۡ عَذَابٌ اَلِيۡمٌ‏"},
"5490": {"surat": 71, "ayat": 3, "teks": "قَالَ يٰقَوۡمِ اِنِّىۡ لَـكُمۡ نَذِيۡرٌ مُّبِيۡنٌۙ‏"},
"5491": {"surat": 71, "ayat": 4, "teks": "اَنِ اعۡبُدُوا اللّٰهَ وَاتَّقُوۡهُ وَاَطِيۡعُوۡنِۙ‏"},
"5492": {"surat": 71, "ayat": 5, "teks": "يَغۡفِرۡ لَـكُمۡ مِّنۡ  ذُنُوۡبِكُمۡ وَيُؤَخِّرۡكُمۡ اِلٰٓى اَجَلٍ مُّسَمًّى‌ؕ اِنَّ اَجَلَ اللّٰهِ  اِذَا جَآءَ لَا يُؤَخَّرُ‌‌ۘ لَوۡ كُنۡتُمۡ تَعۡلَمُوۡنَ‏"},
"5493": {"surat": 71, "ayat": 6, "teks": "قَالَ رَبِّ اِنِّىۡ دَعَوۡتُ قَوۡمِىۡ لَيۡلًا وَّنَهَارًا ۙ‏"},
"5494": {"surat": 71, "ayat": 7, "teks": "فَلَمۡ يَزِدۡهُمۡ دُعَآءِىۡۤ اِلَّا  فِرَارًا‏"},
"5495": {"surat": 71, "ayat": 8, "teks": "وَاِنِّىۡ كُلَّمَا دَعَوۡتُهُمۡ لِتَغۡفِرَ لَهُمۡ جَعَلُوۡۤا اَصَابِعَهُمۡ فِىۡۤ اٰذَانِهِمۡ وَاسۡتَغۡشَوۡا ثِيَابَهُمۡ وَاَصَرُّوۡا وَاسۡتَكۡبَرُوا اسۡتِكۡبَارًا‌ ۚ‏"},
"5496": {"surat": 71, "ayat": 9, "teks": "ثُمَّ اِنِّىۡ دَعَوۡتُهُمۡ جِهَارًا ۙ‏"},
"5497": {"surat": 71, "ayat": 10, "teks": "ثُمَّ اِنِّىۡۤ اَعۡلَـنۡتُ لَهُمۡ وَاَسۡرَرۡتُ لَهُمۡ اِسۡرَارًا ۙ‏"},
"5498": {"surat": 71, "ayat": 11, "teks": "فَقُلۡتُ اسۡتَغۡفِرُوۡا رَبَّكُمۡؕ اِنَّهٗ كَانَ غَفَّارًا ۙ‏"},
"5499": {"surat": 71, "ayat": 12, "teks": "يُّرۡسِلِ السَّمَآءَ عَلَيۡكُمۡ مِّدۡرَارًا ۙ‏"},
"5500": {"surat": 71, "ayat": 13, "teks": "وَّيُمۡدِدۡكُمۡ بِاَمۡوَالٍ وَّبَنِيۡنَ وَيَجۡعَلۡ لَّـكُمۡ جَنّٰتٍ وَّيَجۡعَلۡ لَّـكُمۡ اَنۡهٰرًا ؕ‏"},
"5501": {"surat": 71, "ayat": 14, "teks": "مَا لَـكُمۡ لَا تَرۡجُوۡنَ لِلّٰهِ وَقَارًا‌ ۚ‏"},
"5502": {"surat": 71, "ayat": 15, "teks": "وَقَدۡ خَلَقَكُمۡ اَطۡوَارًا‏"},
"5503": {"surat": 71, "ayat": 16, "teks": "اَلَمۡ تَرَوۡا كَيۡفَ خَلَقَ اللّٰهُ سَبۡعَ سَمٰوٰتٍ طِبَاقًا ۙ‏"},
"5504": {"surat": 71, "ayat": 17, "teks": "وَّجَعَلَ الۡقَمَرَ فِيۡهِنَّ نُوۡرًا ۙ وَّجَعَلَ الشَّمۡسَ سِرَاجًا‏"},
"5505": {"surat": 71, "ayat": 18, "teks": "وَاللّٰهُ اَنۡۢبَتَكُمۡ مِّنَ الۡاَرۡضِ نَبَاتًا ۙ‏"},
"5506": {"surat": 71, "ayat": 19, "teks": "ثُمَّ يُعِيۡدُكُمۡ فِيۡهَا وَيُخۡرِجُكُمۡ اِخۡرَاجًا‏"},
"5507": {"surat": 71, "ayat": 20, "teks": "وَاللّٰهُ جَعَلَ لَـكُمُ الۡاَرۡضَ بِسَاطًا ۙ‏"},
"5508": {"surat": 71, "ayat": 21, "teks": "لِّـتَسۡلُكُوۡا مِنۡهَا سُبُلًا فِجَاجًا‏"},
"5509": {"surat": 71, "ayat": 22, "teks": "قَالَ نُوۡحٌ رَّبِّ اِنَّهُمۡ عَصَوۡنِىۡ وَاتَّبَعُوۡا مَنۡ لَّمۡ يَزِدۡهُ مَالُهٗ وَوَلَدُهٗۤ اِلَّا خَسَارًا‌ ۚ‏"},
"5510": {"surat": 71, "ayat": 23, "teks": "وَمَكَرُوۡا مَكۡرًا كُبَّارًا‌ ۚ‏"},
"5511": {"surat": 71, "ayat": 24, "teks": "وَ قَالُوۡا لَا تَذَرُنَّ اٰلِهَتَكُمۡ وَلَا تَذَرُنَّ وَدًّا وَّلَا سُوَاعًا  ۙ وَّ لَا يَغُوۡثَ وَيَعُوۡقَ وَنَسۡرًا‌ ۚ‏"},
"5512": {"surat": 71, "ayat": 25, "teks": "وَقَدۡ اَضَلُّوۡا كَثِيۡرًا‌ ‌ ۚ وَلَا تَزِدِ الظّٰلِمِيۡنَ اِلَّا ضَلٰلًا‏"},
"5513": {"surat": 71, "ayat": 26, "teks": "مِّمَّا خَطِٓيْئٰتِهِمۡ اُغۡرِقُوۡا فَاُدۡخِلُوۡا نَارًا  ۙ فَلَمۡ يَجِدُوۡا لَهُمۡ مِّنۡ دُوۡنِ اللّٰهِ اَنۡصَارًا‏"},
"5514": {"surat": 71, "ayat": 27, "teks": "وَ قَالَ نُوۡحٌ رَّبِّ لَا تَذَرۡ عَلَى الۡاَرۡضِ مِنَ الۡكٰفِرِيۡنَ دَيَّارًا‏"},
"5515": {"surat": 71, "ayat": 28, "teks": "اِنَّكَ اِنۡ تَذَرۡهُمۡ يُضِلُّوۡا عِبَادَكَ وَلَا يَلِدُوۡۤا اِلَّا فَاجِرًا  كَفَّارًا‏"},
"5516": {"surat": 71, "ayat": 29, "teks": "رَبِّ اغۡفِرۡلِىۡ وَلِـوَالِدَىَّ وَلِمَنۡ دَخَلَ بَيۡتِىَ مُؤۡمِنًا وَّلِلۡمُؤۡمِنِيۡنَ وَالۡمُؤۡمِنٰتِؕ وَلَا تَزِدِ الظّٰلِمِيۡنَ اِلَّا تَبَارًا‏"},
"5517": {"surat": 72, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5518": {"surat": 72, "ayat": 2, "teks": "قُلۡ اُوۡحِىَ اِلَىَّ اَنَّهُ اسۡتَمَعَ نَفَرٌ مِّنَ الۡجِنِّ فَقَالُوۡۤا اِنَّا سَمِعۡنَا قُرۡاٰنًاعَجَبًا ۙ‏"},
"5519": {"surat": 72, "ayat": 3, "teks": "يَّهۡدِىۡۤ اِلَى الرُّشۡدِ فَاٰمَنَّا بِهٖ‌ ؕ وَلَنۡ نُّشۡرِكَ بِرَبِّنَاۤ اَحَدًا ۙ‏"},
"5520": {"surat": 72, "ayat": 4, "teks": "وَّاَنَّهٗ تَعٰلٰى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَّلَا وَلَدًا ۙ‏"},
"5521": {"surat": 72, "ayat": 5, "teks": "وَّ اَنَّهٗ كَانَ يَقُوۡلُ سَفِيۡهُنَا عَلَى اللّٰهِ شَطَطًا ۙ‏"},
"5522": {"surat": 72, "ayat": 6, "teks": "وَّاَنَّا ظَنَنَّاۤ اَنۡ لَّنۡ تَقُوۡلَ الۡاِنۡسُ وَالۡجِنُّ عَلَى اللّٰهِ كَذِبًا ۙ‏"},
"5523": {"surat": 72, "ayat": 7, "teks": "وَّاَنَّهٗ كَانَ رِجَالٌ مِّنَ الۡاِنۡسِ يَعُوۡذُوۡنَ بِرِجَالٍ مِّنَ الۡجِنِّ فَزَادُوۡهُمۡ رَهَقًا ۙ‏"},
"5524": {"surat": 72, "ayat": 8, "teks": "وَّاَنَّهُمۡ ظَنُّوۡا كَمَا ظَنَنۡتُمۡ اَنۡ لَّنۡ يَّبۡعَثَ اللّٰهُ اَحَدًا ۙ‏"},
"5525": {"surat": 72, "ayat": 9, "teks": "وَّاَنَّا لَمَسۡنَا السَّمَآءَ فَوَجَدۡنٰهَا مُلِئَتۡ حَرَسًا شَدِيۡدًا وَّشُهُبًا ۙ‏"},
"5526": {"surat": 72, "ayat": 10, "teks": "وَّاَنَّا كُنَّا نَقۡعُدُ مِنۡهَا مَقَاعِدَ لِلسَّمۡعِ‌ ؕ فَمَنۡ يَّسۡتَمِعِ الۡاٰنَ يَجِدۡ لَهٗ شِهَابًا رَّصَدًا ۙ‏"},
"5527": {"surat": 72, "ayat": 11, "teks": "وَّاَنَّا لَا نَدۡرِىۡۤ اَشَرٌّ اُرِيۡدَ بِمَنۡ فِى الۡاَرۡضِ اَمۡ اَرَادَ بِهِمۡ رَبُّهُمۡ رَشَدًا ۙ‏"},
"5528": {"surat": 72, "ayat": 12, "teks": "وَّاَنَّا مِنَّا الصّٰلِحُوۡنَ وَمِنَّا دُوۡنَ ذٰلِكَ‌ؕ كُنَّا طَرَآٮِٕقَ قِدَدًا ۙ‏"},
"5529": {"surat": 72, "ayat": 13, "teks": "وَّاَنَّا ظَنَنَّاۤ اَنۡ لَّنۡ نُّعۡجِزَ اللّٰهَ فِى الۡاَرۡضِ وَلَنۡ نُّعۡجِزَهٗ هَرَبًا ۙ‏"},
"5530": {"surat": 72, "ayat": 14, "teks": "وَّاَنَّا لَمَّا سَمِعۡنَا الۡهُدٰٓى اٰمَنَّا بِهٖ‌ ؕ فَمَنۡ يُّؤۡمِنۡۢ بِرَبِّهٖ فَلَا يَخَافُ بَخۡسًا وَّلَا رَهَقًا ۙ‏"},
"5531": {"surat": 72, "ayat": 15, "teks": "وَّاَنَّا مِنَّا  الۡمُسۡلِمُوۡنَ وَمِنَّا الۡقٰسِطُوۡنَ‌ؕ فَمَنۡ اَسۡلَمَ فَاُولٰٓٮِٕكَ تَحَرَّوۡا رَشَدًا‏"},
"5532": {"surat": 72, "ayat": 16, "teks": "وَاَمَّا الۡقٰسِطُوۡنَ فَكَانُوۡا لِجَهَنَّمَ حَطَبًا ۙ‏"},
"5533": {"surat": 72, "ayat": 17, "teks": "وَّاَنْ لَّوِ اسۡتَقَامُوۡا عَلَى الطَّرِيۡقَةِ لَاَسۡقَيۡنٰهُمۡ مَّآءً غَدَقًا ۙ‏"},
"5534": {"surat": 72, "ayat": 18, "teks": "لِّنَفۡتِنَهُمۡ فِيۡهِ‌ ؕ وَمَنۡ يُّعۡرِضۡ عَنۡ ذِكۡرِ رَبِّهٖ يَسۡلُكۡهُ عَذَابًا صَعَدًا ۙ‏"},
"5535": {"surat": 72, "ayat": 19, "teks": "وَّاَنَّ الۡمَسٰجِدَ لِلّٰهِ فَلَا تَدۡعُوۡا مَعَ اللّٰهِ اَحَدًا ۙ‏"},
"5536": {"surat": 72, "ayat": 20, "teks": "وَّاَنَّهٗ لَمَّا قَامَ عَبۡدُ اللّٰهِ يَدۡعُوۡهُ كَادُوۡا يَكُوۡنُوۡنَ عَلَيۡهِ لِبَدًا ؕ‏"},
"5537": {"surat": 72, "ayat": 21, "teks": "قُلۡ اِنَّمَاۤ اَدۡعُوۡا رَبِّىۡ وَلَاۤ اُشۡرِكُ بِهٖۤ اَحَدًا‏"},
"5538": {"surat": 72, "ayat": 22, "teks": "قُلۡ اِنِّىۡ لَاۤ  اَمۡلِكُ لَـكُمۡ ضَرًّا وَّلَا رَشَدًا‏"},
"5539": {"surat": 72, "ayat": 23, "teks": "قُلۡ اِنِّىۡ لَنۡ يُّجِيۡرَنِىۡ مِنَ اللّٰهِ اَحَدٌ  ۙ وَّلَنۡ اَجِدَ مِنۡ دُوۡنِهٖ مُلۡتَحَدًا ۙ‏"},
"5540": {"surat": 72, "ayat": 24, "teks": "اِلَّا بَلٰغًا مِّنَ اللّٰهِ وَرِسٰلٰتِهٖ‌ ؕ وَمَنۡ يَّعۡصِ اللّٰهَ وَرَسُوۡلَهٗ فَاِنَّ لَهٗ نَارَ جَهَنَّمَ خٰلِدِيۡنَ فِيۡهَاۤ اَبَدًا ؕ‏"},
"5541": {"surat": 72, "ayat": 25, "teks": "حَتّٰٓى اِذَا رَاَوۡا مَا  يُوۡعَدُوۡنَ فَسَيَعۡلَمُوۡنَ مَنۡ اَضۡعَفُ نَاصِرًا وَّاَقَلُّ عَدَدًا‏"},
"5542": {"surat": 72, "ayat": 26, "teks": "قُلۡ اِنۡ اَدۡرِىۡۤ اَقَرِيۡبٌ مَّا تُوۡعَدُوۡنَ اَمۡ يَجۡعَلُ  لَهٗ رَبِّىۡۤ اَمَدًا‏"},
"5543": {"surat": 72, "ayat": 27, "teks": "عٰلِمُ الۡغَيۡبِ فَلَا يُظۡهِرُ عَلٰى غَيۡبِهٖۤ اَحَدًا ۙ‏"},
"5544": {"surat": 72, "ayat": 28, "teks": "اِلَّا مَنِ ارۡتَضٰى مِنۡ رَّسُوۡلٍ فَاِنَّهٗ يَسۡلُكُ مِنۡۢ بَيۡنِ يَدَيۡهِ وَمِنۡ خَلۡفِهٖ رَصَدًا ۙ‏"},
"5545": {"surat": 72, "ayat": 29, "teks": "لِّيَـعۡلَمَ اَنۡ قَدۡ اَبۡلَغُوۡا رِسٰلٰتِ رَبِّهِمۡ وَاَحَاطَ بِمَا لَدَيۡهِمۡ  وَاَحۡصٰى كُلَّ شَىۡءٍ عَدَدًا‏"},
"5546": {"surat": 73, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5547": {"surat": 73, "ayat": 2, "teks": "يٰۤاَيُّهَا الۡمُزَّمِّلُۙ‏"},
"5548": {"surat": 73, "ayat": 3, "teks": "قُمِ الَّيۡلَ اِلَّا قَلِيۡلًا ۙ‏"},
"5549": {"surat": 73, "ayat": 4, "teks": "نِّصۡفَهٗۤ اَوِ انْقُصۡ مِنۡهُ قَلِيۡلًا ۙ‏"},
"5550": {"surat": 73, "ayat": 5, "teks": "اَوۡ زِدۡ عَلَيۡهِ وَرَتِّلِ الۡقُرۡاٰنَ تَرۡتِيۡلًا ؕ‏"},
"5551": {"surat": 73, "ayat": 6, "teks": "اِنَّا سَنُلۡقِىۡ عَلَيۡكَ قَوۡلًا ثَقِيۡلًا‏"},
"5552": {"surat": 73, "ayat": 7, "teks": "اِنَّ نَاشِئَةَ الَّيۡلِ هِىَ اَشَدُّ وَطۡـاً وَّاَقۡوَمُ قِيۡلًا ؕ‏"},
"5553": {"surat": 73, "ayat": 8, "teks": "اِنَّ لَـكَ فِى النَّهَارِ سَبۡحًا طَوِيۡلًا ؕ‏"},
"5554": {"surat": 73, "ayat": 9, "teks": "وَاذۡكُرِ اسۡمَ رَبِّكَ وَتَبَتَّلۡ اِلَيۡهِ تَبۡتِيۡلًا ؕ‏"},
"5555": {"surat": 73, "ayat": 10, "teks": "رَبُّ الۡمَشۡرِقِ وَالۡمَغۡرِبِ لَاۤ اِلٰهَ اِلَّا هُوَ فَاتَّخِذۡهُ وَكِيۡلًا‏"},
"5556": {"surat": 73, "ayat": 11, "teks": "وَاصۡبِرۡ عَلٰى مَا يَقُوۡلُوۡنَ وَاهۡجُرۡهُمۡ هَجۡرًا جَمِيۡلًا‏"},
"5557": {"surat": 73, "ayat": 12, "teks": "وَذَرۡنِىۡ وَالۡمُكَذِّبِيۡنَ اُولِى النَّعۡمَةِ وَمَهِّلۡهُمۡ قَلِيۡلًا‏"},
"5558": {"surat": 73, "ayat": 13, "teks": "اِنَّ لَدَيۡنَاۤ اَنۡـكَالًا وَّجَحِيۡمًا ۙ‏"},
"5559": {"surat": 73, "ayat": 14, "teks": "وَّطَعَامًا ذَا غُصَّةٍ وَّعَذَابًا اَلِيۡمًا‏"},
"5560": {"surat": 73, "ayat": 15, "teks": "يَوۡمَ تَرۡجُفُ الۡاَرۡضُ وَالۡجِبَالُ وَكَانَتِ الۡجِبَالُ كَثِيۡبًا مَّهِيۡلًا‏"},
"5561": {"surat": 73, "ayat": 16, "teks": "اِنَّاۤ اَرۡسَلۡنَاۤ اِلَيۡكُمۡ رَسُوۡلًا ۙ شَاهِدًا عَلَيۡكُمۡ كَمَاۤ اَرۡسَلۡنَاۤ اِلٰى فِرۡعَوۡنَ رَسُوۡلًا ؕ‏"},
"5562": {"surat": 73, "ayat": 17, "teks": "فَعَصٰى فِرۡعَوۡنُ الرَّسُوۡلَ فَاَخَذۡنٰهُ اَخۡذًا وَّبِيۡلًا‏"},
"5563": {"surat": 73, "ayat": 18, "teks": "فَكَيۡفَ تَتَّقُوۡنَ اِنۡ كَفَرۡتُمۡ يَوۡمًا يَّجۡعَلُ الۡوِلۡدَانَ شِيۡبَا  ۖ‏"},
"5564": {"surat": 73, "ayat": 19, "teks": "اۨلسَّمَآءُ مُنۡفَطِرٌ ۢ بِهٖ‌ؕ كَانَ وَعۡدُهٗ مَفۡعُوۡلًا‏"},
"5565": {"surat": 73, "ayat": 20, "teks": "اِنَّ هٰذِهٖ تَذۡكِرَةٌ ‌ ۚ فَمَنۡ شَآءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيۡلًا‏"},
"5566": {"surat": 73, "ayat": 21, "teks": "اِنَّ رَبَّكَ يَعۡلَمُ اَنَّكَ تَقُوۡمُ اَدۡنىٰ مِنۡ ثُلُثَىِ الَّيۡلِ وَ نِصۡفَهٗ وَثُلُثَهٗ وَطَآٮِٕفَةٌ مِّنَ الَّذِيۡنَ مَعَكَ‌ؕ وَاللّٰهُ يُقَدِّرُ الَّيۡلَ وَالنَّهَارَ‌ؕ عَلِمَ اَنۡ لَّنۡ تُحۡصُوۡهُ فَتَابَ عَلَيۡكُمۡ‌ فَاقۡرَءُوۡا مَا تَيَسَّرَ مِنَ الۡقُرۡاٰنِ‌ؕ عَلِمَ اَنۡ سَيَكُوۡنُ مِنۡكُمۡ مَّرۡضٰى‌ۙ وَاٰخَرُوۡنَ يَضۡرِبُوۡنَ فِى الۡاَرۡضِ يَبۡتَغُوۡنَ مِنۡ فَضۡلِ اللّٰهِ‌ۙ وَاٰخَرُوۡنَ يُقَاتِلُوۡنَ فِىۡ سَبِيۡلِ اللّٰهِ  ۖ فَاقۡرَءُوۡا مَا تَيَسَّرَ مِنۡهُ‌ ۙ وَاَقِيۡمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَقۡرِضُوا اللّٰهَ قَرۡضًا حَسَنًا‌ ؕ وَمَا تُقَدِّمُوۡا لِاَنۡفُسِكُمۡ مِّنۡ خَيۡرٍ تَجِدُوۡهُ عِنۡدَ اللّٰهِ هُوَ خَيۡرًا وَّاَعۡظَمَ اَجۡرًا‌ ؕ وَاسۡتَغۡفِرُوا اللّٰهَ ‌ؕ اِنَّ اللّٰهَ غَفُوۡرٌ رَّحِيۡمٌ‏"},
"5567": {"surat": 74, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5568": {"surat": 74, "ayat": 2, "teks": "يٰۤاَيُّهَا الۡمُدَّثِّرُۙ‏"},
"5569": {"surat": 74, "ayat": 3, "teks": "قُمۡ فَاَنۡذِرۡۙ‏"},
"5570": {"surat": 74, "ayat": 4, "teks": "وَرَبَّكَ فَكَبِّرۡۙ‏"},
"5571": {"surat": 74, "ayat": 5, "teks": "وَثِيَابَكَ  فَطَهِّرۡۙ‏"},
"5572": {"surat": 74, "ayat": 6, "teks": "وَالرُّجۡزَ فَاهۡجُرۡۙ‏"},
"5573": {"surat": 74, "ayat": 7, "teks": "وَلَا تَمۡنُنۡ تَسۡتَكۡثِرُۙ‏"},
"5574": {"surat": 74, "ayat": 8, "teks": "وَ لِرَبِّكَ فَاصۡبِرۡؕ‏"},
"5575": {"surat": 74, "ayat": 9, "teks": "فَاِذَا نُقِرَ فِى النَّاقُوۡرِۙ‏"},
"5576": {"surat": 74, "ayat": 10, "teks": "فَذٰلِكَ يَوۡمَٮِٕذٍ  يَّوۡمٌ عَسِيۡرٌۙ‏"},
"5577": {"surat": 74, "ayat": 11, "teks": "عَلَى الۡكٰفِرِيۡنَ غَيۡرُ يَسِيۡرٍ‏"},
"5578": {"surat": 74, "ayat": 12, "teks": "ذَرۡنِىۡ وَمَنۡ خَلَقۡتُ وَحِيۡدًا ۙ‏"},
"5579": {"surat": 74, "ayat": 13, "teks": "وَّجَعَلۡتُ لَهٗ مَالًا مَّمۡدُوۡدًا ۙ‏"},
"5580": {"surat": 74, "ayat": 14, "teks": "وَّبَنِيۡنَ شُهُوۡدًا ۙ‏"},
"5581": {"surat": 74, "ayat": 15, "teks": "وَّمَهَّدتُّ لَهٗ تَمۡهِيۡدًا ۙ‏"},
"5582": {"surat": 74, "ayat": 16, "teks": "ثُمَّ يَطۡمَعُ اَنۡ اَزِيۡدَ  ۙ‏"},
"5583": {"surat": 74, "ayat": 17, "teks": "كَلَّا  ؕ اِنَّهٗ كَانَ لِاٰيٰتِنَا عَنِيۡدًا ؕ‏"},
"5584": {"surat": 74, "ayat": 18, "teks": "سَاُرۡهِقُهٗ صَعُوۡدًا ؕ‏"},
"5585": {"surat": 74, "ayat": 19, "teks": "اِنَّهٗ فَكَّرَ وَقَدَّرَۙ‏"},
"5586": {"surat": 74, "ayat": 20, "teks": "فَقُتِلَ كَيۡفَ قَدَّرَۙ‏"},
"5587": {"surat": 74, "ayat": 21, "teks": "ثُمَّ  قُتِلَ كَيۡفَ قَدَّرَۙ‏"},
"5588": {"surat": 74, "ayat": 22, "teks": "ثُمَّ نَظَرَۙ‏"},
"5589": {"surat": 74, "ayat": 23, "teks": "ثُمَّ عَبَسَ وَبَسَرَۙ‏"},
"5590": {"surat": 74, "ayat": 24, "teks": "ثُمَّ اَدۡبَرَ وَاسۡتَكۡبَرَۙ‏"},
"5591": {"surat": 74, "ayat": 25, "teks": "فَقَالَ اِنۡ هٰذَاۤ اِلَّا سِحۡرٌ  يُّؤۡثَرُۙ‏"},
"5592": {"surat": 74, "ayat": 26, "teks": "اِنۡ هٰذَاۤ اِلَّا قَوۡلُ الۡبَشَرِؕ‏"},
"5593": {"surat": 74, "ayat": 27, "teks": "سَاُصۡلِيۡهِ  سَقَرَ‏"},
"5594": {"surat": 74, "ayat": 28, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا سَقَرُؕ‏"},
"5595": {"surat": 74, "ayat": 29, "teks": "لَا تُبۡقِىۡ وَ لَا تَذَرُ‌ۚ‏"},
"5596": {"surat": 74, "ayat": 30, "teks": "لَـوَّاحَةٌ لِّلۡبَشَرِ‌ۖ‌ۚ‏"},
"5597": {"surat": 74, "ayat": 31, "teks": "عَلَيۡهَا تِسۡعَةَ عَشَرَؕ‏"},
"5598": {"surat": 74, "ayat": 32, "teks": "وَمَا جَعَلۡنَاۤ اَصۡحٰبَ النَّارِ اِلَّا مَلٰٓٮِٕكَةً‌ وَّمَا جَعَلۡنَا عِدَّتَهُمۡ اِلَّا فِتۡنَةً لِّلَّذِيۡنَ كَفَرُوۡا ۙ لِيَسۡتَيۡقِنَ الَّذِيۡنَ اُوۡتُوا الۡكِتٰبَ وَيَزۡدَادَ الَّذِيۡنَ اٰمَنُوۡۤا اِيۡمَانًا‌ وَّلَا يَرۡتَابَ الَّذِيۡنَ اُوۡتُوا الۡكِتٰبَ وَالۡمُؤۡمِنُوۡنَ‌ۙ وَلِيَقُوۡلَ الَّذِيۡنَ فِىۡ قُلُوۡبِهِمۡ مَّرَضٌ وَّالۡكٰفِرُوۡنَ مَاذَاۤ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًا  ‌ؕ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنۡ يَّشَآءُ وَيَهۡدِىۡ مَنۡ يَّشَآءُ  ‌ؕ وَمَا يَعۡلَمُ جُنُوۡدَ رَبِّكَ اِلَّا هُوَ ‌ؕ وَمَا هِىَ اِلَّا ذِكۡرٰى لِلۡبَشَرِ‏"},
"5599": {"surat": 74, "ayat": 33, "teks": "كَلَّا وَالۡقَمَرِۙ‏"},
"5600": {"surat": 74, "ayat": 34, "teks": "وَالَّيۡلِ اِذۡ اَدۡبَرَۙ‏"},
"5601": {"surat": 74, "ayat": 35, "teks": "وَالصُّبۡحِ اِذَاۤ اَسۡفَرَۙ‏"},
"5602": {"surat": 74, "ayat": 36, "teks": "اِنَّهَا لَاِحۡدَى  الۡكُبَرِۙ‏"},
"5603": {"surat": 74, "ayat": 37, "teks": "نَذِيۡرًا لِّلۡبَشَرِۙ‏"},
"5604": {"surat": 74, "ayat": 38, "teks": "لِمَنۡ شَآءَ مِنۡكُمۡ اَنۡ يَّتَقَدَّمَ  اَوۡ يَتَاَخَّرَؕ‏"},
"5605": {"surat": 74, "ayat": 39, "teks": "كُلُّ نَفۡسٍ ۢ بِمَا كَسَبَتۡ رَهِيۡنَةٌ ۙ‏"},
"5606": {"surat": 74, "ayat": 40, "teks": "اِلَّاۤ اَصۡحٰبَ الۡيَمِيۡنِۛ ؕ‏"},
"5607": {"surat": 74, "ayat": 41, "teks": "فِىۡ جَنّٰتٍ  ۛ يَتَسَآءَلُوۡنَۙ‏"},
"5608": {"surat": 74, "ayat": 42, "teks": "عَنِ الۡمُجۡرِمِيۡنَۙ‏"},
"5609": {"surat": 74, "ayat": 43, "teks": "مَا  سَلَـكَكُمۡ فِىۡ سَقَرَ‏"},
"5610": {"surat": 74, "ayat": 44, "teks": "قَالُوۡا لَمۡ نَكُ مِنَ الۡمُصَلِّيۡنَۙ‏"},
"5611": {"surat": 74, "ayat": 45, "teks": "وَلَمۡ  نَكُ نُطۡعِمُ الۡمِسۡكِيۡنَۙ‏"},
"5612": {"surat": 74, "ayat": 46, "teks": "وَكُنَّا نَخُوۡضُ مَعَ الۡخَـآٮِٕضِيۡنَۙ‏"},
"5613": {"surat": 74, "ayat": 47, "teks": "وَ كُنَّا نُكَذِّبُ بِيَوۡمِ الدِّيۡنِۙ‏"},
"5614": {"surat": 74, "ayat": 48, "teks": "حَتّٰٓى اَتٰٮنَا الۡيَقِيۡنُؕ‏"},
"5615": {"surat": 74, "ayat": 49, "teks": "فَمَا تَنۡفَعُهُمۡ  شَفَاعَةُ الشّٰفِعِيۡنَؕ‏"},
"5616": {"surat": 74, "ayat": 50, "teks": "فَمَا لَهُمۡ عَنِ التَّذۡكِرَةِ مُعۡرِضِيۡنَۙ‏"},
"5617": {"surat": 74, "ayat": 51, "teks": "كَاَنَّهُمۡ حُمُرٌ مُّسۡتَنۡفِرَةٌ ۙ‏"},
"5618": {"surat": 74, "ayat": 52, "teks": "فَرَّتۡ مِنۡ قَسۡوَرَةٍ ؕ‏"},
"5619": {"surat": 74, "ayat": 53, "teks": "بَلۡ يُرِيۡدُ كُلُّ امۡرِىٴٍ مِّنۡهُمۡ اَنۡ يُّؤۡتٰى صُحُفًا مُّنَشَّرَةً ۙ‏"},
"5620": {"surat": 74, "ayat": 54, "teks": "كَلَّا  ‌ؕ بَلۡ لَّا يَخَافُوۡنَ الۡاٰخِرَةَ ؕ‏"},
"5621": {"surat": 74, "ayat": 55, "teks": "كَلَّاۤ اِنَّهٗ تَذۡكِرَةٌ‌ ۚ‏"},
"5622": {"surat": 74, "ayat": 56, "teks": "فَمَنۡ شَآءَ ذَكَرَهٗ ؕ‏"},
"5623": {"surat": 74, "ayat": 57, "teks": "وَمَا يَذۡكُرُوۡنَ اِلَّاۤ اَنۡ يَّشَآءَ اللّٰهُ‌ ؕ هُوَ اَهۡلُ التَّقۡوٰى وَاَهۡلُ الۡمَغۡفِرَةِ‏"},
"5624": {"surat": 75, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5625": {"surat": 75, "ayat": 2, "teks": "لَاۤ اُقۡسِمُ بِيَوۡمِ الۡقِيٰمَةِۙ‏"},
"5626": {"surat": 75, "ayat": 3, "teks": "وَلَاۤ اُقۡسِمُ بِالنَّفۡسِ اللَّوَّامَةِؕ‏"},
"5627": {"surat": 75, "ayat": 4, "teks": "اَيَحۡسَبُ الۡاِنۡسَانُ اَلَّنۡ نَّجۡمَعَ عِظَامَهٗؕ‏"},
"5628": {"surat": 75, "ayat": 5, "teks": "بَلٰى قٰدِرِيۡنَ  عَلٰٓى اَنۡ نُّسَوِّىَ بَنَانَهٗ‏"},
"5629": {"surat": 75, "ayat": 6, "teks": "بَلۡ يُرِيۡدُ الۡاِنۡسَانُ لِيَفۡجُرَ  اَمَامَهٗ‌ۚ‏"},
"5630": {"surat": 75, "ayat": 7, "teks": "يَسۡـَٔـلُ اَيَّانَ يَوۡمُ الۡقِيٰمَةِؕ‏"},
"5631": {"surat": 75, "ayat": 8, "teks": "فَاِذَا بَرِقَ  الۡبَصَرُۙ‏"},
"5632": {"surat": 75, "ayat": 9, "teks": "وَخَسَفَ الۡقَمَرُۙ‏"},
"5633": {"surat": 75, "ayat": 10, "teks": "وَجُمِعَ الشَّمۡسُ وَالۡقَمَرُۙ‏"},
"5634": {"surat": 75, "ayat": 11, "teks": "يَقُوۡلُ الۡاِنۡسَانُ يَوۡمَٮِٕذٍ اَيۡنَ الۡمَفَرُّ‌ ۚ‏"},
"5635": {"surat": 75, "ayat": 12, "teks": "كَلَّا لَا وَزَرَؕ‏"},
"5636": {"surat": 75, "ayat": 13, "teks": "اِلٰى رَبِّكَ يَوۡمَٮِٕذِ اۨلۡمُسۡتَقَرُّ ؕ‏"},
"5637": {"surat": 75, "ayat": 14, "teks": "يُنَبَّؤُا الۡاِنۡسَانُ يَوۡمَٮِٕذٍۢ بِمَا قَدَّمَ وَاَخَّرَؕ‏"},
"5638": {"surat": 75, "ayat": 15, "teks": "بَلِ الۡاِنۡسَانُ عَلٰى نَفۡسِهٖ بَصِيۡرَةٌ ۙ‏"},
"5639": {"surat": 75, "ayat": 16, "teks": "وَّلَوۡ اَلۡقٰى مَعَاذِيۡرَهٗؕ‏"},
"5640": {"surat": 75, "ayat": 17, "teks": "لَا تُحَرِّكۡ بِهٖ لِسَانَكَ لِتَعۡجَلَ  بِهٖؕ‏"},
"5641": {"surat": 75, "ayat": 18, "teks": "اِنَّ عَلَيۡنَا جَمۡعَهٗ وَقُرۡاٰنَهٗۚ  ۖ‏"},
"5642": {"surat": 75, "ayat": 19, "teks": "فَاِذَا قَرَاۡنٰهُ فَاتَّبِعۡ  قُرۡاٰنَهٗ‌ۚ‏"},
"5643": {"surat": 75, "ayat": 20, "teks": "ثُمَّ اِنَّ عَلَيۡنَا بَيَانَهٗؕ‏"},
"5644": {"surat": 75, "ayat": 21, "teks": "كَلَّا بَلۡ تُحِبُّوۡنَ الۡعَاجِلَةَ ۙ‏"},
"5645": {"surat": 75, "ayat": 22, "teks": "وَتَذَرُوۡنَ الۡاٰخِرَةَ ؕ‏"},
"5646": {"surat": 75, "ayat": 23, "teks": "وُجُوۡهٌ يَّوۡمَٮِٕذٍ نَّاضِرَةٌ ۙ‏"},
"5647": {"surat": 75, "ayat": 24, "teks": "اِلٰى رَبِّهَا نَاظِرَةٌ‌ ۚ‏"},
"5648": {"surat": 75, "ayat": 25, "teks": "وَوُجُوۡهٌ يَّوۡمَٮِٕذٍۢ بَاسِرَةٌ ۙ‏"},
"5649": {"surat": 75, "ayat": 26, "teks": "تَظُنُّ اَنۡ يُّفۡعَلَ بِهَا فَاقِرَةٌ ؕ‏"},
"5650": {"surat": 75, "ayat": 27, "teks": "كَلَّاۤ اِذَا بَلَغَتِ التَّرَاقِىَۙ‏"},
"5651": {"surat": 75, "ayat": 28, "teks": "وَقِيۡلَ مَنۡ؄ رَاقٍۙ‏"},
"5652": {"surat": 75, "ayat": 29, "teks": "وَّظَنَّ اَنَّهُ الۡفِرَاقُۙ‏"},
"5653": {"surat": 75, "ayat": 30, "teks": "وَالۡتَفَّتِ  السَّاقُ بِالسَّاقِۙ‏"},
"5654": {"surat": 75, "ayat": 31, "teks": "اِلٰى رَبِّكَ يَوۡمَٮِٕذِ اۨلۡمَسَاقُؕ‏"},
"5655": {"surat": 75, "ayat": 32, "teks": "فَلَا صَدَّقَ وَلَا صَلّٰىۙ‏"},
"5656": {"surat": 75, "ayat": 33, "teks": "وَلٰڪِنۡ كَذَّبَ وَتَوَلّٰىۙ‏"},
"5657": {"surat": 75, "ayat": 34, "teks": "ثُمَّ ذَهَبَ اِلٰٓى اَهۡلِهٖ يَتَمَطّٰىؕ‏"},
"5658": {"surat": 75, "ayat": 35, "teks": "اَوۡلٰى لَكَ فَاَوۡلٰىۙ‏"},
"5659": {"surat": 75, "ayat": 36, "teks": "ثُمَّ اَوۡلٰى لَكَ فَاَوۡلٰىؕ‏"},
"5660": {"surat": 75, "ayat": 37, "teks": "اَيَحۡسَبُ الۡاِنۡسَانُ اَنۡ  يُّتۡرَكَ سُدًىؕ‏"},
"5661": {"surat": 75, "ayat": 38, "teks": "اَلَمۡ يَكُ نُطۡفَةً مِّنۡ مَّنِىٍّ يُّمۡنٰىۙ‏"},
"5662": {"surat": 75, "ayat": 39, "teks": "ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ‏"},
"5663": {"surat": 75, "ayat": 40, "teks": "فَجَعَلَ مِنۡهُ الزَّوۡجَيۡنِ  الذَّكَرَ وَالۡاُنۡثٰىؕ‏"},
"5664": {"surat": 75, "ayat": 41, "teks": "اَلَيۡسَ ذٰلِكَ بِقٰدِرٍ عَلٰٓى اَنۡ يُّحۡـىِۦَ الۡمَوۡتٰى‏"},
"5665": {"surat": 76, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5666": {"surat": 76, "ayat": 2, "teks": "هَلۡ اَتٰى عَلَى الۡاِنۡسَانِ حِيۡنٌ مِّنَ الدَّهۡرِ لَمۡ يَكُنۡ شَيۡـٴً۬ـا  مَّذۡكُوۡرًا‏"},
"5667": {"surat": 76, "ayat": 3, "teks": "اِنَّا خَلَقۡنَا الۡاِنۡسَانَ مِنۡ نُّطۡفَةٍ اَمۡشَاجٍۖ نَّبۡتَلِيۡهِ فَجَعَلۡنٰهُ  سَمِيۡعًۢا بَصِيۡرًا‏"},
"5668": {"surat": 76, "ayat": 4, "teks": "اِنَّا هَدَيۡنٰهُ السَّبِيۡلَ اِمَّا شَاكِرًا وَّاِمَّا  كَفُوۡرًا‏"},
"5669": {"surat": 76, "ayat": 5, "teks": "اِنَّاۤ اَعۡتَدۡنَا لِلۡكٰفِرِيۡنَ سَلٰسِلَا۟ وَاَغۡلٰلًا وَّسَعِيۡرًا‏"},
"5670": {"surat": 76, "ayat": 6, "teks": "اِنَّ الۡاَبۡرَارَ يَشۡرَبُوۡنَ مِنۡ كَاۡسٍ كَانَ مِزَاجُهَا كَافُوۡرًا‌ۚ‏"},
"5671": {"surat": 76, "ayat": 7, "teks": "عَيۡنًا يَّشۡرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُوۡنَهَا تَفۡجِيۡرًا‏"},
"5672": {"surat": 76, "ayat": 8, "teks": "يُوۡفُوۡنَ  بِالنَّذۡرِ وَيَخَافُوۡنَ يَوۡمًا كَانَ شَرُّهٗ مُسۡتَطِيۡرًا‏"},
"5673": {"surat": 76, "ayat": 9, "teks": "وَيُطۡعِمُوۡنَ  الطَّعَامَ عَلٰى حُبِّهٖ مِسۡكِيۡنًا وَّيَتِيۡمًا وَّاَسِيۡرًا‏"},
"5674": {"surat": 76, "ayat": 10, "teks": "اِنَّمَا نُطۡعِمُكُمۡ  لِـوَجۡهِ اللّٰهِ لَا نُرِيۡدُ مِنۡكُمۡ جَزَآءً وَّلَا شُكُوۡرًا‏"},
"5675": {"surat": 76, "ayat": 11, "teks": "اِنَّا نَخَافُ مِنۡ  رَّبِّنَا يَوۡمًا عَبُوۡسًا قَمۡطَرِيۡرًا‏"},
"5676": {"surat": 76, "ayat": 12, "teks": "فَوَقٰٮهُمُ اللّٰهُ شَرَّ ذٰلِكَ الۡيَوۡمِ وَ لَقّٰٮهُمۡ نَضۡرَةً وَّسُرُوۡرًا‌ۚ‏"},
"5677": {"surat": 76, "ayat": 13, "teks": "وَجَزٰٮهُمۡ بِمَا صَبَرُوۡا جَنَّةً وَّحَرِيۡرًا ۙ‏"},
"5678": {"surat": 76, "ayat": 14, "teks": "مُّتَّكِـِٕـيۡنَ فِيۡهَا عَلَى الۡاَرَآٮِٕكِ‌ۚ لَا يَرَوۡنَ فِيۡهَا شَمۡسًا وَّلَا  زَمۡهَرِيۡرًا‌ۚ‏"},
"5679": {"surat": 76, "ayat": 15, "teks": "وَدَانِيَةً عَلَيۡهِمۡ ظِلٰلُهَا وَذُلِّلَتۡ قُطُوۡفُهَا تَذۡلِيۡلًا‏"},
"5680": {"surat": 76, "ayat": 16, "teks": "وَيُطَافُ عَلَيۡهِمۡ بِاٰنِيَةٍ مِّنۡ فِضَّةٍ وَّاَكۡوَابٍ كَانَتۡ قَوَارِيۡرَا۟ؔ ۙ‏"},
"5681": {"surat": 76, "ayat": 17, "teks": "قَوَارِيۡرَا۟ؔ مِنۡ فِضَّةٍ قَدَّرُوۡهَا تَقۡدِيۡرًا‏"},
"5682": {"surat": 76, "ayat": 18, "teks": "‌ ‌ وَيُسۡقَوۡنَ فِيۡهَا كَاۡسًا كَانَ مِزَاجُهَا زَنۡجَبِيۡلًا ۚ‏"},
"5683": {"surat": 76, "ayat": 19, "teks": "عَيۡنًا فِيۡهَا تُسَمّٰى سَلۡسَبِيۡلًا‏"},
"5684": {"surat": 76, "ayat": 20, "teks": "وَيَطُوۡفُ  عَلَيۡهِمۡ وِلۡدَانٌ مُّخَلَّدُوۡنَ‌ۚ اِذَا رَاَيۡتَهُمۡ حَسِبۡتَهُمۡ لُـؤۡلُـؤًا  مَّنۡثُوۡرًا‏"},
"5685": {"surat": 76, "ayat": 21, "teks": "وَاِذَا رَاَيۡتَ ثَمَّ رَاَيۡتَ نَعِيۡمًا وَّمُلۡكًا كَبِيۡرًا‏"},
"5686": {"surat": 76, "ayat": 22, "teks": "عٰلِيَهُمۡ ثِيَابُ سُنۡدُسٍ خُضۡرٌ وَّاِسۡتَبۡرَقٌ‌ وَّحُلُّوۡۤا اَسَاوِرَ  مِنۡ فِضَّةٍ ‌ۚوَسَقٰٮهُمۡ رَبُّهُمۡ شَرَابًا طَهُوۡرًا‏"},
"5687": {"surat": 76, "ayat": 23, "teks": "اِنَّ هٰذَا كَانَ  لَـكُمۡ جَزَآءً وَّكَانَ سَعۡيُكُمۡ مَّشۡكُوۡرًا‏"},
"5688": {"surat": 76, "ayat": 24, "teks": "اِنَّا نَحۡنُ نَزَّلۡنَا عَلَيۡكَ الۡقُرۡاٰنَ تَنۡزِيۡلًا ۚ‏"},
"5689": {"surat": 76, "ayat": 25, "teks": "فَاصۡبِرۡ لِحُكۡمِ رَبِّكَ وَلَا تُطِعۡ مِنۡهُمۡ اٰثِمًا  اَوۡ كَفُوۡرًا‌ۚ‏"},
"5690": {"surat": 76, "ayat": 26, "teks": "وَاذۡكُرِ اسۡمَ رَبِّكَ بُكۡرَةً وَّاَصِيۡلًا ۚ  ۖ‏"},
"5691": {"surat": 76, "ayat": 27, "teks": "وَمِنَ الَّيۡلِ فَاسۡجُدۡ لَهٗ وَسَبِّحۡهُ لَيۡلًا طَوِيۡلًا‏"},
"5692": {"surat": 76, "ayat": 28, "teks": "اِنَّ هٰٓؤُلَاۤءِ يُحِبُّوۡنَ الۡعَاجِلَةَ وَيَذَرُوۡنَ وَرَآءَهُمۡ يَوۡمًا ثَقِيۡلًا‏"},
"5693": {"surat": 76, "ayat": 29, "teks": "نَحۡنُ خَلَقۡنٰهُمۡ وَشَدَدۡنَاۤ اَسۡرَهُمۡ‌ۚ وَاِذَا شِئۡنَا بَدَّلۡنَاۤ اَمۡثَالَهُمۡ تَبۡدِيۡلًا‏"},
"5694": {"surat": 76, "ayat": 30, "teks": "اِنَّ هٰذِهٖ تَذۡكِرَةٌ   ‌ۚ فَمَنۡ شَآءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيۡلًا‏"},
"5695": {"surat": 76, "ayat": 31, "teks": "وَمَا تَشَآءُوۡنَ اِلَّاۤ اَنۡ يَّشَآءَ اللّٰهُ ‌ؕ اِنَّ اللّٰهَ كَانَ عَلِيۡمًا حَكِيۡمًا  ۖ‏"},
"5696": {"surat": 76, "ayat": 32, "teks": "يُّدۡخِلُ مَنۡ يَّشَآءُ فِىۡ رَحۡمَتِهٖ‌ؕ وَالظّٰلِمِيۡنَ اَعَدَّ لَهُمۡ  عَذَابًا اَلِيۡمًا‏"},
"5697": {"surat": 77, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5698": {"surat": 77, "ayat": 2, "teks": "وَالۡمُرۡسَلٰتِ عُرۡفًا ۙ‏"},
"5699": {"surat": 77, "ayat": 3, "teks": "فَالۡعٰصِفٰتِ عَصۡفًا ۙ‏"},
"5700": {"surat": 77, "ayat": 4, "teks": "وَّالنّٰشِرٰتِ نَشۡرًا ۙ‏"},
"5701": {"surat": 77, "ayat": 5, "teks": "فَالۡفٰرِقٰتِ فَرۡقًا ۙ‏"},
"5702": {"surat": 77, "ayat": 6, "teks": "فَالۡمُلۡقِيٰتِ ذِكۡرًا ۙ‏"},
"5703": {"surat": 77, "ayat": 7, "teks": "عُذۡرًا اَوۡ نُذۡرًا ۙ‏"},
"5704": {"surat": 77, "ayat": 8, "teks": "اِنَّمَا تُوۡعَدُوۡنَ لَوَاقِعٌ ؕ‏"},
"5705": {"surat": 77, "ayat": 9, "teks": "فَاِذَا النُّجُوۡمُ طُمِسَتۡۙ‏"},
"5706": {"surat": 77, "ayat": 10, "teks": "وَ اِذَا السَّمَآءُ فُرِجَتۡۙ‏"},
"5707": {"surat": 77, "ayat": 11, "teks": "وَاِذَا الۡجِبَالُ نُسِفَتۡۙ‏"},
"5708": {"surat": 77, "ayat": 12, "teks": "وَاِذَا الرُّسُلُ  اُقِّتَتۡؕ‏"},
"5709": {"surat": 77, "ayat": 13, "teks": "لِاَىِّ يَوۡمٍ اُجِّلَتۡؕ‏"},
"5710": {"surat": 77, "ayat": 14, "teks": "لِيَوۡمِ الۡفَصۡلِ‌ۚ‏"},
"5711": {"surat": 77, "ayat": 15, "teks": "وَمَاۤ اَدۡرٰٮكَ  مَا يَوۡمُ الۡفَصۡلِؕ‏"},
"5712": {"surat": 77, "ayat": 16, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5713": {"surat": 77, "ayat": 17, "teks": "اَلَمۡ  نُهۡلِكِ الۡاَوَّلِيۡنَؕ‏"},
"5714": {"surat": 77, "ayat": 18, "teks": "ثُمَّ نُتۡبِعُهُمُ الۡاٰخِرِيۡنَ‏"},
"5715": {"surat": 77, "ayat": 19, "teks": "كَذٰلِكَ  نَفۡعَلُ بِالۡمُجۡرِمِيۡنَ‏"},
"5716": {"surat": 77, "ayat": 20, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5717": {"surat": 77, "ayat": 21, "teks": "اَلَمۡ نَخۡلُقۡكُّمۡ مِّنۡ مَّآءٍ مَّهِيۡنٍۙ‏"},
"5718": {"surat": 77, "ayat": 22, "teks": "فَجَعَلۡنٰهُ فِىۡ قَرَارٍ مَّكِيۡنٍۙ‏"},
"5719": {"surat": 77, "ayat": 23, "teks": "اِلٰى قَدَرٍ مَّعۡلُوۡمٍۙ‏"},
"5720": {"surat": 77, "ayat": 24, "teks": "فَقَدَرۡنَاۖ فَنِعۡمَ الۡقٰدِرُوۡنَ‏"},
"5721": {"surat": 77, "ayat": 25, "teks": "وَيۡلٌ  يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5722": {"surat": 77, "ayat": 26, "teks": "اَلَمۡ نَجۡعَلِ الۡاَرۡضَ كِفَاتًا ۙ‏"},
"5723": {"surat": 77, "ayat": 27, "teks": "اَحۡيَآءً وَّاَمۡوَاتًا ۙ‏"},
"5724": {"surat": 77, "ayat": 28, "teks": "وَّجَعَلۡنَا فِيۡهَا رَوَاسِىَ شٰمِخٰتٍ وَّ اَسۡقَيۡنٰكُمۡ مَّآءً فُرَاتًا ؕ‏"},
"5725": {"surat": 77, "ayat": 29, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5726": {"surat": 77, "ayat": 30, "teks": "اِنْطَلِقُوۡۤا اِلٰى مَا كُنۡتُمۡ بِهٖ تُكَذِّبُوۡنَ‌ۚ‏"},
"5727": {"surat": 77, "ayat": 31, "teks": "اِنْطَلِقُوۡۤا اِلٰى ظِلٍّ  ذِىۡ ثَلٰثِ شُعَبٍۙ‏"},
"5728": {"surat": 77, "ayat": 32, "teks": "لَّا ظَلِيۡلٍ وَّلَا يُغۡنِىۡ مِنَ اللَّهَبِؕ‏"},
"5729": {"surat": 77, "ayat": 33, "teks": "اِنَّهَا تَرۡمِىۡ بِشَرَرٍ كَالۡقَصۡرِ‌ۚ‏"},
"5730": {"surat": 77, "ayat": 34, "teks": "كَاَنَّهٗ جِمٰلَتٌ صُفۡرٌ ؕ‏"},
"5731": {"surat": 77, "ayat": 35, "teks": "وَيۡلٌ  يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5732": {"surat": 77, "ayat": 36, "teks": "هٰذَا يَوۡمُ لَا يَنۡطِقُوۡنَۙ‏"},
"5733": {"surat": 77, "ayat": 37, "teks": "وَلَا يُؤۡذَنُ لَهُمۡ فَيَـعۡتَذِرُوۡنَ‏"},
"5734": {"surat": 77, "ayat": 38, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5735": {"surat": 77, "ayat": 39, "teks": "هٰذَا يَوۡمُ الۡفَصۡلِ‌ۚ جَمَعۡنٰكُمۡ وَالۡاَوَّلِيۡنَ‏"},
"5736": {"surat": 77, "ayat": 40, "teks": "فَاِنۡ كَانَ لَـكُمۡ  كَيۡدٌ فَكِيۡدُوۡنِ‏"},
"5737": {"surat": 77, "ayat": 41, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5738": {"surat": 77, "ayat": 42, "teks": "اِنَّ  الۡمُتَّقِيۡنَ فِىۡ ظِلٰلٍ وَّعُيُوۡنٍۙ‏"},
"5739": {"surat": 77, "ayat": 43, "teks": "وَّفَوَاكِهَ مِمَّا يَشۡتَهُوۡنَؕ‏"},
"5740": {"surat": 77, "ayat": 44, "teks": "كُلُوۡا وَاشۡرَبُوۡا هَنِيْٓئًا ۢ بِمَا كُنۡتُمۡ تَعۡمَلُوۡنَ‏"},
"5741": {"surat": 77, "ayat": 45, "teks": "اِنَّا كَذٰلِكَ نَجۡزِى الۡمُحۡسِنِيۡنَ‏"},
"5742": {"surat": 77, "ayat": 46, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5743": {"surat": 77, "ayat": 47, "teks": "كُلُوۡا وَتَمَتَّعُوۡا قَلِيۡلًا اِنَّكُمۡ مُّجۡرِمُوۡنَ‏"},
"5744": {"surat": 77, "ayat": 48, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ  لِّلۡمُكَذِّبِيۡنَ‏"},
"5745": {"surat": 77, "ayat": 49, "teks": "وَاِذَا قِيۡلَ لَهُمُ ارۡكَعُوۡا لَا يَرۡكَعُوۡنَ‏"},
"5746": {"surat": 77, "ayat": 50, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَ‏"},
"5747": {"surat": 77, "ayat": 51, "teks": "فَبِاَىِّ حَدِيۡثٍۢ بَعۡدَهٗ يُؤۡمِنُوۡنَ‏"},
"5748": {"surat": 78, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5749": {"surat": 78, "ayat": 2, "teks": "عَمَّ يَتَسَآءَلُوۡنَ‌ۚ‏"},
"5750": {"surat": 78, "ayat": 3, "teks": "عَنِ النَّبَاِ الۡعَظِيۡمِۙ‏"},
"5751": {"surat": 78, "ayat": 4, "teks": "الَّذِىۡ هُمۡ فِيۡهِ مُخۡتَلِفُوۡنَؕ‏"},
"5752": {"surat": 78, "ayat": 5, "teks": "كَلَّا سَيَعۡلَمُوۡنَۙ‏"},
"5753": {"surat": 78, "ayat": 6, "teks": "ثُمَّ كَلَّا سَيَعۡلَمُوۡنَ‏"},
"5754": {"surat": 78, "ayat": 7, "teks": "اَلَمۡ نَجۡعَلِ الۡاَرۡضَ مِهٰدًا ۙ‏"},
"5755": {"surat": 78, "ayat": 8, "teks": "وَّالۡجِبَالَ اَوۡتَادًاۙ‏"},
"5756": {"surat": 78, "ayat": 9, "teks": "وَّخَلَقۡنٰكُمۡ اَزۡوَاجًا ۙ‏"},
"5757": {"surat": 78, "ayat": 10, "teks": "وَّجَعَلۡنَا نَوۡمَكُمۡ سُبَاتًا ۙ‏"},
"5758": {"surat": 78, "ayat": 11, "teks": "وَّجَعَلۡنَا الَّيۡلَ لِبَاسًا ۙ‏"},
"5759": {"surat": 78, "ayat": 12, "teks": "وَّجَعَلۡنَا النَّهَارَ مَعَاشًا‏"},
"5760": {"surat": 78, "ayat": 13, "teks": "وَّبَنَيۡنَا فَوۡقَكُمۡ سَبۡعًا شِدَادًا ۙ‏"},
"5761": {"surat": 78, "ayat": 14, "teks": "وَّ جَعَلۡنَا سِرَاجًا وَّهَّاجًاۙ‏"},
"5762": {"surat": 78, "ayat": 15, "teks": "وَّاَنۡزَلۡنَا مِنَ الۡمُعۡصِرٰتِ مَآءً ثَجَّاجًا ۙ‏"},
"5763": {"surat": 78, "ayat": 16, "teks": "لِّـنُخۡرِجَ بِهٖ حَبًّا وَّنَبَاتًا ۙ‏"},
"5764": {"surat": 78, "ayat": 17, "teks": "وَّجَنّٰتٍ اَلۡفَافًا ؕ‏"},
"5765": {"surat": 78, "ayat": 18, "teks": "اِنَّ يَوۡمَ الۡفَصۡلِ كَانَ مِيۡقَاتًا ۙ‏"},
"5766": {"surat": 78, "ayat": 19, "teks": "يَّوۡمَ يُنۡفَخُ فِى الصُّوۡرِ فَتَاۡتُوۡنَ اَفۡوَاجًا ۙ‏"},
"5767": {"surat": 78, "ayat": 20, "teks": "وَّفُتِحَتِ السَّمَآءُ فَكَانَتۡ اَبۡوَابًا ۙ‏"},
"5768": {"surat": 78, "ayat": 21, "teks": "وَّ سُيِّرَتِ الۡجِبَالُ فَكَانَتۡ سَرَابًا ؕ‏"},
"5769": {"surat": 78, "ayat": 22, "teks": "اِنَّ جَهَنَّمَ كَانَتۡ مِرۡصَادًاۙ‏"},
"5770": {"surat": 78, "ayat": 23, "teks": "لِّلطّٰغِيۡنَ مَاٰبًا ۙ‏"},
"5771": {"surat": 78, "ayat": 24, "teks": "لّٰبِثِيۡنَ فِيۡهَاۤ اَحۡقَابًا‌ ۚ‏"},
"5772": {"surat": 78, "ayat": 25, "teks": "لَا يَذُوۡقُوۡنَ فِيۡهَا بَرۡدًا وَّلَا شَرَابًا ۙ‏"},
"5773": {"surat": 78, "ayat": 26, "teks": "اِلَّا حَمِيۡمًا وَّغَسَّاقًا ۙ‏"},
"5774": {"surat": 78, "ayat": 27, "teks": "جَزَآءً وِّفَاقًا ؕ‏"},
"5775": {"surat": 78, "ayat": 28, "teks": "اِنَّهُمۡ كَانُوۡا لَا يَرۡجُوۡنَ حِسَابًا ۙ‏"},
"5776": {"surat": 78, "ayat": 29, "teks": "وَّكَذَّبُوۡا بِاٰيٰتِنَا كِذَّابًا ؕ‏"},
"5777": {"surat": 78, "ayat": 30, "teks": "وَكُلَّ شَىۡءٍ اَحۡصَيۡنٰهُ كِتٰبًا ۙ‏"},
"5778": {"surat": 78, "ayat": 31, "teks": "فَذُوۡقُوۡا فَلَنۡ نَّزِيۡدَكُمۡ اِلَّا عَذَابًا‏"},
"5779": {"surat": 78, "ayat": 32, "teks": "اِنَّ لِلۡمُتَّقِيۡنَ مَفَازًا ۙ‏"},
"5780": {"surat": 78, "ayat": 33, "teks": "حَدَآٮِٕقَ وَاَعۡنَابًا ۙ‏"},
"5781": {"surat": 78, "ayat": 34, "teks": "وَّكَوَاعِبَ اَتۡرَابًا ۙ‏"},
"5782": {"surat": 78, "ayat": 35, "teks": "وَّكَاۡسًا دِهَاقًا ؕ‏"},
"5783": {"surat": 78, "ayat": 36, "teks": "لَا يَسۡمَعُوۡنَ فِيۡهَا لَـغۡوًا وَّلَا كِذّٰبًا‌ ۚ‏"},
"5784": {"surat": 78, "ayat": 37, "teks": "جَزَآءً مِّنۡ رَّبِّكَ عَطَآءً حِسَابًا ۙ‏"},
"5785": {"surat": 78, "ayat": 38, "teks": "رَّبِّ السَّمٰوٰتِ وَالۡاَرۡضِ وَمَا بَيۡنَهُمَا الرَّحۡمٰنِ‌ لَا يَمۡلِكُوۡنَ مِنۡهُ خِطَابًا‌ ۚ‏"},
"5786": {"surat": 78, "ayat": 39, "teks": "يَوۡمَ يَقُوۡمُ الرُّوۡحُ وَالۡمَلٰٓٮِٕكَةُ صَفًّاؕۙ لَّا يَتَكَلَّمُوۡنَ اِلَّا مَنۡ اَذِنَ لَهُ الرَّحۡمٰنُ وَقَالَ صَوَابًا‌‏"},
"5787": {"surat": 78, "ayat": 40, "teks": "ذٰلِكَ الۡيَوۡمُ الۡحَـقُّ‌ۚ فَمَنۡ شَآءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا‏"},
"5788": {"surat": 78, "ayat": 41, "teks": "اِنَّاۤ اَنۡذَرۡنٰـكُمۡ عَذَابًا قَرِيۡبًا ۖۚ  يَّوۡمَ يَنۡظُرُ الۡمَرۡءُ مَا قَدَّمَتۡ يَدٰهُ وَيَقُوۡلُ الۡـكٰفِرُ يٰلَيۡتَنِىۡ كُنۡتُ تُرٰبًا‏"},
"5789": {"surat": 79, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5790": {"surat": 79, "ayat": 2, "teks": "وَالنّٰزِعٰتِ غَرۡقًا ۙ‏"},
"5791": {"surat": 79, "ayat": 3, "teks": "وَّالنّٰشِطٰتِ نَشۡطًا ۙ‏"},
"5792": {"surat": 79, "ayat": 4, "teks": "وَّالسّٰبِحٰتِ سَبۡحًا ۙ‏"},
"5793": {"surat": 79, "ayat": 5, "teks": "فَالسّٰبِقٰتِ سَبۡقًا ۙ‏"},
"5794": {"surat": 79, "ayat": 6, "teks": "فَالۡمُدَبِّرٰتِ اَمۡرًا‌ ۘ‏"},
"5795": {"surat": 79, "ayat": 7, "teks": "يَوۡمَ تَرۡجُفُ الرَّاجِفَةُ ۙ‏"},
"5796": {"surat": 79, "ayat": 8, "teks": "تَتۡبَعُهَا الرَّادِفَةُ ؕ‏"},
"5797": {"surat": 79, "ayat": 9, "teks": "قُلُوۡبٌ يَّوۡمَٮِٕذٍ وَّاجِفَةٌ ۙ‏"},
"5798": {"surat": 79, "ayat": 10, "teks": "اَبۡصَارُهَا خَاشِعَةٌ‌ ۘ‏"},
"5799": {"surat": 79, "ayat": 11, "teks": "يَقُوۡلُوۡنَ ءَاِنَّا لَمَرۡدُوۡدُوۡنَ فِى الۡحَـافِرَةِ ؕ‏"},
"5800": {"surat": 79, "ayat": 12, "teks": "ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ؕ‏"},
"5801": {"surat": 79, "ayat": 13, "teks": "قَالُوۡا تِلۡكَ اِذًا كَرَّةٌ خَاسِرَةٌ‌ ۘ‏"},
"5802": {"surat": 79, "ayat": 14, "teks": "فَاِنَّمَا هِىَ زَجۡرَةٌ وَّاحِدَةٌ ۙ‏"},
"5803": {"surat": 79, "ayat": 15, "teks": "فَاِذَا هُمۡ بِالسَّاهِرَةِ ؕ‏"},
"5804": {"surat": 79, "ayat": 16, "teks": "هَلۡ اَتٰٮكَ حَدِيۡثُ مُوۡسٰى‌ۘ‏"},
"5805": {"surat": 79, "ayat": 17, "teks": "اِذۡ نَادٰٮهُ رَبُّهٗ بِالۡوَادِ الۡمُقَدَّسِ طُوًى‌ۚ‏"},
"5806": {"surat": 79, "ayat": 18, "teks": "اِذۡهَبۡ اِلٰى فِرۡعَوۡنَ اِنَّهٗ طَغٰى ۖ‏"},
"5807": {"surat": 79, "ayat": 19, "teks": "فَقُلۡ هَلۡ لَّكَ اِلٰٓى اَنۡ تَزَكّٰى ۙ‏"},
"5808": {"surat": 79, "ayat": 20, "teks": "وَاَهۡدِيَكَ اِلٰى رَبِّكَ فَتَخۡشٰى‌ۚ‏"},
"5809": {"surat": 79, "ayat": 21, "teks": "فَاَرٰٮهُ الۡاٰيَةَ الۡكُبۡرٰى ۖ‏"},
"5810": {"surat": 79, "ayat": 22, "teks": "فَكَذَّبَ وَعَصٰى ۖ‏"},
"5811": {"surat": 79, "ayat": 23, "teks": "ثُمَّ اَدۡبَرَ يَسۡعٰىۖ‏"},
"5812": {"surat": 79, "ayat": 24, "teks": "فَحَشَرَ فَنَادٰىۖ‏"},
"5813": {"surat": 79, "ayat": 25, "teks": "فَقَالَ اَنَا رَبُّكُمُ الۡاَعۡلٰى ۖ‏"},
"5814": {"surat": 79, "ayat": 26, "teks": "فَاَخَذَهُ اللّٰهُ نَڪَالَ الۡاٰخِرَةِ وَالۡاُوۡلٰى ؕ‏"},
"5815": {"surat": 79, "ayat": 27, "teks": "اِنَّ فِىۡ ذٰلِكَ لَعِبۡرَةً لِّمَنۡ يَّخۡشٰىؕ‏"},
"5816": {"surat": 79, "ayat": 28, "teks": "ءَاَنۡتُمۡ اَشَدُّ خَلۡقًا اَمِ السَّمَآءُ‌ ؕ بَنٰٮهَا‏"},
"5817": {"surat": 79, "ayat": 29, "teks": "رَفَعَ سَمۡكَهَا فَسَوَّٮهَا ۙ‏"},
"5818": {"surat": 79, "ayat": 30, "teks": "وَ اَغۡطَشَ لَيۡلَهَا وَاَخۡرَجَ ضُحٰٮهَا‏"},
"5819": {"surat": 79, "ayat": 31, "teks": "وَالۡاَرۡضَ بَعۡدَ ذٰلِكَ دَحٰٮهَا ؕ‏"},
"5820": {"surat": 79, "ayat": 32, "teks": "اَخۡرَجَ مِنۡهَا مَآءَهَا وَمَرۡعٰٮهَا‏"},
"5821": {"surat": 79, "ayat": 33, "teks": "وَالۡجِبَالَ اَرۡسٰٮهَا ۙ‏"},
"5822": {"surat": 79, "ayat": 34, "teks": "مَتَاعًا لَّـكُمۡ وَلِاَنۡعَامِكُمۡؕ‏"},
"5823": {"surat": 79, "ayat": 35, "teks": "فَاِذَا جَآءَتِ الطَّآمَّةُ الۡكُبۡرٰى ۖ‏"},
"5824": {"surat": 79, "ayat": 36, "teks": "يَوۡمَ يَتَذَكَّرُ الۡاِنۡسَانُ مَا سَعٰىۙ‏"},
"5825": {"surat": 79, "ayat": 37, "teks": "وَبُرِّزَتِ الۡجَحِيۡمُ لِمَنۡ يَّرٰى‏"},
"5826": {"surat": 79, "ayat": 38, "teks": "فَاَمَّا مَنۡ طَغٰىۙ‏"},
"5827": {"surat": 79, "ayat": 39, "teks": "وَاٰثَرَ الۡحَيٰوةَ الدُّنۡيَا ۙ‏"},
"5828": {"surat": 79, "ayat": 40, "teks": "فَاِنَّ الۡجَحِيۡمَ هِىَ الۡمَاۡوٰىؕ‏"},
"5829": {"surat": 79, "ayat": 41, "teks": "وَاَمَّا مَنۡ خَافَ مَقَامَ رَبِّهٖ وَ نَهَى النَّفۡسَ عَنِ الۡهَوٰىۙ‏"},
"5830": {"surat": 79, "ayat": 42, "teks": "فَاِنَّ الۡجَـنَّةَ هِىَ الۡمَاۡوٰىؕ‏"},
"5831": {"surat": 79, "ayat": 43, "teks": "يَسۡــٴَــلُوۡنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرۡسٰٮهَا ؕ‏"},
"5832": {"surat": 79, "ayat": 44, "teks": "فِيۡمَ اَنۡتَ مِنۡ ذِكۡرٰٮهَاؕ‏"},
"5833": {"surat": 79, "ayat": 45, "teks": "اِلٰى رَبِّكَ مُنۡتَهٰٮهَاؕ‏"},
"5834": {"surat": 79, "ayat": 46, "teks": "اِنَّمَاۤ اَنۡتَ مُنۡذِرُ مَنۡ يَّخۡشٰٮهَاؕ‏"},
"5835": {"surat": 79, "ayat": 47, "teks": "كَاَنَّهُمۡ يَوۡمَ يَرَوۡنَهَا لَمۡ يَلۡبَثُوۡۤا اِلَّا عَشِيَّةً اَوۡ ضُحٰٮهَا‏"},
"5836": {"surat": 80, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5837": {"surat": 80, "ayat": 2, "teks": "عَبَسَ وَتَوَلّٰٓىۙ‏"},
"5838": {"surat": 80, "ayat": 3, "teks": "اَنۡ جَآءَهُ الۡاَعۡمٰىؕ‏"},
"5839": {"surat": 80, "ayat": 4, "teks": "وَمَا يُدۡرِيۡكَ لَعَلَّهٗ يَزَّكّٰٓىۙ‏"},
"5840": {"surat": 80, "ayat": 5, "teks": "اَوۡ يَذَّكَّرُ فَتَنۡفَعَهُ الذِّكۡرٰىؕ‏"},
"5841": {"surat": 80, "ayat": 6, "teks": "اَمَّا مَنِ اسۡتَغۡنٰىۙ‏"},
"5842": {"surat": 80, "ayat": 7, "teks": "فَاَنۡتَ لَهٗ تَصَدّٰىؕ‏"},
"5843": {"surat": 80, "ayat": 8, "teks": "وَمَا عَلَيۡكَ اَلَّا يَزَّكّٰٓىؕ‏"},
"5844": {"surat": 80, "ayat": 9, "teks": "وَاَمَّا مَنۡ جَآءَكَ يَسۡعٰىۙ‏"},
"5845": {"surat": 80, "ayat": 10, "teks": "وَهُوَ يَخۡشٰىۙ‏"},
"5846": {"surat": 80, "ayat": 11, "teks": "فَاَنۡتَ عَنۡهُ تَلَهّٰى‌ۚ‏"},
"5847": {"surat": 80, "ayat": 12, "teks": "كَلَّاۤ اِنَّهَا تَذۡكِرَةٌ ۚ‏"},
"5848": {"surat": 80, "ayat": 13, "teks": "فَمَنۡ شَآءَ ذَكَرَهٗ‌ۘ‏"},
"5849": {"surat": 80, "ayat": 14, "teks": "فِىۡ صُحُفٍ مُّكَرَّمَةٍۙ‏"},
"5850": {"surat": 80, "ayat": 15, "teks": "مَّرۡفُوۡعَةٍ مُّطَهَّرَةٍ ۭۙ‏"},
"5851": {"surat": 80, "ayat": 16, "teks": "بِاَيۡدِىۡ سَفَرَةٍۙ‏"},
"5852": {"surat": 80, "ayat": 17, "teks": "كِرَامٍۢ بَرَرَةٍؕ‏"},
"5853": {"surat": 80, "ayat": 18, "teks": "قُتِلَ الۡاِنۡسَانُ مَاۤ اَكۡفَرَهٗؕ‏"},
"5854": {"surat": 80, "ayat": 19, "teks": "مِنۡ اَىِّ شَىۡءٍ خَلَقَهٗؕ‏"},
"5855": {"surat": 80, "ayat": 20, "teks": "مِنۡ نُّطۡفَةٍؕ خَلَقَهٗ فَقَدَّرَهٗ ۙ‏"},
"5856": {"surat": 80, "ayat": 21, "teks": "ثُمَّ السَّبِيۡلَ يَسَّرَهٗۙ‏"},
"5857": {"surat": 80, "ayat": 22, "teks": "ثُمَّ اَمَاتَهٗ فَاَقۡبَرَهٗۙ‏"},
"5858": {"surat": 80, "ayat": 23, "teks": "ثُمَّ اِذَا شَآءَ اَنۡشَرَهٗؕ‏"},
"5859": {"surat": 80, "ayat": 24, "teks": "كَلَّا لَـمَّا يَقۡضِ مَاۤ  اَمَرَهٗؕ‏"},
"5860": {"surat": 80, "ayat": 25, "teks": "فَلۡيَنۡظُرِ الۡاِنۡسَانُ اِلٰى طَعَامِهٖۤۙ‏"},
"5861": {"surat": 80, "ayat": 26, "teks": "اَنَّا صَبَبۡنَا الۡمَآءَ صَبًّا ۙ‏"},
"5862": {"surat": 80, "ayat": 27, "teks": "ثُمَّ شَقَقۡنَا الۡاَرۡضَ شَقًّا ۙ‏"},
"5863": {"surat": 80, "ayat": 28, "teks": "فَاَنۡۢبَتۡنَا فِيۡهَا حَبًّا ۙ‏"},
"5864": {"surat": 80, "ayat": 29, "teks": "وَّ عِنَبًا وَّقَضۡبًا ۙ‏"},
"5865": {"surat": 80, "ayat": 30, "teks": "وَّزَيۡتُوۡنًا وَّنَخۡلًا ؕ‏"},
"5866": {"surat": 80, "ayat": 31, "teks": "وَحَدَآٮِٕقَ غُلۡبًا ۙ‏"},
"5867": {"surat": 80, "ayat": 32, "teks": "وَّفَاكِهَةً وَّاَبًّا ۙ‏"},
"5868": {"surat": 80, "ayat": 33, "teks": "مَّتَاعًا لَّـكُمۡ وَلِاَنۡعَامِكُمۡؕ‏"},
"5869": {"surat": 80, "ayat": 34, "teks": "فَاِذَا جَآءَتِ الصَّآخَّةُ‏"},
"5870": {"surat": 80, "ayat": 35, "teks": "يَوۡمَ يَفِرُّ الۡمَرۡءُ مِنۡ اَخِيۡهِۙ‏"},
"5871": {"surat": 80, "ayat": 36, "teks": "وَاُمِّهٖ وَاَبِيۡهِۙ‏"},
"5872": {"surat": 80, "ayat": 37, "teks": "وَصَاحِبَتِهٖ  وَبَنِيۡهِؕ‏"},
"5873": {"surat": 80, "ayat": 38, "teks": "لِكُلِّ امۡرِیءٍ مِّنۡهُمۡ يَوۡمَٮِٕذٍ شَاۡنٌ يُّغۡنِيۡهِؕ‏"},
"5874": {"surat": 80, "ayat": 39, "teks": "وُجُوۡهٌ يَّوۡمَٮِٕذٍ مُّسۡفِرَةٌ ۙ‏"},
"5875": {"surat": 80, "ayat": 40, "teks": "ضَاحِكَةٌ مُّسۡتَبۡشِرَةٌ ۚ‏"},
"5876": {"surat": 80, "ayat": 41, "teks": "وَوُجُوۡهٌ يَّوۡمَٮِٕذٍ عَلَيۡهَا غَبَرَةٌ ۙ‏"},
"5877": {"surat": 80, "ayat": 42, "teks": "تَرۡهَقُهَا قَتَرَةٌ ؕ‏"},
"5878": {"surat": 80, "ayat": 43, "teks": "اُولٰٓٮِٕكَ هُمُ الۡكَفَرَةُ الۡفَجَرَةُ‏"},
"5879": {"surat": 81, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5880": {"surat": 81, "ayat": 2, "teks": "اِذَا الشَّمۡسُ كُوِّرَتۡۙ‏"},
"5881": {"surat": 81, "ayat": 3, "teks": "وَاِذَا النُّجُوۡمُ انْكَدَرَتۡۙ‏"},
"5882": {"surat": 81, "ayat": 4, "teks": "وَاِذَا الۡجِبَالُ  سُيِّرَتۡۙ‏"},
"5883": {"surat": 81, "ayat": 5, "teks": "وَاِذَا الۡعِشَارُ عُطِّلَتۡۙ‏"},
"5884": {"surat": 81, "ayat": 6, "teks": "وَاِذَا الۡوُحُوۡشُ  حُشِرَتۡۙ‏"},
"5885": {"surat": 81, "ayat": 7, "teks": "وَاِذَا الۡبِحَارُ سُجِّرَتۡۙ‏"},
"5886": {"surat": 81, "ayat": 8, "teks": "وَاِذَا النُّفُوۡسُ زُوِّجَتۡۙ‏"},
"5887": {"surat": 81, "ayat": 9, "teks": "وَاِذَا الۡمَوۡءٗدَةُ سُٮِٕلَتۡۙ‏"},
"5888": {"surat": 81, "ayat": 10, "teks": "بِاَىِّ ذَنۡۢبٍ قُتِلَتۡ‌ۚ‏"},
"5889": {"surat": 81, "ayat": 11, "teks": "وَاِذَا الصُّحُفُ نُشِرَتۡۙ‏"},
"5890": {"surat": 81, "ayat": 12, "teks": "وَاِذَا السَّمَآءُ كُشِطَتۡۙ‏"},
"5891": {"surat": 81, "ayat": 13, "teks": "وَاِذَا الۡجَحِيۡمُ سُعِّرَتۡۙ‏"},
"5892": {"surat": 81, "ayat": 14, "teks": "وَاِذَا الۡجَـنَّةُ اُزۡلِفَتۡۙ‏"},
"5893": {"surat": 81, "ayat": 15, "teks": "عَلِمَتۡ نَفۡسٌ مَّاۤ اَحۡضَرَتۡؕ‏"},
"5894": {"surat": 81, "ayat": 16, "teks": "فَلَاۤ  اُقۡسِمُ بِالۡخُنَّسِۙ‏"},
"5895": {"surat": 81, "ayat": 17, "teks": "الۡجَوَارِ الۡكُنَّسِۙ‏"},
"5896": {"surat": 81, "ayat": 18, "teks": "وَالَّيۡلِ اِذَا عَسۡعَسَۙ‏"},
"5897": {"surat": 81, "ayat": 19, "teks": "وَالصُّبۡحِ اِذَا تَنَفَّسَۙ‏"},
"5898": {"surat": 81, "ayat": 20, "teks": "اِنَّهٗ لَقَوۡلُ رَسُوۡلٍ كَرِيۡمٍۙ‏"},
"5899": {"surat": 81, "ayat": 21, "teks": "ذِىۡ قُوَّةٍ عِنۡدَ ذِى الۡعَرۡشِ مَكِيۡنٍۙ‏"},
"5900": {"surat": 81, "ayat": 22, "teks": "مُّطَاعٍ ثَمَّ اَمِيۡنٍؕ‏"},
"5901": {"surat": 81, "ayat": 23, "teks": "وَ مَا صَاحِبُكُمۡ بِمَجۡنُوۡنٍ‌ۚ‏"},
"5902": {"surat": 81, "ayat": 24, "teks": "وَلَقَدۡ رَاٰهُ بِالۡاُفُقِ الۡمُبِيۡنِ‌ۚ‏"},
"5903": {"surat": 81, "ayat": 25, "teks": "وَمَا هُوَ عَلَى الۡغَيۡبِ بِضَنِيۡنٍ‌ۚ‏"},
"5904": {"surat": 81, "ayat": 26, "teks": "وَمَا هُوَ بِقَوۡلِ شَيۡطٰنٍ  رَّجِيۡمٍۙ‏"},
"5905": {"surat": 81, "ayat": 27, "teks": "فَاَيۡنَ تَذۡهَبُوۡنَؕ‏"},
"5906": {"surat": 81, "ayat": 28, "teks": "اِنۡ هُوَ اِلَّا ذِكۡرٌ لِّلۡعٰلَمِيۡنَۙ‏"},
"5907": {"surat": 81, "ayat": 29, "teks": "لِمَنۡ شَآءَ مِنۡكُمۡ اَنۡ يَّسۡتَقِيۡمَؕ‏"},
"5908": {"surat": 81, "ayat": 30, "teks": "وَمَا تَشَآءُوۡنَ اِلَّاۤ اَنۡ  يَّشَآءَ اللّٰهُ رَبُّ الۡعٰلَمِيۡنَ‏"},
"5909": {"surat": 82, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5910": {"surat": 82, "ayat": 2, "teks": "اِذَا السَّمَآءُ انْفَطَرَتۡۙ‏"},
"5911": {"surat": 82, "ayat": 3, "teks": "وَاِذَا الۡكَوَاكِبُ انْتَثَرَتۡۙ‏"},
"5912": {"surat": 82, "ayat": 4, "teks": "وَاِذَا الۡبِحَارُ  فُجِّرَتۡۙ‏"},
"5913": {"surat": 82, "ayat": 5, "teks": "وَاِذَا الۡقُبُوۡرُ بُعۡثِرَتۡۙ‏"},
"5914": {"surat": 82, "ayat": 6, "teks": "عَلِمَتۡ نَفۡسٌ مَّا قَدَّمَتۡ وَاَخَّرَتۡؕ‏"},
"5915": {"surat": 82, "ayat": 7, "teks": "يٰۤاَيُّهَا الۡاِنۡسَانُ مَا غَرَّكَ بِرَبِّكَ الۡكَرِيۡمِۙ‏"},
"5916": {"surat": 82, "ayat": 8, "teks": "الَّذِىۡ خَلَقَكَ  فَسَوّٰٮكَ فَعَدَلَـكَۙ‏"},
"5917": {"surat": 82, "ayat": 9, "teks": "فِىۡۤ اَىِّ صُوۡرَةٍ مَّا شَآءَ رَكَّبَكَؕ‏"},
"5918": {"surat": 82, "ayat": 10, "teks": "كَلَّا بَلۡ  تُكَذِّبُوۡنَ بِالدِّيۡنِۙ‏"},
"5919": {"surat": 82, "ayat": 11, "teks": "وَاِنَّ عَلَيۡكُمۡ لَحٰـفِظِيۡنَۙ‏"},
"5920": {"surat": 82, "ayat": 12, "teks": "كِرَامًا كَاتِبِيۡنَۙ‏"},
"5921": {"surat": 82, "ayat": 13, "teks": "يَعۡلَمُوۡنَ مَا تَفۡعَلُوۡنَ‏"},
"5922": {"surat": 82, "ayat": 14, "teks": "اِنَّ الۡاَبۡرَارَ لَفِىۡ نَعِيۡمٍۚ‏"},
"5923": {"surat": 82, "ayat": 15, "teks": "وَاِنَّ الۡفُجَّارَ لَفِىۡ جَحِيۡمٍ ۚۖ‏"},
"5924": {"surat": 82, "ayat": 16, "teks": "يَّصۡلَوۡنَهَا يَوۡمَ الدِّيۡنِ‏"},
"5925": {"surat": 82, "ayat": 17, "teks": "وَمَا هُمۡ  عَنۡهَا بِغَآٮِٕبِيۡنَؕ‏"},
"5926": {"surat": 82, "ayat": 18, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا يَوۡمُ الدِّيۡنِۙ‏"},
"5927": {"surat": 82, "ayat": 19, "teks": "ثُمَّ مَاۤ اَدۡرٰٮكَ  مَا يَوۡمُ الدِّيۡنِؕ‏"},
"5928": {"surat": 82, "ayat": 20, "teks": "يَوۡمَ لَا تَمۡلِكُ نَفۡسٌ لِّنَفۡسٍ شَيۡـــًٔا‌ ؕ وَالۡاَمۡرُ يَوۡمَٮِٕذٍ لِّلَّهِ‏"},
"5929": {"surat": 83, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5930": {"surat": 83, "ayat": 2, "teks": "وَيۡلٌ لِّلۡمُطَفِّفِيۡنَۙ‏"},
"5931": {"surat": 83, "ayat": 3, "teks": "الَّذِيۡنَ اِذَا اكۡتَالُوۡا عَلَى النَّاسِ يَسۡتَوۡفُوۡنَۖ‏"},
"5932": {"surat": 83, "ayat": 4, "teks": "وَاِذَا كَالُوۡهُمۡ اَوْ وَّزَنُوۡهُمۡ يُخۡسِرُوۡنَؕ‏"},
"5933": {"surat": 83, "ayat": 5, "teks": "اَلَا يَظُنُّ اُولٰٓٮِٕكَ اَنَّهُمۡ  مَّبۡعُوۡثُوۡنَۙ‏"},
"5934": {"surat": 83, "ayat": 6, "teks": "لِيَوۡمٍ عَظِيۡمٍۙ‏"},
"5935": {"surat": 83, "ayat": 7, "teks": "يَّوۡمَ يَقُوۡمُ النَّاسُ لِرَبِّ  الۡعٰلَمِيۡنَؕ‏"},
"5936": {"surat": 83, "ayat": 8, "teks": "كَلَّاۤ اِنَّ كِتٰبَ الۡفُجَّارِ لَفِىۡ سِجِّيۡنٍؕ‏"},
"5937": {"surat": 83, "ayat": 9, "teks": "وَمَاۤ اَدۡرٰٮكَ  مَا سِجِّيۡنٌؕ‏"},
"5938": {"surat": 83, "ayat": 10, "teks": "كِتٰبٌ مَّرۡقُوۡمٌؕ‏"},
"5939": {"surat": 83, "ayat": 11, "teks": "وَيۡلٌ يَّوۡمَٮِٕذٍ لِّلۡمُكَذِّبِيۡنَۙ‏"},
"5940": {"surat": 83, "ayat": 12, "teks": "الَّذِيۡنَ يُكَذِّبُوۡنَ بِيَوۡمِ الدِّيۡنِؕ‏"},
"5941": {"surat": 83, "ayat": 13, "teks": "وَمَا يُكَذِّبُ بِهٖۤ اِلَّا كُلُّ  مُعۡتَدٍ اَثِيۡمٍۙ‏"},
"5942": {"surat": 83, "ayat": 14, "teks": "اِذَا تُتۡلٰى عَلَيۡهِ اٰيٰتُنَا قَالَ اَسَاطِيۡرُ  الۡاَوَّلِيۡنَؕ‏"},
"5943": {"surat": 83, "ayat": 15, "teks": "كَلَّا‌ بَلۡ؄ رَانَ عَلٰى قُلُوۡبِهِمۡ مَّا كَانُوۡا يَكۡسِبُوۡنَ‏"},
"5944": {"surat": 83, "ayat": 16, "teks": "كَلَّاۤ اِنَّهُمۡ عَنۡ رَّبِّهِمۡ يَوۡمَٮِٕذٍ لَّمَحۡجُوۡبُوۡنَ‌ؕ‏"},
"5945": {"surat": 83, "ayat": 17, "teks": "ثُمَّ اِنَّهُمۡ لَصَالُوا الۡجَحِيۡمِؕ‏"},
"5946": {"surat": 83, "ayat": 18, "teks": "ثُمَّ يُقَالُ هٰذَا الَّذِىۡ كُنۡتُمۡ بِهٖ تُكَذِّبُوۡنَؕ‏"},
"5947": {"surat": 83, "ayat": 19, "teks": "كَلَّاۤ اِنَّ كِتٰبَ الۡاَبۡرَارِ لَفِىۡ عِلِّيِّيۡنَؕ‏"},
"5948": {"surat": 83, "ayat": 20, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا عِلِّيُّوۡنَؕ‏"},
"5949": {"surat": 83, "ayat": 21, "teks": "كِتٰبٌ مَّرۡقُوۡمٌۙ‏"},
"5950": {"surat": 83, "ayat": 22, "teks": "يَّشۡهَدُهُ الۡمُقَرَّبُوۡنَؕ‏"},
"5951": {"surat": 83, "ayat": 23, "teks": "اِنَّ الۡاَبۡرَارَ لَفِىۡ  نَعِيۡمٍۙ‏"},
"5952": {"surat": 83, "ayat": 24, "teks": "عَلَى الۡاَرَآٮِٕكِ يَنۡظُرُوۡنَۙ‏"},
"5953": {"surat": 83, "ayat": 25, "teks": "تَعۡرِفُ فِىۡ وُجُوۡهِهِمۡ  نَضۡرَةَ النَّعِيۡمِ‌ۚ‏"},
"5954": {"surat": 83, "ayat": 26, "teks": "يُسۡقَوۡنَ مِنۡ رَّحِيۡقٍ مَّخۡتُوۡمٍۙ‏"},
"5955": {"surat": 83, "ayat": 27, "teks": "خِتٰمُهٗ مِسۡكٌ  ‌ؕ وَفِىۡ ذٰلِكَ فَلۡيَتَنَافَسِ الْمُتَنَافِسُوۡنَ‏"},
"5956": {"surat": 83, "ayat": 28, "teks": "وَ مِزَاجُهٗ مِنۡ تَسۡنِيۡمٍۙ‏"},
"5957": {"surat": 83, "ayat": 29, "teks": "عَيۡنًا يَّشۡرَبُ بِهَا الۡمُقَرَّبُوۡنَؕ‏"},
"5958": {"surat": 83, "ayat": 30, "teks": "اِنَّ الَّذِيۡنَ اَجۡرَمُوۡا كَانُوۡا مِنَ الَّذِيۡنَ اٰمَنُوۡا يَضۡحَكُوۡنَ  ۖ‏"},
"5959": {"surat": 83, "ayat": 31, "teks": "وَاِذَا مَرُّوۡا بِهِمۡ يَتَغَامَزُوۡنَ  ۖ‏"},
"5960": {"surat": 83, "ayat": 32, "teks": "وَاِذَا انۡقَلَبُوۡۤا اِلٰٓى اَهۡلِهِمُ انْقَلَبُوۡا فَكِهِيۡنَ  ۖ‏"},
"5961": {"surat": 83, "ayat": 33, "teks": "وَاِذَا رَاَوۡهُمۡ قَالُوۡۤا اِنَّ  هٰٓؤُلَاۤءِ لَـضَآلُّوۡنَۙ‏"},
"5962": {"surat": 83, "ayat": 34, "teks": "وَمَاۤ اُرۡسِلُوۡا عَلَيۡهِمۡ حٰفِظِيۡنَۙ‏"},
"5963": {"surat": 83, "ayat": 35, "teks": "فَالۡيَوۡمَ الَّذِيۡنَ اٰمَنُوۡا مِنَ الۡكُفَّارِ يَضۡحَكُوۡنَۙ‏"},
"5964": {"surat": 83, "ayat": 36, "teks": "عَلَى  الۡاَرَآٮِٕكِۙ يَنۡظُرُوۡنَؕ‏"},
"5965": {"surat": 83, "ayat": 37, "teks": "هَلۡ ثُوِّبَ الۡكُفَّارُ مَا كَانُوۡا يَفۡعَلُوۡنَ‏"},
"5966": {"surat": 84, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5967": {"surat": 84, "ayat": 2, "teks": "اِذَا السَّمَآءُ انْشَقَّتۡۙ‏"},
"5968": {"surat": 84, "ayat": 3, "teks": "وَاَذِنَتۡ لِرَبِّهَا وَحُقَّتۡۙ‏"},
"5969": {"surat": 84, "ayat": 4, "teks": "وَاِذَا الۡاَرۡضُ مُدَّتۡؕ‏"},
"5970": {"surat": 84, "ayat": 5, "teks": "وَاَلۡقَتۡ مَا فِيۡهَا وَتَخَلَّتۡۙ‏"},
"5971": {"surat": 84, "ayat": 6, "teks": "وَاَذِنَتۡ  لِرَبِّهَا وَحُقَّتۡؕ‏"},
"5972": {"surat": 84, "ayat": 7, "teks": "يٰۤاَيُّهَا الۡاِنۡسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ  كَدۡحًا فَمُلٰقِيۡهِ‌ۚ‏"},
"5973": {"surat": 84, "ayat": 8, "teks": "فَاَمَّا مَنۡ اُوۡتِىَ كِتٰبَهٗ بِيَمِيۡنِهٖۙ‏"},
"5974": {"surat": 84, "ayat": 9, "teks": "فَسَوۡفَ يُحَاسَبُ حِسَابًا يَّسِيۡرًا ۙ‏"},
"5975": {"surat": 84, "ayat": 10, "teks": "وَّيَنۡقَلِبُ اِلٰٓى اَهۡلِهٖ مَسۡرُوۡرًا ؕ‏"},
"5976": {"surat": 84, "ayat": 11, "teks": "وَاَمَّا مَنۡ اُوۡتِىَ كِتٰبَهٗ وَرَآءَ ظَهۡرِهٖۙ‏"},
"5977": {"surat": 84, "ayat": 12, "teks": "فَسَوۡفَ يَدۡعُوۡا ثُبُوۡرًا ۙ‏"},
"5978": {"surat": 84, "ayat": 13, "teks": "وَّيَصۡلٰى سَعِيۡرًا ؕ‏"},
"5979": {"surat": 84, "ayat": 14, "teks": "اِنَّهٗ كَانَ فِىۡۤ اَهۡلِهٖ مَسۡرُوۡرًا ؕ‏"},
"5980": {"surat": 84, "ayat": 15, "teks": "اِنَّهٗ ظَنَّ اَنۡ لَّنۡ يَّحُوۡرَ ۛۚ‏"},
"5981": {"surat": 84, "ayat": 16, "teks": "بَلٰٓى  ۛۚ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيۡرًا ؕ‏"},
"5982": {"surat": 84, "ayat": 17, "teks": "فَلَاۤ اُقۡسِمُ بِالشَّفَقِۙ‏"},
"5983": {"surat": 84, "ayat": 18, "teks": "وَالَّيۡلِ وَمَا  وَسَقَۙ‏"},
"5984": {"surat": 84, "ayat": 19, "teks": "وَالۡقَمَرِ اِذَا اتَّسَقَۙ‏"},
"5985": {"surat": 84, "ayat": 20, "teks": "لَتَرۡكَبُنَّ طَبَقًا عَنۡ طَبَقٍؕ‏"},
"5986": {"surat": 84, "ayat": 21, "teks": "فَمَا  لَهُمۡ لَا يُؤۡمِنُوۡنَۙ‏"},
"5987": {"surat": 84, "ayat": 22, "teks": "وَاِذَا قُرِئَ عَلَيۡهِمُ الۡقُرۡاٰنُ لَا يَسۡجُدُوۡنَ  ؕ ۩‏"},
"5988": {"surat": 84, "ayat": 23, "teks": "بَلِ الَّذِيۡنَ كَفَرُوۡا يُكَذِّبُوۡنَ ۖ‏"},
"5989": {"surat": 84, "ayat": 24, "teks": "وَاللّٰهُ اَعۡلَمُ بِمَا يُوۡعُوۡنَ ۖ‏"},
"5990": {"surat": 84, "ayat": 25, "teks": "فَبَشِّرۡهُمۡ بِعَذَابٍ اَلِيۡمٍۙ‏"},
"5991": {"surat": 84, "ayat": 26, "teks": "اِلَّا الَّذِيۡنَ اٰمَنُوۡا وَعَمِلُوا الصّٰلِحٰتِ لَهُمۡ اَجۡرٌ غَيۡرُ مَمۡنُوۡنٍ‏"},
"5992": {"surat": 85, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"5993": {"surat": 85, "ayat": 2, "teks": "وَالسَّمَآءِ ذَاتِ الۡبُرُوۡجِۙ‏"},
"5994": {"surat": 85, "ayat": 3, "teks": "وَالۡيَوۡمِ الۡمَوۡعُوۡدِۙ‏"},
"5995": {"surat": 85, "ayat": 4, "teks": "وَشَاهِدٍ  وَّمَشۡهُوۡدٍؕ‏"},
"5996": {"surat": 85, "ayat": 5, "teks": "قُتِلَ اَصۡحٰبُ الۡاُخۡدُوۡدِۙ‏"},
"5997": {"surat": 85, "ayat": 6, "teks": "النَّارِ ذَاتِ  الۡوَقُوۡدِۙ‏"},
"5998": {"surat": 85, "ayat": 7, "teks": "اِذۡ هُمۡ عَلَيۡهَا قُعُوۡدٌ  ۙ‏"},
"5999": {"surat": 85, "ayat": 8, "teks": "وَّهُمۡ عَلٰى مَا يَفۡعَلُوۡنَ بِالۡمُؤۡمِنِيۡنَ شُهُوۡدٌ  ؕ‏"},
"6000": {"surat": 85, "ayat": 9, "teks": "وَمَا نَقَمُوۡا مِنۡهُمۡ اِلَّاۤ اَنۡ يُّؤۡمِنُوۡا بِاللّٰهِ  الۡعَزِيۡزِ الۡحَمِيۡدِۙ‏"},
"6001": {"surat": 85, "ayat": 10, "teks": "الَّذِىۡ لَهٗ مُلۡكُ السَّمٰوٰتِ وَالۡاَرۡضِ‌ؕ وَ اللّٰهُ عَلٰى كُلِّ شَىۡءٍ شَهِيۡدٌ ؕ‏"},
"6002": {"surat": 85, "ayat": 11, "teks": "اِنَّ الَّذِيۡنَ فَتَـنُوا الۡمُؤۡمِنِيۡنَ وَ الۡمُؤۡمِنٰتِ ثُمَّ لَمۡ يَتُوۡبُوۡا فَلَهُمۡ عَذَابُ جَهَنَّمَ وَلَهُمۡ عَذَابُ الۡحَرِيۡقِؕ‏"},
"6003": {"surat": 85, "ayat": 12, "teks": "اِنَّ الَّذِيۡنَ اٰمَنُوۡا وَعَمِلُوا الصّٰلِحٰتِ لَهُمۡ جَنّٰتٌ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ ؕؔ ذٰلِكَ الۡفَوۡزُ الۡكَبِيۡرُؕ‏"},
"6004": {"surat": 85, "ayat": 13, "teks": "اِنَّ بَطۡشَ رَبِّكَ لَشَدِيۡدٌ ؕ‏"},
"6005": {"surat": 85, "ayat": 14, "teks": "اِنَّهٗ هُوَ يُبۡدِئُ وَيُعِيۡدُ‌ ۚ‏"},
"6006": {"surat": 85, "ayat": 15, "teks": "وَهُوَ الۡغَفُوۡرُ الۡوَدُوۡدُۙ‏"},
"6007": {"surat": 85, "ayat": 16, "teks": "ذُو الۡعَرۡشِ الۡمَجِيۡدُ ۙ‏"},
"6008": {"surat": 85, "ayat": 17, "teks": "فَعَّالٌ لِّمَا يُرِيۡدُ ؕ‏"},
"6009": {"surat": 85, "ayat": 18, "teks": "هَلۡ اَتٰٮكَ حَدِيۡثُ الۡجُـنُوۡدِۙ‏"},
"6010": {"surat": 85, "ayat": 19, "teks": "فِرۡعَوۡنَ وَثَمُوۡدَؕ‏"},
"6011": {"surat": 85, "ayat": 20, "teks": "بَلِ الَّذِيۡنَ كَفَرُوۡا فِىۡ تَكۡذِيۡبٍۙ‏"},
"6012": {"surat": 85, "ayat": 21, "teks": "وَّاللّٰهُ مِنۡ وَّرَآٮِٕهِمۡ مُّحِيۡطٌۚ‏"},
"6013": {"surat": 85, "ayat": 22, "teks": "بَلۡ هُوَ قُرۡاٰنٌ مَّجِيۡدٌ ۙ‏"},
"6014": {"surat": 85, "ayat": 23, "teks": "فِىۡ لَوۡحٍ مَّحۡفُوۡظٍ‏"},
"6015": {"surat": 86, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6016": {"surat": 86, "ayat": 2, "teks": "وَالسَّمَآءِ وَالطَّارِقِۙ‏"},
"6017": {"surat": 86, "ayat": 3, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا الطَّارِقُۙ‏"},
"6018": {"surat": 86, "ayat": 4, "teks": "النَّجۡمُ الثَّاقِبُۙ‏"},
"6019": {"surat": 86, "ayat": 5, "teks": "اِنۡ كُلُّ نَفۡسٍ لَّمَّا عَلَيۡهَا حَافِظٌؕ‏"},
"6020": {"surat": 86, "ayat": 6, "teks": "فَلۡيَنۡظُرِ الۡاِنۡسَانُ مِمَّ خُلِقَؕ‏"},
"6021": {"surat": 86, "ayat": 7, "teks": "خُلِقَ مِنۡ مَّآءٍ دَافِقٍۙ‏"},
"6022": {"surat": 86, "ayat": 8, "teks": "يَّخۡرُجُ مِنۡۢ بَيۡنِ الصُّلۡبِ وَالتَّرَآٮِٕبِؕ‏"},
"6023": {"surat": 86, "ayat": 9, "teks": "اِنَّهٗ عَلٰى رَجۡعِهٖ لَقَادِرٌؕ‏"},
"6024": {"surat": 86, "ayat": 10, "teks": "يَوۡمَ تُبۡلَى السَّرَآٮِٕرُۙ‏"},
"6025": {"surat": 86, "ayat": 11, "teks": "فَمَا لَهٗ مِنۡ قُوَّةٍ وَّلَا  نَاصِرٍؕ‏"},
"6026": {"surat": 86, "ayat": 12, "teks": "وَالسَّمَآءِ ذَاتِ الرَّجۡعِۙ‏"},
"6027": {"surat": 86, "ayat": 13, "teks": "وَالۡاَرۡضِ ذَاتِ  الصَّدۡعِۙ‏"},
"6028": {"surat": 86, "ayat": 14, "teks": "اِنَّهٗ لَقَوۡلٌ فَصۡلٌۙ‏"},
"6029": {"surat": 86, "ayat": 15, "teks": "وَّمَا هُوَ بِالۡهَزۡلِؕ‏"},
"6030": {"surat": 86, "ayat": 16, "teks": "اِنَّهُمۡ يَكِيۡدُوۡنَ كَيۡدًا ۙ‏"},
"6031": {"surat": 86, "ayat": 17, "teks": "وَّاَكِيۡدُ كَيۡدًا ۚۖ‏"},
"6032": {"surat": 86, "ayat": 18, "teks": "فَمَهِّلِ الۡكٰفِرِيۡنَ  اَمۡهِلۡهُمۡ رُوَيۡدًا‏"},
"6033": {"surat": 87, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6034": {"surat": 87, "ayat": 2, "teks": "سَبِّحِ اسۡمَ رَبِّكَ الۡاَعۡلَىۙ‏"},
"6035": {"surat": 87, "ayat": 3, "teks": "الَّذِىۡ خَلَقَ فَسَوّٰىۙ‏"},
"6036": {"surat": 87, "ayat": 4, "teks": "وَالَّذِىۡ قَدَّرَ  فَهَدٰىۙ‏"},
"6037": {"surat": 87, "ayat": 5, "teks": "وَالَّذِىۡۤ اَخۡرَجَ الۡمَرۡعٰىۙ‏"},
"6038": {"surat": 87, "ayat": 6, "teks": "فَجَعَلَهٗ غُثَآءً اَحۡوٰىؕ‏"},
"6039": {"surat": 87, "ayat": 7, "teks": "سَنُقۡرِئُكَ فَلَا تَنۡسٰٓىۙ‏"},
"6040": {"surat": 87, "ayat": 8, "teks": "اِلَّا مَا شَآءَ اللّٰهُ‌ؕ اِنَّهٗ يَعۡلَمُ الۡجَهۡرَ وَمَا  يَخۡفٰىؕ‏"},
"6041": {"surat": 87, "ayat": 9, "teks": "وَنُيَسِّرُكَ لِلۡيُسۡرٰى‌ۖ‌ۚ‏"},
"6042": {"surat": 87, "ayat": 10, "teks": "فَذَكِّرۡ اِنۡ نَّفَعَتِ الذِّكۡرٰىؕ‏"},
"6043": {"surat": 87, "ayat": 11, "teks": "سَيَذَّكَّرُ  مَنۡ يَّخۡشٰىۙ‏"},
"6044": {"surat": 87, "ayat": 12, "teks": "وَيَتَجَنَّبُهَا الۡاَشۡقَىۙ‏"},
"6045": {"surat": 87, "ayat": 13, "teks": "الَّذِىۡ يَصۡلَى النَّارَ الۡكُبۡرٰى‌ۚ‏"},
"6046": {"surat": 87, "ayat": 14, "teks": "ثُمَّ لَا يَمُوۡتُ فِيۡهَا وَلَا يَحۡيٰىؕ‏"},
"6047": {"surat": 87, "ayat": 15, "teks": "قَدۡ اَفۡلَحَ مَنۡ تَزَكّٰىۙ‏"},
"6048": {"surat": 87, "ayat": 16, "teks": "وَذَكَرَ اسۡمَ  رَبِّهٖ فَصَلّٰى‌ؕ‏"},
"6049": {"surat": 87, "ayat": 17, "teks": "بَلۡ تُؤۡثِرُوۡنَ الۡحَيٰوةَ الدُّنۡيَا ۖ‏"},
"6050": {"surat": 87, "ayat": 18, "teks": "وَالۡاٰخِرَةُ خَيۡرٌ وَّ اَبۡقٰىؕ‏"},
"6051": {"surat": 87, "ayat": 19, "teks": "اِنَّ هٰذَا لَفِى الصُّحُفِ الۡاُوۡلٰىۙ‏"},
"6052": {"surat": 87, "ayat": 20, "teks": "صُحُفِ اِبۡرٰهِيۡمَ وَمُوۡسٰى‏"},
"6053": {"surat": 88, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6054": {"surat": 88, "ayat": 2, "teks": "هَلۡ اَتٰٮكَ حَدِيۡثُ الۡغَاشِيَةِؕ‏"},
"6055": {"surat": 88, "ayat": 3, "teks": "وُجُوۡهٌ يَّوۡمَٮِٕذٍ خَاشِعَةٌ ۙ‏"},
"6056": {"surat": 88, "ayat": 4, "teks": "عَامِلَةٌ نَّاصِبَةٌ ۙ‏"},
"6057": {"surat": 88, "ayat": 5, "teks": "تَصۡلٰى نَارًا حَامِيَةً ۙ‏"},
"6058": {"surat": 88, "ayat": 6, "teks": "تُسۡقٰى مِنۡ عَيۡنٍ اٰنِيَةٍؕ‏"},
"6059": {"surat": 88, "ayat": 7, "teks": "لَـيۡسَ  لَهُمۡ طَعَامٌ اِلَّا مِنۡ ضَرِيۡعٍۙ‏"},
"6060": {"surat": 88, "ayat": 8, "teks": "لَّا يُسۡمِنُ وَلَا يُغۡنِىۡ مِنۡ جُوۡعٍؕ‏"},
"6061": {"surat": 88, "ayat": 9, "teks": "وُجُوۡهٌ يَّوۡمَٮِٕذٍ نَّاعِمَةٌ ۙ‏"},
"6062": {"surat": 88, "ayat": 10, "teks": "لِّسَعۡيِهَا رَاضِيَةٌ ۙ‏"},
"6063": {"surat": 88, "ayat": 11, "teks": "فِىۡ جَنَّةٍ  عَالِيَةٍۙ‏"},
"6064": {"surat": 88, "ayat": 12, "teks": "لَّا تَسۡمَعُ فِيۡهَا لَاغِيَةً ؕ‏"},
"6065": {"surat": 88, "ayat": 13, "teks": "فِيۡهَا عَيۡنٌ جَارِيَةٌ‌ ۘ‏"},
"6066": {"surat": 88, "ayat": 14, "teks": "فِيۡهَا سُرُرٌ مَّرۡفُوۡعَةٌ ۙ‏"},
"6067": {"surat": 88, "ayat": 15, "teks": "وَّاَكۡوَابٌ مَّوۡضُوۡعَةٌ ۙ‏"},
"6068": {"surat": 88, "ayat": 16, "teks": "وَّنَمَارِقُ مَصۡفُوۡفَةٌ ۙ‏"},
"6069": {"surat": 88, "ayat": 17, "teks": "وَّزَرَابِىُّ مَبۡثُوۡثَةٌ ؕ‏"},
"6070": {"surat": 88, "ayat": 18, "teks": "اَفَلَا يَنۡظُرُوۡنَ اِلَى الۡاِ بِلِ كَيۡفَ خُلِقَتۡ‏"},
"6071": {"surat": 88, "ayat": 19, "teks": "وَاِلَى السَّمَآءِ كَيۡفَ رُفِعَتۡ‏"},
"6072": {"surat": 88, "ayat": 20, "teks": "وَاِلَى الۡجِبَالِ  كَيۡفَ نُصِبَتۡ‏"},
"6073": {"surat": 88, "ayat": 21, "teks": "وَاِلَى الۡاَرۡضِ كَيۡفَ سُطِحَتۡ‏"},
"6074": {"surat": 88, "ayat": 22, "teks": "فَذَكِّرۡ ؕ اِنَّمَاۤ اَنۡتَ مُذَكِّرٌ ؕ‏"},
"6075": {"surat": 88, "ayat": 23, "teks": "لَـسۡتَ عَلَيۡهِمۡ بِمُصَۜيۡطِرٍۙ‏"},
"6076": {"surat": 88, "ayat": 24, "teks": "اِلَّا مَنۡ  تَوَلّٰى وَكَفَرَۙ‏"},
"6077": {"surat": 88, "ayat": 25, "teks": "فَيُعَذِّبُهُ اللّٰهُ الۡعَذَابَ الۡاَكۡبَرَؕ‏"},
"6078": {"surat": 88, "ayat": 26, "teks": "اِنَّ اِلَيۡنَاۤ   اِيَابَهُمۡۙ‏"},
"6079": {"surat": 88, "ayat": 27, "teks": "ثُمَّ اِنَّ عَلَيۡنَا حِسَابَهُمْ‏"},
"6080": {"surat": 89, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6081": {"surat": 89, "ayat": 2, "teks": "وَالۡفَجۡرِۙ‏"},
"6082": {"surat": 89, "ayat": 3, "teks": "وَلَيَالٍ عَشۡرٍۙ‏"},
"6083": {"surat": 89, "ayat": 4, "teks": "وَّالشَّفۡعِ وَالۡوَتۡرِۙ‏"},
"6084": {"surat": 89, "ayat": 5, "teks": "وَالَّيۡلِ اِذَا  يَسۡرِ‌ۚ‏"},
"6085": {"surat": 89, "ayat": 6, "teks": "هَلۡ فِىۡ ذٰلِكَ قَسَمٌ لِّذِىۡ حِجۡرٍؕ‏"},
"6086": {"surat": 89, "ayat": 7, "teks": "اَلَمۡ تَرَ كَيۡفَ فَعَلَ  رَبُّكَ بِعَادٍۙ‏"},
"6087": {"surat": 89, "ayat": 8, "teks": "اِرَمَ ذَاتِ الۡعِمَادِۙ‏"},
"6088": {"surat": 89, "ayat": 9, "teks": "الَّتِىۡ لَمۡ يُخۡلَقۡ مِثۡلُهَا فِى الۡبِلَادِۙ‏"},
"6089": {"surat": 89, "ayat": 10, "teks": "وَثَمُوۡدَ الَّذِيۡنَ جَابُوا الصَّخۡرَ بِالۡوَادِۙ‏"},
"6090": {"surat": 89, "ayat": 11, "teks": "وَفِرۡعَوۡنَ ذِى الۡاَوۡتَادِۙ‏"},
"6091": {"surat": 89, "ayat": 12, "teks": "الَّذِيۡنَ طَغَوۡا فِى الۡبِلَادِۙ‏"},
"6092": {"surat": 89, "ayat": 13, "teks": "فَاَكۡثَرُوۡا فِيۡهَا الۡفَسَادَۙ‏"},
"6093": {"surat": 89, "ayat": 14, "teks": "فَصَبَّ عَلَيۡهِمۡ رَبُّكَ سَوۡطَ عَذَابٍ ۙۚ‏"},
"6094": {"surat": 89, "ayat": 15, "teks": "اِنَّ رَبَّكَ لَبِالۡمِرۡصَادِؕ‏"},
"6095": {"surat": 89, "ayat": 16, "teks": "فَاَمَّا الۡاِنۡسَانُ اِذَا مَا ابۡتَلٰٮهُ رَبُّهٗ فَاَكۡرَمَهٗ وَنَعَّمَهٗ  ۙ فَيَقُوۡلُ رَبِّىۡۤ اَكۡرَمَنِؕ‏"},
"6096": {"surat": 89, "ayat": 17, "teks": "وَاَمَّاۤ اِذَا مَا ابۡتَلٰٮهُ فَقَدَرَ عَلَيۡهِ رِزۡقَهٗۙ فَيَقُوۡلُ رَبِّىۡۤ  اَهَانَنِ‌ۚ‏"},
"6097": {"surat": 89, "ayat": 18, "teks": "كَلَّا‌ بَلۡ لَّا تُكۡرِمُوۡنَ الۡيَتِيۡمَۙ‏"},
"6098": {"surat": 89, "ayat": 19, "teks": "وَلَا تَحٰٓضُّوۡنَ  عَلٰى طَعَامِ الۡمِسۡكِيۡنِۙ‏"},
"6099": {"surat": 89, "ayat": 20, "teks": "وَتَاۡكُلُوۡنَ التُّرَاثَ اَكۡلًا لَّـمًّا ۙ‏"},
"6100": {"surat": 89, "ayat": 21, "teks": "وَّتُحِبُّوۡنَ الۡمَالَ حُبًّا جَمًّا ؕ‏"},
"6101": {"surat": 89, "ayat": 22, "teks": "كَلَّاۤ اِذَا دُكَّتِ الۡاَرۡضُ دَكًّا دَكًّا ۙ‏"},
"6102": {"surat": 89, "ayat": 23, "teks": "وَّجَآءَ رَبُّكَ وَالۡمَلَكُ صَفًّا صَفًّا ۚ‏"},
"6103": {"surat": 89, "ayat": 24, "teks": "وَجِاىْٓءَ يَوۡمَٮِٕذٍۢ بِجَهَنَّمَ  ۙ‌ يَوۡمَٮِٕذٍ يَّتَذَكَّرُ الۡاِنۡسَانُ وَاَنّٰى لَـهُ الذِّكۡرٰىؕ‏"},
"6104": {"surat": 89, "ayat": 25, "teks": "يَقُوۡلُ يٰلَيۡتَنِىۡ قَدَّمۡتُ لِحَـيَاتِى‌ۚ‏"},
"6105": {"surat": 89, "ayat": 26, "teks": "فَيَوۡمَٮِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗۤ اَحَدٌ ۙ‏"},
"6106": {"surat": 89, "ayat": 27, "teks": "وَّلَا يُوۡثِقُ وَثَاقَهٗۤ اَحَدٌ ؕ‏"},
"6107": {"surat": 89, "ayat": 28, "teks": "يٰۤاَيَّتُهَا النَّفۡسُ الۡمُطۡمَٮِٕنَّةُ  ۖ‏"},
"6108": {"surat": 89, "ayat": 29, "teks": "ارۡجِعِىۡۤ اِلٰى رَبِّكِ رَاضِيَةً مَّرۡضِيَّةً‌ ۚ‏"},
"6109": {"surat": 89, "ayat": 30, "teks": "فَادۡخُلِىۡ فِىۡ عِبٰدِىۙ‏"},
"6110": {"surat": 89, "ayat": 31, "teks": "وَادۡخُلِىۡ جَنَّتِى‏"},
"6111": {"surat": 90, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6112": {"surat": 90, "ayat": 2, "teks": "لَاۤ اُقۡسِمُ بِهٰذَا الۡبَلَدِۙ‏"},
"6113": {"surat": 90, "ayat": 3, "teks": "وَاَنۡتَ حِلٌّۢ بِهٰذَا الۡبَلَدِۙ‏"},
"6114": {"surat": 90, "ayat": 4, "teks": "وَوَالِدٍ وَّمَا وَلَدَ ۙ‏"},
"6115": {"surat": 90, "ayat": 5, "teks": "لَقَدۡ خَلَقۡنَا الۡاِنۡسَانَ فِىۡ كَبَدٍؕ‏"},
"6116": {"surat": 90, "ayat": 6, "teks": "اَيَحۡسَبُ اَنۡ لَّنۡ يَّقۡدِرَ عَلَيۡهِ اَحَدٌ‌ ۘ‏"},
"6117": {"surat": 90, "ayat": 7, "teks": "يَقُوۡلُ اَهۡلَكۡتُ مَالًا لُّبَدًا ؕ‏"},
"6118": {"surat": 90, "ayat": 8, "teks": "اَيَحۡسَبُ اَنۡ لَّمۡ يَرَهٗۤ اَحَدٌ ؕ‏"},
"6119": {"surat": 90, "ayat": 9, "teks": "اَلَمۡ نَجۡعَلۡ لَّهٗ عَيۡنَيۡنِۙ‏"},
"6120": {"surat": 90, "ayat": 10, "teks": "وَلِسَانًا وَّشَفَتَيۡنِۙ‏"},
"6121": {"surat": 90, "ayat": 11, "teks": "وَهَدَيۡنٰهُ  النَّجۡدَيۡنِ‌ۚ‏"},
"6122": {"surat": 90, "ayat": 12, "teks": "فَلَا اقۡتَحَمَ الۡعَقَبَةَ ۖ‏"},
"6123": {"surat": 90, "ayat": 13, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا الۡعَقَبَةُ  ؕ‏"},
"6124": {"surat": 90, "ayat": 14, "teks": "فَكُّ رَقَبَةٍ ۙ‏"},
"6125": {"surat": 90, "ayat": 15, "teks": "اَوۡ اِطۡعٰمٌ فِىۡ يَوۡمٍ ذِىۡ مَسۡغَبَةٍ ۙ‏"},
"6126": {"surat": 90, "ayat": 16, "teks": "يَّتِيۡمًا ذَا مَقۡرَبَةٍ ۙ‏"},
"6127": {"surat": 90, "ayat": 17, "teks": "اَوۡ مِسۡكِيۡنًا ذَا مَتۡرَبَةٍ ؕ‏"},
"6128": {"surat": 90, "ayat": 18, "teks": "ثُمَّ كَانَ مِنَ الَّذِيۡنَ اٰمَنُوۡا وَتَوَاصَوۡا بِالصَّبۡرِ وَتَوَاصَوۡا بِالۡمَرۡحَمَةِ ؕ‏"},
"6129": {"surat": 90, "ayat": 19, "teks": "اُولٰٓٮِٕكَ اَصۡحٰبُ الۡمَيۡمَنَةِ ؕ‏"},
"6130": {"surat": 90, "ayat": 20, "teks": "وَالَّذِيۡنَ كَفَرُوۡا بِاٰيٰتِنَا هُمۡ اَصۡحٰبُ الۡمَشۡـَٔـمَةِ ؕ‏"},
"6131": {"surat": 90, "ayat": 21, "teks": "عَلَيۡهِمۡ نَارٌ مُّؤۡصَدَةٌ‏"},
"6132": {"surat": 91, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6133": {"surat": 91, "ayat": 2, "teks": "وَالشَّمۡسِ وَضُحٰٮهَا  ۙ‏"},
"6134": {"surat": 91, "ayat": 3, "teks": "وَالۡقَمَرِ اِذَا تَلٰٮهَا  ۙ‏"},
"6135": {"surat": 91, "ayat": 4, "teks": "وَالنَّهَارِ اِذَا جَلّٰٮهَا ۙ‏"},
"6136": {"surat": 91, "ayat": 5, "teks": "وَالَّيۡلِ اِذَا يَغۡشٰٮهَا ۙ‏"},
"6137": {"surat": 91, "ayat": 6, "teks": "وَالسَّمَآءِ وَمَا بَنٰٮهَا ۙ‏"},
"6138": {"surat": 91, "ayat": 7, "teks": "وَالۡاَرۡضِ وَمَا طَحٰٮهَا ۙ‏"},
"6139": {"surat": 91, "ayat": 8, "teks": "وَنَفۡسٍ وَّمَا سَوّٰٮهَا ۙ‏"},
"6140": {"surat": 91, "ayat": 9, "teks": "فَاَلۡهَمَهَا فُجُوۡرَهَا وَتَقۡوٰٮهَا ۙ‏"},
"6141": {"surat": 91, "ayat": 10, "teks": "قَدۡ اَفۡلَحَ مَنۡ زَكّٰٮهَا ۙ‏"},
"6142": {"surat": 91, "ayat": 11, "teks": "وَقَدۡ خَابَ مَنۡ دَسّٰٮهَا ؕ‏"},
"6143": {"surat": 91, "ayat": 12, "teks": "كَذَّبَتۡ ثَمُوۡدُ بِطَغۡوٰٮهَآ  ۙ‏"},
"6144": {"surat": 91, "ayat": 13, "teks": "اِذِ انۡۢبَعَثَ اَشۡقٰٮهَا  ۙ‏"},
"6145": {"surat": 91, "ayat": 14, "teks": "فَقَالَ لَهُمۡ رَسُوۡلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقۡيٰهَا ؕ‏"},
"6146": {"surat": 91, "ayat": 15, "teks": "فَكَذَّبُوۡهُ فَعَقَرُوۡهَا   ۙفَدَمۡدَمَ عَلَيۡهِمۡ رَبُّهُمۡ بِذَنۡۢبِهِمۡ فَسَوّٰٮهَا  ۙ‏"},
"6147": {"surat": 91, "ayat": 16, "teks": "وَلَا يَخَافُ عُقۡبٰهَا‏"},
"6148": {"surat": 92, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6149": {"surat": 92, "ayat": 2, "teks": "وَالَّيۡلِ اِذَا يَغۡشٰىۙ‏"},
"6150": {"surat": 92, "ayat": 3, "teks": "وَالنَّهَارِ اِذَا تَجَلّٰىۙ‏"},
"6151": {"surat": 92, "ayat": 4, "teks": "وَمَا خَلَقَ الذَّكَرَ  وَالۡاُنۡثٰٓىۙ‏"},
"6152": {"surat": 92, "ayat": 5, "teks": "اِنَّ سَعۡيَكُمۡ لَشَتّٰىؕ‏"},
"6153": {"surat": 92, "ayat": 6, "teks": "فَاَمَّا مَنۡ اَعۡطٰى وَاتَّقٰىۙ‏"},
"6154": {"surat": 92, "ayat": 7, "teks": "وَصَدَّقَ بِالۡحُسۡنٰىۙ‏"},
"6155": {"surat": 92, "ayat": 8, "teks": "فَسَنُيَسِّرُهٗ لِلۡيُسۡرٰىؕ‏"},
"6156": {"surat": 92, "ayat": 9, "teks": "وَاَمَّا مَنۡۢ  بَخِلَ وَاسۡتَغۡنٰىۙ‏"},
"6157": {"surat": 92, "ayat": 10, "teks": "وَكَذَّبَ بِالۡحُسۡنٰىۙ‏"},
"6158": {"surat": 92, "ayat": 11, "teks": "فَسَنُيَسِّرُهٗ  لِلۡعُسۡرٰىؕ‏"},
"6159": {"surat": 92, "ayat": 12, "teks": "وَمَا يُغۡنِىۡ عَنۡهُ مَالُهٗۤ اِذَا تَرَدّٰىؕ‏"},
"6160": {"surat": 92, "ayat": 13, "teks": "اِنَّ عَلَيۡنَا لَـلۡهُدٰىۖ‏"},
"6161": {"surat": 92, "ayat": 14, "teks": "وَاِنَّ لَـنَا لَـلۡاٰخِرَةَ وَالۡاُوۡلٰى‏"},
"6162": {"surat": 92, "ayat": 15, "teks": "فَاَنۡذَرۡتُكُمۡ نَارًا  تَلَظّٰى‌ۚ‏"},
"6163": {"surat": 92, "ayat": 16, "teks": "لَا يَصۡلٰٮهَاۤ اِلَّا الۡاَشۡقَىۙ‏"},
"6164": {"surat": 92, "ayat": 17, "teks": "الَّذِىۡ كَذَّبَ وَتَوَلّٰىؕ‏"},
"6165": {"surat": 92, "ayat": 18, "teks": "وَسَيُجَنَّبُهَا الۡاَتۡقَىۙ‏"},
"6166": {"surat": 92, "ayat": 19, "teks": "الَّذِىۡ يُؤۡتِىۡ مَالَهٗ يَتَزَكّٰى‌ۚ‏"},
"6167": {"surat": 92, "ayat": 20, "teks": "وَمَا لِاَحَدٍ عِنۡدَهٗ مِنۡ نِّعۡمَةٍ تُجۡزٰٓىۙ‏"},
"6168": {"surat": 92, "ayat": 21, "teks": "اِلَّا ابۡتِغَآءَ وَجۡهِ   رَبِّهِ الۡاَعۡلٰى‌ۚ‏"},
"6169": {"surat": 92, "ayat": 22, "teks": "وَلَسَوۡفَ يَرۡضٰى‏"},
"6170": {"surat": 93, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6171": {"surat": 93, "ayat": 2, "teks": "وَالضُّحٰىۙ‏"},
"6172": {"surat": 93, "ayat": 3, "teks": "وَالَّيۡلِ اِذَا سَجٰىۙ‏"},
"6173": {"surat": 93, "ayat": 4, "teks": "مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىؕ‏"},
"6174": {"surat": 93, "ayat": 5, "teks": "وَلَـلۡاٰخِرَةُ خَيۡرٌ لَّكَ مِنَ الۡاُوۡلٰىؕ‏"},
"6175": {"surat": 93, "ayat": 6, "teks": "وَلَسَوۡفَ يُعۡطِيۡكَ رَبُّكَ  فَتَرۡضٰىؕ‏"},
"6176": {"surat": 93, "ayat": 7, "teks": "اَلَمۡ يَجِدۡكَ يَتِيۡمًا فَاٰوٰى‏"},
"6177": {"surat": 93, "ayat": 8, "teks": "وَوَجَدَكَ ضَآ لًّا فَهَدٰى‏"},
"6178": {"surat": 93, "ayat": 9, "teks": "وَوَجَدَكَ عَآٮِٕلًا فَاَغۡنٰىؕ‏"},
"6179": {"surat": 93, "ayat": 10, "teks": "فَاَمَّا الۡيَتِيۡمَ فَلَا تَقۡهَرۡؕ‏"},
"6180": {"surat": 93, "ayat": 11, "teks": "وَاَمَّا السَّآٮِٕلَ فَلَا تَنۡهَرۡؕ‏"},
"6181": {"surat": 93, "ayat": 12, "teks": "وَاَمَّا بِنِعۡمَةِ رَبِّكَ فَحَدِّثۡ‏"},
"6182": {"surat": 94, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6183": {"surat": 94, "ayat": 2, "teks": "اَلَمۡ نَشۡرَحۡ لَـكَ صَدۡرَكَۙ‏"},
"6184": {"surat": 94, "ayat": 3, "teks": "وَوَضَعۡنَا عَنۡكَ وِزۡرَكَۙ‏"},
"6185": {"surat": 94, "ayat": 4, "teks": "الَّذِىۡۤ اَنۡقَضَ ظَهۡرَكَۙ‏"},
"6186": {"surat": 94, "ayat": 5, "teks": "وَرَفَعۡنَا لَـكَ ذِكۡرَكَؕ‏"},
"6187": {"surat": 94, "ayat": 6, "teks": "فَاِنَّ مَعَ الۡعُسۡرِ يُسۡرًا ۙ‏"},
"6188": {"surat": 94, "ayat": 7, "teks": "اِنَّ مَعَ الۡعُسۡرِ يُسۡرًا ؕ‏"},
"6189": {"surat": 94, "ayat": 8, "teks": "فَاِذَا فَرَغۡتَ فَانۡصَبۡۙ‏"},
"6190": {"surat": 94, "ayat": 9, "teks": "وَاِلٰى رَبِّكَ فَارۡغَب‏"},
"6191": {"surat": 95, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6192": {"surat": 95, "ayat": 2, "teks": "وَالتِّيۡنِ وَالزَّيۡتُوۡنِۙ‏"},
"6193": {"surat": 95, "ayat": 3, "teks": "وَطُوۡرِ سِيۡنِيۡنَۙ‏"},
"6194": {"surat": 95, "ayat": 4, "teks": "وَهٰذَا الۡبَلَدِ الۡاَمِيۡنِۙ‏"},
"6195": {"surat": 95, "ayat": 5, "teks": "لَقَدۡ خَلَقۡنَا الۡاِنۡسَانَ فِىۡۤ اَحۡسَنِ تَقۡوِيۡمٍ‏"},
"6196": {"surat": 95, "ayat": 6, "teks": "ثُمَّ رَدَدۡنٰهُ اَسۡفَلَ سَافِلِيۡنَۙ‏"},
"6197": {"surat": 95, "ayat": 7, "teks": "اِلَّا الَّذِيۡنَ اٰمَنُوۡا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمۡ اَجۡرٌ غَيۡرُ مَمۡنُوۡنٍؕ‏"},
"6198": {"surat": 95, "ayat": 8, "teks": "فَمَا يُكَذِّبُكَ بَعۡدُ بِالدِّيۡنِ‏"},
"6199": {"surat": 95, "ayat": 9, "teks": "اَلَيۡسَ اللّٰهُ بِاَحۡكَمِ الۡحٰكِمِيۡنَ‏"},
"6200": {"surat": 96, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6201": {"surat": 96, "ayat": 2, "teks": "اِقۡرَاۡ بِاسۡمِ رَبِّكَ الَّذِىۡ خَلَقَ‌ۚ‏"},
"6202": {"surat": 96, "ayat": 3, "teks": "خَلَقَ الۡاِنۡسَانَ مِنۡ عَلَقٍ‌ۚ‏"},
"6203": {"surat": 96, "ayat": 4, "teks": "اِقۡرَاۡ وَرَبُّكَ الۡاَكۡرَمُۙ‏"},
"6204": {"surat": 96, "ayat": 5, "teks": "الَّذِىۡ عَلَّمَ بِالۡقَلَمِۙ‏"},
"6205": {"surat": 96, "ayat": 6, "teks": "عَلَّمَ الۡاِنۡسَانَ مَا  لَمۡ يَعۡلَمۡؕ‏"},
"6206": {"surat": 96, "ayat": 7, "teks": "كَلَّاۤ اِنَّ الۡاِنۡسَانَ لَيَطۡغٰٓىۙ‏"},
"6207": {"surat": 96, "ayat": 8, "teks": "اَنۡ رَّاٰهُ اسۡتَغۡنٰىؕ‏"},
"6208": {"surat": 96, "ayat": 9, "teks": "اِنَّ  اِلٰى رَبِّكَ الرُّجۡعٰىؕ‏"},
"6209": {"surat": 96, "ayat": 10, "teks": "اَرَءَيۡتَ الَّذِىۡ يَنۡهٰىؕ‏"},
"6210": {"surat": 96, "ayat": 11, "teks": "عَبۡدًا اِذَا صَلّٰىؕ‏"},
"6211": {"surat": 96, "ayat": 12, "teks": "اَرَءَيۡتَ اِنۡ كَانَ عَلَى الۡهُدٰٓىۙ‏"},
"6212": {"surat": 96, "ayat": 13, "teks": "اَوۡ اَمَرَ بِالتَّقۡوٰىۙ‏"},
"6213": {"surat": 96, "ayat": 14, "teks": "اَرَءَيۡتَ اِنۡ  كَذَّبَ وَتَوَلّٰىؕ‏"},
"6214": {"surat": 96, "ayat": 15, "teks": "اَلَمۡ يَعۡلَمۡ بِاَنَّ اللّٰهَ يَرٰىؕ‏"},
"6215": {"surat": 96, "ayat": 16, "teks": "كَلَّا لَٮِٕنۡ لَّمۡ يَنۡتَهِ  ۙ لَنَسۡفَعًۢا بِالنَّاصِيَةِۙ‏"},
"6216": {"surat": 96, "ayat": 17, "teks": "نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍ‌ ۚ‏"},
"6217": {"surat": 96, "ayat": 18, "teks": "فَلۡيَدۡعُ نَادِيَهٗ  ۙ‏"},
"6218": {"surat": 96, "ayat": 19, "teks": "سَنَدۡعُ الزَّبَانِيَةَ ۙ‏"},
"6219": {"surat": 96, "ayat": 20, "teks": "كَلَّاؕ لَا تُطِعۡهُ وَاسۡجُدۡ وَاقۡتَرِبْ۩‏"},
"6220": {"surat": 97, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6221": {"surat": 97, "ayat": 2, "teks": "اِنَّاۤ اَنۡزَلۡنٰهُ فِىۡ لَيۡلَةِ الۡقَدۡرِ ۖ  ۚ‏"},
"6222": {"surat": 97, "ayat": 3, "teks": "وَمَاۤ  اَدۡرٰٮكَ مَا لَيۡلَةُ الۡقَدۡرِؕ‏"},
"6223": {"surat": 97, "ayat": 4, "teks": "لَيۡلَةُ الۡقَدۡرِ  ۙ خَيۡرٌ مِّنۡ اَلۡفِ شَهۡرٍؕ‏"},
"6224": {"surat": 97, "ayat": 5, "teks": "تَنَزَّلُ الۡمَلٰٓٮِٕكَةُ وَالرُّوۡحُ فِيۡهَا بِاِذۡنِ رَبِّهِمۡ‌ۚ مِّنۡ كُلِّ اَمۡرٍ ۛۙ‏"},
"6225": {"surat": 97, "ayat": 6, "teks": "سَلٰمٌ   ۛهِىَ حَتّٰى مَطۡلَعِ الۡفَجۡرِ‏"},
"6226": {"surat": 98, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6227": {"surat": 98, "ayat": 2, "teks": "لَمۡ يَكُنِ الَّذِيۡنَ كَفَرُوۡا مِنۡ اَهۡلِ الۡكِتٰبِ وَالۡمُشۡرِكِيۡنَ مُنۡفَكِّيۡنَ حَتّٰى تَاۡتِيَهُمُ الۡبَيِّنَةُ ۙ‏"},
"6228": {"surat": 98, "ayat": 3, "teks": "رَسُوۡلٌ مِّنَ اللّٰهِ يَتۡلُوۡا صُحُفًا مُّطَهَّرَةً ۙ‏"},
"6229": {"surat": 98, "ayat": 4, "teks": "فِيۡهَا كُتُبٌ قَيِّمَةٌ ؕ‏"},
"6230": {"surat": 98, "ayat": 5, "teks": "وَمَا تَفَرَّقَ الَّذِيۡنَ اُوۡتُوا الۡكِتٰبَ اِلَّا مِنۡۢ بَعۡدِ مَا جَآءَتۡهُمُ الۡبَيِّنَةُ ؕ‏"},
"6231": {"surat": 98, "ayat": 6, "teks": "وَمَاۤ اُمِرُوۡۤا اِلَّا لِيَعۡبُدُوا اللّٰهَ مُخۡلِصِيۡنَ لَـهُ الدِّيۡنَ  ۙ حُنَفَآءَ وَيُقِيۡمُوا الصَّلٰوةَ وَيُؤۡتُوا الزَّكٰوةَ‌ وَذٰلِكَ دِيۡنُ الۡقَيِّمَةِ ؕ‏"},
"6232": {"surat": 98, "ayat": 7, "teks": "اِنَّ الَّذِيۡنَ كَفَرُوۡا مِنۡ اَهۡلِ الۡكِتٰبِ وَ الۡمُشۡرِكِيۡنَ فِىۡ نَارِ جَهَنَّمَ خٰلِدِيۡنَ فِيۡهَا ‌ؕ اُولٰٓٮِٕكَ هُمۡ شَرُّ الۡبَرِيَّةِ ؕ‏"},
"6233": {"surat": 98, "ayat": 8, "teks": "اِنَّ الَّذِيۡنَ اٰمَنُوۡا وَعَمِلُوا الصّٰلِحٰتِۙ اُولٰٓٮِٕكَ هُمۡ خَيۡرُ الۡبَرِيَّةِ ؕ‏"},
"6234": {"surat": 98, "ayat": 9, "teks": "جَزَآؤُهُمۡ عِنۡدَ رَبِّهِمۡ جَنّٰتُ عَدۡنٍ تَجۡرِىۡ مِنۡ تَحۡتِهَا الۡاَنۡهٰرُ خٰلِدِيۡنَ فِيۡهَاۤ اَبَدًا ‌ؕ رَضِىَ اللّٰهُ عَنۡهُمۡ وَرَضُوۡا عَنۡهُ ‌ؕ ذٰلِكَ لِمَنۡ خَشِىَ رَبَّهٗ‏"},
"6235": {"surat": 99, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6236": {"surat": 99, "ayat": 2, "teks": "اِذَا زُلۡزِلَتِ الۡاَرۡضُ زِلۡزَالَهَا ۙ‏"},
"6237": {"surat": 99, "ayat": 3, "teks": "وَاَخۡرَجَتِ الۡاَرۡضُ اَثۡقَالَهَا ۙ‏"},
"6238": {"surat": 99, "ayat": 4, "teks": "وَقَالَ الۡاِنۡسَانُ مَا لَهَا‌ ۚ‏"},
"6239": {"surat": 99, "ayat": 5, "teks": "يَوۡمَٮِٕذٍ تُحَدِّثُ اَخۡبَارَهَا ۙ‏"},
"6240": {"surat": 99, "ayat": 6, "teks": "بِاَنَّ رَبَّكَ اَوۡحٰى لَهَا ؕ‏"},
"6241": {"surat": 99, "ayat": 7, "teks": "يَوۡمَٮِٕذٍ يَّصۡدُرُ النَّاسُ اَشۡتَاتًا  ۙ لِّيُرَوۡا اَعۡمَالَهُمۡؕ‏"},
"6242": {"surat": 99, "ayat": 8, "teks": "فَمَنۡ يَّعۡمَلۡ مِثۡقَالَ ذَرَّةٍ خَيۡرًا يَّرَهٗ ؕ‏"},
"6243": {"surat": 99, "ayat": 9, "teks": "وَمَنۡ  يَّعۡمَلۡ مِثۡقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ‏"},
"6244": {"surat": 100, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6245": {"surat": 100, "ayat": 2, "teks": "وَالۡعٰدِيٰتِ ضَبۡحًا ۙ‏"},
"6246": {"surat": 100, "ayat": 3, "teks": "فَالۡمُوۡرِيٰتِ قَدۡحًا ۙ‏"},
"6247": {"surat": 100, "ayat": 4, "teks": "فَالۡمُغِيۡرٰتِ صُبۡحًا ۙ‏"},
"6248": {"surat": 100, "ayat": 5, "teks": "فَاَثَرۡنَ بِهٖ نَقۡعًا ۙ‏"},
"6249": {"surat": 100, "ayat": 6, "teks": "فَوَسَطۡنَ بِهٖ جَمۡعًا     ۙ‏"},
"6250": {"surat": 100, "ayat": 7, "teks": "اِنَّ الۡاِنۡسَانَ لِرَبِّهٖ لَـكَنُوۡدٌ ۚ‏"},
"6251": {"surat": 100, "ayat": 8, "teks": "وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيۡدٌ ۚ‏"},
"6252": {"surat": 100, "ayat": 9, "teks": "وَاِنَّهٗ لِحُبِّ الۡخَيۡرِ لَشَدِيۡدٌ ؕ‏"},
"6253": {"surat": 100, "ayat": 10, "teks": "اَفَلَا يَعۡلَمُ اِذَا بُعۡثِرَ مَا فِى الۡقُبُوۡرِۙ‏"},
"6254": {"surat": 100, "ayat": 11, "teks": "وَحُصِّلَ مَا فِى الصُّدُوۡرِۙ‏"},
"6255": {"surat": 100, "ayat": 12, "teks": "اِنَّ رَبَّهُمۡ بِهِمۡ يَوۡمَٮِٕذٍ لَّخَبِيۡرٌ‏"},
"6256": {"surat": 101, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6257": {"surat": 101, "ayat": 2, "teks": "اَلۡقَارِعَةُ ۙ‏"},
"6258": {"surat": 101, "ayat": 3, "teks": "مَا الۡقَارِعَةُ‌ ۚ‏"},
"6259": {"surat": 101, "ayat": 4, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا الۡقَارِعَةُ ؕ‏"},
"6260": {"surat": 101, "ayat": 5, "teks": "يَوۡمَ يَكُوۡنُ النَّاسُ كَالۡفَرَاشِ الۡمَبۡثُوۡثِۙ‏"},
"6261": {"surat": 101, "ayat": 6, "teks": "وَتَكُوۡنُ الۡجِبَالُ  كَالۡعِهۡنِ الۡمَنۡفُوۡشِؕ‏"},
"6262": {"surat": 101, "ayat": 7, "teks": "فَاَمَّا مَنۡ ثَقُلَتۡ مَوَازِيۡنُهٗ ۙ‏"},
"6263": {"surat": 101, "ayat": 8, "teks": "فَهُوَ فِىۡ عِيۡشَةٍ رَّاضِيَةٍ ؕ‏"},
"6264": {"surat": 101, "ayat": 9, "teks": "وَاَمَّا مَنۡ خَفَّتۡ مَوَازِيۡنُهٗ ۙ‏"},
"6265": {"surat": 101, "ayat": 10, "teks": "فَاُمُّهٗ هَاوِيَةٌ ؕ‏"},
"6266": {"surat": 101, "ayat": 11, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا هِيَهۡ ؕ‏"},
"6267": {"surat": 101, "ayat": 12, "teks": "نَارٌ حَامِيَةٌ‏"},
"6268": {"surat": 102, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6269": {"surat": 102, "ayat": 2, "teks": "اَلۡهٰٮكُمُ التَّكَاثُرُۙ‏"},
"6270": {"surat": 102, "ayat": 3, "teks": "حَتّٰى زُرۡتُمُ الۡمَقَابِرَؕ‏"},
"6271": {"surat": 102, "ayat": 4, "teks": "كَلَّا سَوۡفَ  تَعۡلَمُوۡنَۙ‏"},
"6272": {"surat": 102, "ayat": 5, "teks": "ثُمَّ كَلَّا سَوۡفَ تَعۡلَمُوۡنَؕ‏"},
"6273": {"surat": 102, "ayat": 6, "teks": "كَلَّا لَوۡ تَعۡلَمُوۡنَ عِلۡمَ  الۡيَقِيۡنِؕ‏"},
"6274": {"surat": 102, "ayat": 7, "teks": "لَتَرَوُنَّ الۡجَحِيۡمَۙ‏"},
"6275": {"surat": 102, "ayat": 8, "teks": "ثُمَّ لَتَرَوُنَّهَا عَيۡنَ الۡيَقِيۡنِۙ‏"},
"6276": {"surat": 102, "ayat": 9, "teks": "ثُمَّ لَـتُسۡـَٔـلُنَّ يَوۡمَٮِٕذٍ عَنِ النَّعِيۡمِ‏"},
"6277": {"surat": 103, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6278": {"surat": 103, "ayat": 2, "teks": "وَالۡعَصۡرِۙ‏"},
"6279": {"surat": 103, "ayat": 3, "teks": "اِنَّ الۡاِنۡسَانَ لَفِىۡ خُسۡرٍۙ‏"},
"6280": {"surat": 103, "ayat": 4, "teks": "اِلَّا الَّذِيۡنَ اٰمَنُوۡا وَ عَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوۡا بِالۡحَقِّ   ۙ وَتَوَاصَوۡا بِالصَّبۡرِ‏"},
"6281": {"surat": 104, "ayat": 1, "teks": "بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ"},
"6282": {"surat": 104, "ayat": 2, "teks": "وَيۡلٌ لِّـكُلِّ هُمَزَةٍ لُّمَزَةِ ۙ‏"},
"6283": {"surat": 104, "ayat": 3, "teks": "اۨلَّذِىۡ جَمَعَ مَالًا وَّعَدَّدَهٗ ۙ‏"},
"6284": {"surat": 104, "ayat": 4, "teks": "يَحۡسَبُ اَنَّ مَالَهٗۤ اَخۡلَدَهٗ‌ ۚ‏"},
"6285": {"surat": 104, "ayat": 5, "teks": "كَلَّا‌ لَيُنۡۢبَذَنَّ فِى الۡحُطَمَةِ  ۖ‏"},
"6286": {"surat": 104, "ayat": 6, "teks": "وَمَاۤ اَدۡرٰٮكَ مَا الۡحُطَمَةُ ؕ‏"},
"6287": {"surat": 104, "ayat": 7, "teks": "نَارُ اللّٰهِ الۡمُوۡقَدَةُ ۙ‏"},
"6288": {"surat": 104, "ayat": 8, "teks": "الَّتِىۡ تَطَّلِعُ عَلَى الۡاَفۡـــِٕدَةِ ؕ‏"},
"6289": {"surat": 104, "ayat": 9, "teks": "اِنَّهَا عَلَيۡهِمۡ مُّؤۡصَدَةٌ ۙ‏"},
"6290": {"surat": 104, "ayat": 10, "teks": "فِىۡ عَمَدٍ مُّمَدَّدَةٍ‏"},
